#!/usr/bin/env node
/**
 * Functional test for Notion skill - validates API command syntax
 * This test extracts curl commands from the skill and validates their JSON syntax
 */

const fs = require('fs');
const path = require('path');

const SKILL_FILE = path.join(__dirname, '../../pi-skills/notion/SKILL.md');

let passed = 0;
let failed = 0;
let jsonTests = 0;

function test(name, fn) {
  try {
    fn();
    console.log(`✓ ${name}`);
    passed++;
  } catch (err) {
    console.log(`✗ ${name}`);
    console.log(`  Error: ${err.message}`);
    failed++;
  }
}

function assert(condition, message) {
  if (!condition) throw new Error(message);
}

// Read skill content
const content = fs.readFileSync(SKILL_FILE, 'utf8');

// Extract JSON blocks from curl commands
const jsonBlockRegex = /-d\s+'({[\s\S]*?})'/g;
let match;

// Test each JSON payload found in curl commands
while ((match = jsonBlockRegex.exec(content)) !== null) {
  jsonTests++;
  const jsonStr = match[1].replace(/"/g, '"').replace(/'/g, '"');

  test(`JSON payload #${jsonTests} is valid`, () => {
    JSON.parse(jsonStr);
  });
}

if (jsonTests === 0) {
  console.log('⚠ No JSON payloads found to test');
}

// Test search operation
test('Search endpoint uses correct URL', () => {
  assert(content.includes('"https://api.notion.com/v1/search"'), 'Search URL incorrect');
});

// Test page creation endpoint
test('Pages endpoint is correct', () => {
  assert(content.includes('"https://api.notion.com/v1/pages"'), 'Pages URL incorrect');
});

// Test database query endpoint
test('Database query endpoint is correct', () => {
  assert(content.includes('/databases/'), 'Database endpoint incorrect');
  assert(content.includes('/query"'), 'Query endpoint incorrect');
});

// Test authorization header format
test('Authorization header format is correct', () => {
  assert(content.includes('Authorization: Bearer $NOTION_KEY'), 'Auth header incorrect');
});

// Test Notion version header
test('Notion version header is present', () => {
  assert(content.includes('Notion-Version:'), 'Version header missing');
});

// Test required parent object structure
test('Parent object structure is correct', () => {
  assert(content.includes('"parent": {"page_id"'), 'Page parent structure incorrect');
  assert(content.includes('"parent": {"database_id"'), 'Database parent structure incorrect');
});

// Test properties object structure
test('Properties object structure is correct', () => {
  assert(content.includes('"properties": {'), 'Properties object missing');
});

// Summary
console.log('\n' + '='.repeat(40));
console.log(`Results: ${passed} passed, ${failed} failed`);
console.log(`JSON payloads tested: ${jsonTests}`);
console.log('='.repeat(40));

if (failed > 0) {
  process.exit(1);
}

console.log('\n✅ All functional tests passed!');
process.exit(0);

