  console.log('\n🧪 Mintlify Skill Test Suite\n');
  console.log('=' .repeat(50));

  let passed = 0;
  let failed = 0;

  // Test 1: Helper script exists
  console.log('\n📋 Test 1: Helper script exists');
  try {
    const helperPath = path.join(SKILL_DIR, 'mintlify-helper.js');
    if (fs.existsSync(helperPath)) {
      console.log('✅ PASS: Helper script exists');
      passed++;
    } else {
      console.log('❌ FAIL: Helper script not found');
      failed++;
    }
  } catch (e) {
    console.log(`❌ FAIL: ${e.message}`);
    failed++;
  }

  // Test 2: SKILL.md exists and has proper frontmatter
  console.log('\n📋 Test 2: SKILL.md exists with proper structure');
  try {
    const skillPath = path.join(SKILL_DIR, 'SKILL.md');
    const content = fs.readFileSync(skillPath, 'utf-8');

    const hasName = content.includes('name: mintlify');
    const hasDescription = content.includes('description:');
    const hasCliSection = content.includes('mint dev');

    if (hasName && hasDescription && hasCliSection) {
      console.log('✅ PASS: SKILL.md properly structured');
      passed++;
    } else {
      console.log('❌ FAIL: SKILL.md missing required sections');
      failed++;
    }
  } catch (e) {
    console.log(`❌ FAIL: ${e.message}`);
    failed++;
  }

  // Test 3: Validate mock docs structure
  console.log('\n📋 Test 3: Validate mock documentation structure');
  try {
    const result = execSync(
      `node ${path.join(SKILL_DIR, 'mintlify-helper.js')} validate ${path.join(TEST_DIR, 'docs')}`,
