//! Evaluator ‚Äî scores discovered skill candidates across multiple dimensions.

use serde::{Deserialize, Serialize};

use super::scout::ScoutResult;

// ---------------------------------------------------------------------------
// Scoring dimensions
// ---------------------------------------------------------------------------

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct Scores {
    /// OS / arch / runtime compatibility (0.0‚Äì1.0).
    pub compatibility: f64,
    /// Code quality signals: stars, tests, docs (0.0‚Äì1.0).
    pub quality: f64,
    /// Security posture: license, known-bad patterns (0.0‚Äì1.0).
    pub security: f64,
}

impl Scores {
    /// Weighted total. Weights: compatibility 0.3, quality 0.35, security 0.35.
    pub fn total(&self) -> f64 {
        self.compatibility * 0.30 + self.quality * 0.35 + self.security * 0.35
    }
}

// ---------------------------------------------------------------------------
// Recommendation
// ---------------------------------------------------------------------------

#[derive(Debug, Clone, Copy, PartialEq, Eq, Serialize, Deserialize)]
pub enum Recommendation {
    /// Score >= threshold ‚Üí safe to auto-integrate.
    Auto,
    /// Score in [0.4, threshold) ‚Üí needs human review.
    Manual,
    /// Score < 0.4 ‚Üí skip entirely.
    Skip,
}

