"""
Base utilities for creating tools.
"""

from typing import Any, Callable, Optional

from langchain_core.tools import tool as langchain_tool


def tool(
    func: Optional[Callable] = None,
    *,
    name: Optional[str] = None,
    description: Optional[str] = None,
) -> Any:
    """
    Decorator to create a LangChain tool from a function.

    This is a convenience wrapper around langchain_core.tools.tool that
    provides a simpler interface for ZeroClaw users.

    Args:
        func: The function to wrap (when used without parentheses)
        name: Optional custom name for the tool
        description: Optional custom description

    Returns:
        A BaseTool instance

    Example:
        ```python
        from zeroclaw_tools import tool

        @tool
        def my_tool(query: str) -> str:
            \"\"\"Description of what this tool does.\"\"\"
            return f"Result: {query}"
        ```
    """
    if func is not None:
        if name is not None:
            return langchain_tool(name, func, description=description)
        return langchain_tool(func, description=description)

    def decorator(f: Callable) -> Any:
        if name is not None:
            return langchain_tool(name, f, description=description)
        return langchain_tool(f, description=description)

    return decorator

"""
Memory storage tools for persisting data between conversations.
"""

import json
from pathlib import Path

from langchain_core.tools import tool


def _get_memory_path() -> Path:
    """Get the path to the memory storage file."""
    return Path.home() / ".zeroclaw" / "memory_store.json"


def _load_memory() -> dict:
    """Load memory from disk."""
    path = _get_memory_path()
    if not path.exists():
        return {}
    try:
        with open(path, "r", encoding="utf-8") as f:
            return json.load(f)
    except Exception:
        return {}


def _save_memory(data: dict) -> None:
    """Save memory to disk."""
    path = _get_memory_path()
    path.parent.mkdir(parents=True, exist_ok=True)
    with open(path, "w", encoding="utf-8") as f:
        json.dump(data, f, indent=2)


@tool
def memory_store(key: str, value: str) -> str:
    """
    Store a key-value pair in persistent memory.

    Args:
        key: The key to store under
        value: The value to store

    Returns:
        Confirmation message
    """
    try:
        data = _load_memory()
        data[key] = value
        _save_memory(data)
        return f"Stored: {key}"
    except Exception as e:
        return f"Error: {e}"


@tool
def memory_recall(query: str) -> str:
    """
    Search memory for entries matching the query.

    Args:
        query: The search query

    Returns:
        Matching entries or "no matches" message
    """
    try:
        data = _load_memory()
        if not data:
            return "No memories stored yet"

        query_lower = query.lower()
        matches = {
            k: v
            for k, v in data.items()
            if query_lower in k.lower() or query_lower in str(v).lower()
        }

        if not matches:
            return f"No matches for: {query}"

        return json.dumps(matches, indent=2)
    except Exception as e:
        return f"Error: {e}"
