/**
 * Session Logs Skill
 * Search and analyze conversation history stored in session JSONL files
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

const LOGS_DIR = path.join(process.env.WORKDIR || '/job', 'logs');

/**
 * Search all sessions for a keyword
 * @param {string} keyword - The keyword to search for
 * @param {number} limit - Maximum number of results
 * @returns {Array} Matching sessions with context
 */
function searchSessions(keyword, limit = 10) {
  const results = [];

  if (!fs.existsSync(LOGS_DIR)) {
    return { error: 'Logs directory not found', results: [] };
  }

  const sessions = fs.readdirSync(LOGS_DIR)
    .filter(item => {
      const sessionPath = path.join(LOGS_DIR, item, 'session.jsonl');
      return fs.existsSync(sessionPath);
    });

  for (const sessionId of sessions) {
    const sessionPath = path.join(LOGS_DIR, sessionId, 'session.jsonl');
    try {
      const content = fs.readFileSync(sessionPath, 'utf-8');
      const lines = content.split('\n').filter(line => line.trim());

      for (const line of lines) {
        try {
          const msg = JSON.parse(line);
          const content = msg.content || '';
          if (content.toLowerCase().includes(keyword.toLowerCase())) {
            results.push({
              session: sessionId,
              role: msg.role,
              content: content.substring(0, 200),
              timestamp: msg.timestamp || null
            });

            if (results.length >= limit) {
              return results;
            }
          }
        } catch (e) {
          // Skip invalid JSON lines
        }
      }
    } catch (e) {
      console.error(`Error reading session ${sessionId}:`, e.message);
    }
  }

  return results;
}

/**
 * Get messages from a specific session
 * @param {string} jobId - The job/session ID
 * @param {string|null} role - Filter by role (user, assistant, tool)
 * @returns {Array} Messages from the session
 */
function getSessionMessages(jobId, role = null) {
  const sessionPath = path.join(LOGS_DIR, jobId, 'session.jsonl');

  if (!fs.existsSync(sessionPath)) {
    return { error: `Session not found: ${jobId}`, messages: [] };
  }

  try {
    const content = fs.readFileSync(sessionPath, 'utf-8');
    const lines = content.split('\n').filter(line => line.trim());
    const messages = [];

    for (const line of lines) {
      try {
        const msg = JSON.parse(line);
        if (!role || msg.role === role) {
          messages.push(msg);
        }
      } catch (e) {
        // Skip invalid JSON lines
      }
    }

    return messages;
  } catch (e) {
    return { error: `Error reading session: ${e.message}`, messages: [] };
  }
}

/**
 * Summarize a session
 * @param {string} jobId - The job/session ID
 * @returns {Object} Session summary
 */
function summarizeSession(jobId) {
  const messages = getSessionMessages(jobId);

  if (messages.error) {
    return messages;
  }

  const userMessages = messages.filter(m => m.role === 'user');
  const assistantMessages = messages.filter(m => m.role === 'assistant');
  const toolMessages = messages.filter(m => m.tool_call_id !== undefined);

  // Extract topics from first user message (usually the task)
  const firstUserMsg = userMessages[0]?.content || 'Unknown task';

  // Find tool usage
  const toolUsage = {};
  toolMessages.forEach(msg => {
    const toolName = msg.name || 'unknown';
    toolUsage[toolName] = (toolUsage[toolName] || 0) + 1;
  });

  return {
    jobId,
    totalMessages: messages.length,
    userMessages: userMessages.length,
    assistantMessages: assistantMessages.length,
    toolCalls: toolMessages.length,
    toolUsage,
    taskPreview: firstUserMsg.substring(0, 100),
    firstTimestamp: messages[0]?.timestamp,
    lastTimestamp: messages[messages.length - 1]?.timestamp
  };
}

/**
 * Get all sessions with metadata
 * @returns {Array} List of sessions with basic info
 */
function listSessions() {
  if (!fs.existsSync(LOGS_DIR)) {
    return [];
  }

  return fs.readdirSync(LOGS_DIR)
    .map(sessionId => {
      const sessionPath = path.join(LOGS_DIR, sessionId, 'session.jsonl');
      if (!fs.existsSync(sessionPath)) return null;

      const stats = fs.statSync(sessionPath);
      const content = fs.readFileSync(sessionPath, 'utf-8');
      const lines = content.split('\n').filter(line => line.trim());

      return {
        jobId: sessionId,
        size: stats.size,
        messageCount: lines.length,
        createdAt: stats.birthtime,
        modifiedAt: stats.mtime
      };
    })
    .filter(s => s !== null)
    .sort((a, b) => b.modifiedAt - a.modifiedAt);
}

module.exports = {
  searchSessions,
  getSessionMessages,
  summarizeSession,
  listSessions
};

/**
 * Xurl Skill - X (Twitter) API CLI wrapper
 * Makes authenticated requests to the X API
 */

const crypto = require('crypto');
const https = require('https');

class XurlClient {
  constructor(credentials = {}) {
    this.apiKey = credentials.apiKey || process.env.X_API_KEY;
    this.apiSecret = credentials.apiSecret || process.env.X_API_SECRET;
    this.accessToken = credentials.accessToken || process.env.X_ACCESS_TOKEN;
    this.accessSecret = credentials.accessSecret || process.env.X_ACCESS_SECRET;
    this.baseUrl = 'https://api.twitter.com';
  }

  /**
   * Generate OAuth 1.0a signature for X API requests
   */
  generateOAuthSignature(method, url, params) {
    const paramsString = Object.keys(params)
      .sort()
      .map(key => `${encodeURIComponent(key)}=${encodeURIComponent(params[key] || '')}`)
      .join('&');

    const baseString = `${method.toUpperCase()}&${encodeURIComponent(url)}&${encodeURIComponent(paramsString)}`;
    const signingKey = `${encodeURIComponent(this.apiSecret)}&${encodeURIComponent(this.accessSecret)}`;

    const signature = crypto
      .createHmac('sha1', signingKey)
      .update(baseString)
      .digest('base64');

    return signature;
  }

  /**
   * Generate OAuth authorization header
   */
  generateAuthHeader(method, url, params) {
    const oauthParams = {
      oauth_consumer_key: this.apiKey,
      oauth_nonce: crypto.randomBytes(16).toString('hex'),
      oauth_signature_method: 'HMAC-SHA1',
      oauth_timestamp: Math.floor(Date.now() / 1000),
      oauth_token: this.accessToken,
      oauth_version: '1.0'
    };

    const signature = this.generateOAuthSignature(method, url, { ...oauthParams, ...params });
    oauthParams.oauth_signature = signature;

    const authParams = Object.keys(oauthParams)
      .sort()
      .map(key => `${key}="${encodeURIComponent(oauthParams[key])}"`)
      .join(', ');

    return `OAuth ${authParams}`;
  }

  /**
   * Make a request to the X API
   */
  async request(method, endpoint, params = {}, body = null) {
    if (!this.apiKey || !this.apiSecret || !this.accessToken || !this.accessSecret) {
      throw new Error('X API credentials not configured. Set X_API_KEY, X_API_SECRET, X_ACCESS_TOKEN, X_ACCESS_SECRET');
    }

    const url = `${this.baseUrl}${endpoint}`;
    const authHeader = this.generateAuthHeader(method, url, params);

    return new Promise((resolve, reject) => {
      const options = {
        hostname: 'api.twitter.com',
        path: endpoint + '?' + new URLSearchParams(params),
        method: method,
        headers: {
          'Authorization': authHeader,
          'Content-Type': 'application/json'
        }
      };

      const req = https.request(options, (res) => {
        let data = '';
        res.on('data', chunk => data += chunk);
        res.on('end', () => {
          try {
            resolve(JSON.parse(data));
          } catch (e) {
            resolve({ raw: data });
          }
        });
      });

      req.on('error', reject);
      if (body) req.write(JSON.stringify(body));
      req.end();
    });
  }

  /**
   * Post a tweet
   */
  async tweet(text) {
    return this.request('POST', '/2/tweets', {}, { text });
  }

  /**
   * Reply to a tweet
   */
  async reply(tweetId, text) {
    return this.request('POST', '/2/tweets', {}, { 
      text,
      reply: { in_reply_to_tweet_id: tweetId }
    });
  }

  /**
   * Quote tweet
   */
  async quote(tweetId, text) {
    return this.request('POST', '/2/tweets', {}, {
      text,
      quote_tweet_id: tweetId
    });
  }

  /**
   * Search tweets
   */
  async search(query, options = {}) {
    const params = {
      query,
      max_results: options.maxResults || 10,
      'tweet.fields': 'created_at,author_id,public_metrics'
    };
    return this.request('GET', '/2/tweets/search/recent', params);
  }

  /**
   * Get user by username
   */
  async getUser(username) {
    const params = {
      username,
      'user.fields': 'created_at,description,public_metrics,verified'
    };
    return this.request('GET', `/2/users/by/username/${username}`, params);
  }

  /**
   * Get user's tweets
   */
  async getUserTweets(userId, options = {}) {
    const params = {
      'tweet.fields': 'created_at,public_metrics,text',
      max_results: options.limit || 10
    };
    return this.request('GET', `/2/users/${userId}/tweets`, params);
  }

  /**
   * Get tweet by ID
   */
  async getTweet(tweetId) {
    const params = {
      'tweet.fields': 'created_at,author_id,public_metrics,text'
    };
    return this.request('GET', `/2/tweets/${tweetId}`, params);
  }

  /**
   * Follow a user
   */
  async follow(targetUserId) {
    const me = await this.request('GET', '/2/users/me', { 'user.fields': 'id' });
    return this.request('POST', `/2/users/${me.data.id}/following`, {}, {
      target_user_id: targetUserId
    });
  }

  /**
   * Unfollow a user
   */
  async unfollow(targetUserId) {
    const me = await this.request('GET', '/2/users/me', { 'user.fields': 'id' });
    return this.request('DELETE', `/2/users/${me.data.id}/following/${targetUserId}`);
  }

  /**
   * Get user's followers
   */
  async getFollowers(userId, options = {}) {
    const params = {
      max_results: options.limit || 10,
      'user.fields': 'created_at,description,public_metrics'
    };
    return this.request('GET', `/2/users/${userId}/followers`, params);
  }

  /**
   * Get users that a user follows
   */
  async getFollowing(userId, options = {}) {
    const params = {
      max_results: options.limit || 10,
      'user.fields': 'created_at,description,public_metrics'
    };
    return this.request('GET', `/2/users/${userId}/following`, params);
  }

  /**
   * Send a DM
   */
  async dm(userId, text) {
    return this.request('POST', '/2/dm_events', {}, {
      event_type: 'MessageCreate',
      data: {
        conversation_id: userId,
        text
      }
    });
  }
}

module.exports = XurlClient;

/**
 * Video Frames Skill
 * Extract frames and clips from videos using ffmpeg
 */

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

class VideoFrames {
  constructor(options = {}) {
    this.ffmpegPath = options.ffmpegPath || 'ffmpeg';
    this.ffprobePath = options.ffprobePath || 'ffprobe';
    this.outputDir = options.outputDir || '/job/tmp';
  }

  /**
   * Check if ffmpeg is available
   */
  isAvailable() {
    try {
      execSync(`${this.ffmpegPath} -version`, { stdio: 'pipe' });
      return true;
    } catch (e) {
      return false;
    }
  }

  /**
   * Get video metadata
   */
  getVideoInfo(videoPath) {
    if (!fs.existsSync(videoPath)) {
      throw new Error(`Video file not found: ${videoPath}`);
    }

    const output = execSync(
      `${this.ffprobePath} -v quiet -print_format json -show_format -show_streams "${videoPath}"`,
      { encoding: 'utf-8' }
    );

    return JSON.parse(output);
  }

  /**
   * Extract a single frame from a video
   * @param {string} videoPath - Path to input video
   * @param {object} options - Extraction options
   * @returns {string} Path to output frame
   */
  extractFrame(videoPath, options = {}) {
    const {
      timestamp = '00:00:01',
      quality = 2,
      format = 'jpg',
      output = null
    } = options;

    if (!fs.existsSync(videoPath)) {
      throw new Error(`Video file not found: ${videoPath}`);
    }

    const outputFile = output || path.join(
      this.outputDir,
      `frame_${path.basename(videoPath, path.extname(videoPath))}_${Date.now()}.${format}`
    );

    const qualityArg = format === 'jpg' ? `-q:v ${quality}` : '-q:v 2';

    execSync(
      `${this.ffmpegPath} -i "${videoPath}" -ss "${timestamp}" -frames:v 1 ${qualityArg} "${outputFile}"`,
      { stdio: 'pipe' }
    );

    return outputFile;
  }

  /**
   * Extract multiple frames at regular intervals
   * @param {string} videoPath - Path to input video
   * @param {object} options - Extraction options
   * @returns {string[]} Paths to output frames
   */
  extractFrames(videoPath, options = {}) {
    const {
      interval = 60, // seconds between frames
      format = 'jpg',
      outputDir = this.outputDir
    } = options;

    if (!fs.existsSync(videoPath)) {
      throw new Error(`Video file not found: ${videoPath}`);
    }

    const pattern = path.join(outputDir, `frame_%03d.${format}`);

    execSync(
      `${this.ffmpegPath} -i "${videoPath}" -vf "fps=1/${interval}" "${pattern}"`,
      { stdio: 'pipe' }
    );

    // Return list of generated files
    const files = fs.readdirSync(outputDir)
      .filter(f => f.startsWith('frame_') && f.endsWith(`.${format}`))
      .map(f => path.join(outputDir, f));

    return files;
  }

  /**
   * Generate a thumbnail grid (contact sheet)
   * @param {string} videoPath - Path to input video
   * @param {object} options - Grid options
   * @returns {string} Path to output grid image
   */
  generateThumbnailGrid(videoPath, options = {}) {
    const {
      columns = 3,
      rows = 3,
      output = null
    } = options;

    if (!fs.existsSync(videoPath)) {
      throw new Error(`Video file not found: ${videoPath}`);
    }

    const outputFile = output || path.join(
      this.outputDir,
      `grid_${path.basename(videoPath, path.extname(videoPath))}_${Date.now()}.jpg`
    );

    execSync(
      `${this.ffmpegPath} -i "${videoPath}" -vf "tile=${columns}x${rows}" "${outputFile}"`,
      { stdio: 'pipe' }
    );

    return outputFile;
  }

  /**
   * Extract a short clip from a video
   * @param {string} videoPath - Path to input video
   * @param {object} options - Clip options
   * @returns {string} Path to output clip
   */
  extractClip(videoPath, options = {}) {
    const {
      startTime = '00:00:10',
      duration = '00:00:05',
      copyCodec = true,
      output = null
    } = options;

    if (!fs.existsSync(videoPath)) {
      throw new Error(`Video file not found: ${videoPath}`);
    }

    const outputFile = output || path.join(
      this.outputDir,
      `clip_${path.basename(videoPath, path.extname(videoPath))}_${Date.now()}.mp4`
    );

    const codecArg = copyCodec ? '-c copy' : '';

    execSync(
      `${this.ffmpegPath} -i "${videoPath}" -ss "${startTime}" -t "${duration}" ${codecArg} "${outputFile}"`,
      { stdio: 'pipe' }
    );

    return outputFile;
  }

  /**
   * Create an animated GIF from a video segment
   * @param {string} videoPath - Path to input video
   * @param {object} options - GIF options
   * @returns {string} Path to output GIF
   */
  createGif(videoPath, options = {}) {
    const {
      startTime = '00:00:10',
      duration = '00:00:05',
      fps = 10,
      width = 320,
      output = null
    } = options;

    if (!fs.existsSync(videoPath)) {
      throw new Error(`Video file not found: ${videoPath}`);
    }

    const outputFile = output || path.join(
      this.outputDir,
      `clip_${path.basename(videoPath, path.extname(videoPath))}_${Date.now()}.gif`
    );

    execSync(
      `${this.ffmpegPath} -i "${videoPath}" -ss "${startTime}" -t "${duration}" -vf "fps=${fps},scale=${width}:-1" "${outputFile}"`,
      { stdio: 'pipe' }
    );

    return outputFile;
  }

  /**
   * Generate a single high-quality thumbnail
   * @param {string} videoPath - Path to input video
   * @param {object} options - Thumbnail options
   * @returns {string} Path to output thumbnail
   */
  generateThumbnail(videoPath, options = {}) {
    const {
      timestamp = '00:00:02',
      width = 640,
      quality = 85,
      output = null
    } = options;

    if (!fs.existsSync(videoPath)) {
      throw new Error(`Video file not found: ${videoPath}`);
    }

    const outputFile = output || path.join(
      this.outputDir,
      `thumb_${path.basename(videoPath, path.extname(videoPath))}_${Date.now()}.jpg`
    );

    execSync(
      `${this.ffmpegPath} -i "${videoPath}" -ss "${timestamp}" -frames:v 1 -vf "scale=${width}:-1" -q:v ${quality} "${outputFile}"`,
      { stdio: 'pipe' }
    );

    return outputFile;
  }
}

module.exports = VideoFrames;

/**
 * Camsnap Skill
 * Capture frames and clips from RTSP/ONVIF cameras
 */

const { execSync } = require('child_process');
const fs = require('fs');
const path = require('path');

class Camsnap {
  constructor(options = {}) {
    this.ffmpegPath = options.ffmpegPath || 'ffmpeg';
    this.outputDir = options.outputDir || '/job/tmp';
    this.timeout = options.timeout || 5000000; // 5 seconds in microseconds
  }

  /**
   * Build RTSP URL with credentials
   */
  buildUrl(config) {
    if (config.url.includes('@')) {
      return config.url; // Already has credentials
    }

    if (config.username && config.password) {
      const protocol = config.url.split('://')[0];
      const rest = config.url.replace(`${protocol}://`, '');
      return `${protocol}://${encodeURIComponent(config.username)}:${encodeURIComponent(config.password)}@${rest}`;
    }

    return config.url;
  }

  /**
   * Capture a snapshot from a camera
   * @param {object} config - Camera configuration
   * @returns {string} Path to output image
   */
  captureSnapshot(config) {
    const {
      url,
      username,
      password,
      output = null,
      timeout = this.timeout
    } = config;

    const cameraUrl = this.buildUrl({ url, username, password });
    const outputFile = output || path.join(
      this.outputDir,
      `snapshot_${Date.now()}.jpg`
    );

    try {
      execSync(
        `${this.ffmpegPath} -rtsp_transport tcp -stimeout ${timeout} -i "${cameraUrl}" -frames:v 1 -q:v 2 "${outputFile}"`,
        { stdio: 'pipe', timeout: 30000 }
      );

      if (fs.existsSync(outputFile)) {
        return { success: true, path: outputFile };
      } else {
        return { success: false, error: 'Failed to capture snapshot' };
      }
    } catch (e) {
      return { success: false, error: e.message };
    }
  }

  /**
   * Capture a short video clip from a camera
   * @param {object} config - Camera configuration
   * @returns {string} Path to output video
   */
  captureClip(config) {
    const {
      url,
      username,
      password,
      duration = 5,
      output = null,
      timeout = this.timeout
    } = config;

    const cameraUrl = this.buildUrl({ url, username, password });
    const outputFile = output || path.join(
      this.outputDir,
      `clip_${Date.now()}.mp4`
    );

    try {
      execSync(
        `${this.ffmpegPath} -rtsp_transport tcp -stimeout ${timeout} -i "${cameraUrl}" -t ${duration} -c copy "${outputFile}"`,
        { stdio: 'pipe', timeout: 30000 + (duration * 1000) }
      );

      if (fs.existsSync(outputFile)) {
        return { success: true, path: outputFile };
      } else {
        return { success: false, error: 'Failed to capture clip' };
      }
    } catch (e) {
      return { success: false, error: e.message };
    }
  }

  /**
   * Test camera connection
   * @param {object} config - Camera configuration
   * @returns {object} Connection test result
   */
  testConnection(config) {
    const {
      url,
      username,
      password,
      timeout = this.timeout
    } = config;

    const cameraUrl = this.buildUrl({ url, username, password });

    try {
      // Try to get stream info with ffprobe
      const output = execSync(
        `${this.ffmpegPath.replace('ffmpeg', 'ffprobe')} -rtsp_transport tcp -stimeout ${timeout} -v quiet -print_format json -show_format -show_streams "${cameraUrl}"`,
        { encoding: 'utf-8', timeout: 15000 }
      );

      const info = JSON.parse(output);
      return {
        success: true,
        connected: true,
        info: {
          duration: info.format?.duration,
          streams: info.streams?.length,
          videoCodec: info.streams?.find(s => s.codec_type === 'video')?.codec_name,
          resolution: info.streams?.find(s => s.codec_type === 'video')?.width 
            ? `${info.streams.find(s => s.codec_type === 'video').width}x${info.streams.find(s => s.codec_type === 'video').height}`
            : null
        }
      };
    } catch (e) {
      return {
        success: false,
        connected: false,
        error: e.message
      };
    }
  }

  /**
   * Capture snapshots from multiple cameras
   * @param {Array} cameras - Array of camera configurations
   * @returns {Array} Results for each camera
   */
  captureMultiSnapshot(cameras) {
    const results = [];

    for (const camera of cameras) {
      const result = this.captureSnapshot(camera);
      results.push({
        name: camera.name || camera.url,
        ...result
      });
    }

    return results;
  }

  /**
   * Generate a thumbnail grid from multiple cameras
   * @param {Array} cameras - Array of camera configurations
   * @param {object} options - Grid options
   * @returns {string} Path to output grid image
   */
  generateMultiCameraGrid(cameras, options = {}) {
    const {
      columns = 3,
      output = null
    } = options;

    // First capture all snapshots
    const snapshots = [];
    for (const camera of cameras) {
      const result = this.captureSnapshot(camera);
      if (result.success) {
        snapshots.push(result.path);
      }
    }

    if (snapshots.length === 0) {
      return { success: false, error: 'No snapshots captured' };
    }

    const outputFile = output || path.join(
      this.outputDir,
      `grid_${Date.now()}.jpg`
    );

    // Create input list for ffmpeg
    const inputList = snapshots.map(s => `-i "${s}"`).join(' ');
    const filterComplex = snapshots.map((_, i) => `[${i}:v]`).join('');

    try {
      execSync(
        `${this.ffmpegPath} ${inputList} -filter_complex "${filterComplex}tile=${columns}x${Math.ceil(snapshots.length / columns)}" "${outputFile}"`,
        { stdio: 'pipe' }
      );

      return { success: true, path: outputFile, snapshots: snapshots.length };
    } catch (e) {
      return { success: false, error: e.message };
    }
  }
}

module.exports = Camsnap;

/**
 * Gifgrep Skill
 * Search GIF providers (GIPHY, Tenor), download and extract frames
 */

const https = require('https');
const http = require('http');
const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');
const { URL } = require('url');

class Gifgrep {
  constructor(options = {}) {
    this.giphyApiKey = options.giphyApiKey || process.env.GIPHY_API_KEY;
    this.tenorApiKey = options.tenorApiKey || process.env.TENOR_API_KEY;
    this.outputDir = options.outputDir || '/job/tmp';
    this.limit = options.limit || 10;
  }

  /**
   * Make HTTP request
   */
  request(url) {
    return new Promise((resolve, reject) => {
      const client = url.startsWith('https') ? https : http;
      client.get(url, (res) => {
        let data = '';
        res.on('data', chunk => data += chunk);
        res.on('end', () => {
          try {
            resolve(JSON.parse(data));
          } catch (e) {
            resolve({ raw: data });
          }
        });
      }).on('error', reject);
    });
  }

  /**
   * Search GIPHY for GIFs
   * @param {string} query - Search query
   * @param {object} options - Search options
   * @returns {Array} Array of GIF results
   */
  async searchGiphy(query, options = {}) {
    if (!this.giphyApiKey) {
      throw new Error('GIPHY_API_KEY not set');
    }

    const params = new URLSearchParams({
      api_key: this.giphyApiKey,
      q: query,
      limit: options.limit || this.limit,
      rating: options.rating || 'g'
    });

    const url = `https://api.giphy.com/v1/gifs/search?${params}`;
    const response = await this.request(url);

    return (response.data || []).map(gif => ({
      id: gif.id,
      title: gif.title,
      url: gif.images.original.url,
      previewUrl: gif.images.downsized_small?.url || gif.images.fixed_height.url,
      width: gif.images.original.width,
      height: gif.images.original.height,
      source: 'giphy'
    }));
  }

  /**
   * Search Tenor for GIFs
   * @param {string} query - Search query
   * @param {object} options - Search options
   * @returns {Array} Array of GIF results
   */
  async searchTenor(query, options = {}) {
    const apiKey = this.tenorApiKey || 'demo_key'; // Tenor provides a demo key
    const params = new URLSearchParams({
      key: apiKey,
      q: query,
      limit: options.limit || this.limit,
      media_filter: 'minimal'
    });

    const url = `https://tenor.googleapis.com/v2/search?${params}`;
    const response = await this.request(url);

    return (response.results || []).map(gif => ({
      id: gif.id,
      title: gif.content_description,
      url: gif.media_formats.gif.url,
      previewUrl: gif.media_formats.tinygif.url,
      width: gif.media_formats.gif.dims[0],
      height: gif.media_formats.gif.dims[1],
      source: 'tenor'
    }));
  }

  /**
   * Search both providers and merge results
   * @param {string} query - Search query
   * @param {object} options - Search options
   * @returns {Array} Combined GIF results
   */
  async search(query, options = {}) {
    const {
      source = 'auto', // 'auto', 'giphy', 'tenor'
      limit = this.limit
    } = options;

    let results = [];

    if (source === 'auto' || source === 'giphy') {
      try {
        const giphyResults = await this.searchGiphy(query, { limit: source === 'auto' ? Math.ceil(limit / 2) : limit });
        results = results.concat(giphyResults);
      } catch (e) {
        console.error('GIPHY search failed:', e.message);
      }
    }

    if (source === 'auto' || source === 'tenor') {
      try {
        const tenorResults = await this.searchTenor(query, { limit: source === 'auto' ? Math.ceil(limit / 2) : limit });
        results = results.concat(tenorResults);
      } catch (e) {
        console.error('Tenor search failed:', e.message);
      }
    }

    return results.slice(0, limit);
  }

  /**
   * Download a GIF from URL
   * @param {string} url - GIF URL
   * @param {string} filename - Output filename (optional)
   * @returns {string} Path to downloaded file
   */
  async download(url, filename = null) {
    return new Promise((resolve, reject) => {
      const client = url.startsWith('https') ? https : http;

      const outputFile = filename || path.join(
        this.outputDir,
        `gif_${Date.now()}.gif`
      );

      const file = fs.createWriteStream(outputFile);

      client.get(url, (response) => {
        if (response.statusCode === 301 || response.statusCode === 302) {
          // Follow redirect
          file.close();
          fs.unlinkSync(outputFile);
          this.download(response.headers.location, filename)
            .then(resolve)
            .catch(reject);
          return;
        }

        response.pipe(file);
        file.on('finish', () => {
          file.close();
          resolve(outputFile);
        });
      }).on('error', (err) => {
        fs.unlink(outputFile, () => reject(err));
      });
    });
  }

  /**
   * Extract a still frame from a GIF
   * @param {string} gifPath - Path to GIF file
   * @param {object} options - Extraction options
   * @returns {string} Path to output image
   */
  extractStill(gifPath, options = {}) {
    const {
      timestamp = '0', // Time in seconds
      format = 'png',
      output = null
    } = options;

    if (!fs.existsSync(gifPath)) {
      throw new Error(`GIF not found: ${gifPath}`);
    }

    const outputFile = output || path.join(
      this.outputDir,
      `still_${Date.now()}.${format}`
    );

    execSync(
      `ffmpeg -i "${gifPath}" -ss ${timestamp} -frames:v 1 -vsync vfr "${outputFile}"`,
      { stdio: 'pipe' }
    );

    return outputFile;
  }

  /**
   * Create a contact sheet from a GIF
   * @param {string} gifPath - Path to GIF file
   * @param {object} options - Sheet options
   * @returns {string} Path to output image
   */
  createContactSheet(gifPath, options = {}) {
    const {
      frames = 9,
      columns = 3,
      width = 100,
      output = null
    } = options;

    if (!fs.existsSync(gifPath)) {
      throw new Error(`GIF not found: ${gifPath}`);
    }

    const outputFile = output || path.join(
      this.outputDir,
      `sheet_${Date.now()}.png`
    );

    const rows = Math.ceil(frames / columns);

    execSync(
      `ffmpeg -i "${gifPath}" -vf "fps=${frames/10},scale=${width}:-1,tile=${columns}x${rows}" "${outputFile}"`,
      { stdio: 'pipe' }
    );

    return outputFile;
  }

  /**
   * Get trending GIFs from GIPHY
   * @param {object} options - Search options
   * @returns {Array} Trending GIFs
   */
  async getTrending(options = {}) {
    if (!this.giphyApiKey) {
      throw new Error('GIPHY_API_KEY not set');
    }

    const params = new URLSearchParams({
      api_key: this.giphyApiKey,
      limit: options.limit || this.limit,
      rating: options.rating || 'g'
    });

    const url = `https://api.giphy.com/v1/gifs/trending?${params}`;
    const response = await this.request(url);

    return (response.data || []).map(gif => ({
      id: gif.id,
      title: gif.title,
      url: gif.images.original.url,
      previewUrl: gif.images.downsized_small?.url || gif.images.fixed_height.url,
      width: gif.images.original.width,
      height: gif.images.original.height,
      source: 'giphy'
    }));
  }

  /**
   * Search and download top result
   * @param {string} query - Search query
   * @returns {object} Download result with metadata
   */
  async searchAndDownload(query) {
    const results = await this.search(query, { limit: 1 });

    if (results.length === 0) {
      return { success: false, error: 'No results found' };
    }

    const gif = results[0];
    const downloadedPath = await this.download(gif.url);

    return {
      success: true,
      gif: gif,
      path: downloadedPath
    };
  }
}

