    console.log(colors.bold + 'Summary:' + colors.reset);
    console.log("  Total:  ".concat(config.repeat, " requests"));
    console.log(colors.green + "  Success: ".concat(successCount).concat(colors.reset));
    if (failCount > 0)
        console.log(colors.red + "  Failed:  ".concat(failCount).concat(colors.reset));
    console.log("  Avg Time: ".concat(Math.round(totalTime / successCount) || 0, "ms"));
