#!/usr/bin/env node
/**
 * Dependency Scanner - Check for outdated dependencies
 */

import fs from 'fs';
import path from 'path';
import { execSync } from 'child_process';

// Colors
const colors = {
  reset: '\x1b[0m',
  red: '\x1b[31m',
  yellow: '\x1b[33m',
  green: '\x1b[32m',
  blue: '\x1b[34m',
  cyan: '\x1b[36m',
  bold: '\x1b[1m'
};

const check = colors.green + '‚úì' + colors.reset;
const warn = colors.yellow + '!' + colors.reset;
const fail = colors.red + '‚úó' + colors.reset;

// Parse arguments
const args = process.argv.slice(2);
let targetDir = '.';
let showOutdated = args.includes('--outdated');
let showSecurity = args.includes('--security');
let specificPackage = args.find((_, i) => args[i-1] === '--package');

// Find target directory
const dirIndex = args.findIndex(a => !a.startsWith('--') && !args[args.indexOf(a)-1]?.startsWith('--'));
if (dirIndex >= 0) {
  targetDir = args[dirIndex];
}

function printHeader(title) {
  console.log(colors.cyan + '‚ïê'.repeat(66) + colors.reset);
  console.log(colors.bold + title.padStart(33 + title.length/2).padEnd(66) + colors.reset);
  console.log(colors.cyan + '‚ïê'.repeat(66) + colors.reset);
  console.log();
}

function parseVersion(version) {
  // Remove leading ^, ~, >=, <=, etc.
  const clean = version.replace(/^[^0-9]*/, '');
  const parts = clean.split('.').map(Number);
  return {
    major: parts[0] || 0,
    minor: parts[1] || 0,
    patch: parts[2] || 0,
    raw: version
  };
}

function compareVersions(v1, v2) {
  if (v1.major !== v2.major) return v1.major - v2.major;
  if (v1.minor !== v2.minor) return v1.minor - v2.minor;
  return v1.patch - v2.patch;
}

function getUpdateType(current, latest) {
  const cur = parseVersion(current);
  const lat = parseVersion(latest);

  if (cur.major !== lat.major) return 'major';
  if (cur.minor !== lat.minor) return 'minor';
  if (cur.patch !== lat.patch) return 'patch';
  return 'current';
}

async function getLatestVersion(pkg) {
  try {
    const result = execSync(`npm view ${pkg} version 2>/dev/null`, { encoding: 'utf8', timeout: 5000 });
    return result.trim();
  } catch (e) {
    return null;
  }
}

async function scanDependencies(projectDir) {
  const packageJsonPath = path.join(projectDir, 'package.json');

  if (!fs.existsSync(packageJsonPath)) {
    console.error(colors.red + `Error: No package.json found in ${projectDir}` + colors.reset);
    process.exit(1);
  }

  const packageJson = JSON.parse(fs.readFileSync(packageJsonPath, 'utf8'));
  const deps = packageJson.dependencies || {};
  const devDeps = packageJson.devDependencies || {};

  printHeader('DEPENDENCY SCAN REPORT');

  console.log(`${colors.bold}Project:${colors.reset} ${packageJson.name || 'unknown'}`);
  console.log(`${colors.bold}Package Manager:${colors.reset} npm`);
  console.log(`${colors.bold}Location:${colors.reset} ${projectDir}`);
  console.log();

  const results = {
    current: [],
    patch: [],
    minor: [],
    major: []
  };

  // Process dependencies
  if (Object.keys(deps).length > 0) {
    console.log(colors.bold + `DEPENDENCIES (${Object.keys(deps).length} total)` + colors.reset);
    console.log(colors.cyan + '‚îÄ'.repeat(66) + colors.reset);

    for (const [pkg, version] of Object.entries(deps)) {
      if (specificPackage && pkg !== specificPackage) continue;

      const latest = await getLatestVersion(pkg);
      if (!latest) {
        console.log(`  ${warn} ${pkg.padEnd(15)} ${version.padEnd(12)} (unable to check)`);
        continue;
      }

      const updateType = getUpdateType(version.replace(/^[^0-9]*/, ''), latest);

      if (updateType === 'current') {
        if (!showOutdated && !showSecurity) {
          console.log(`  ${check} ${colors.green}${pkg.padEnd(15)}${colors.reset} ${version.padEnd(12)} (current)`);
        }
        results.current.push({ pkg, version, latest });
      } else if (updateType === 'patch') {
        console.log(`  ${warn} ${colors.yellow}${pkg.padEnd(15)}${colors.reset} ${version.padEnd(12)} ‚Üí ${colors.green}${latest}${colors.reset} (patch)`);
        results.patch.push({ pkg, version, latest });
      } else if (updateType === 'minor') {
        console.log(`  ${warn} ${colors.yellow}${pkg.padEnd(15)}${colors.reset} ${version.padEnd(12)} ‚Üí ${colors.green}${latest}${colors.reset} (minor)`);
        results.minor.push({ pkg, version, latest });
      } else {
        console.log(`  ${fail} ${colors.red}${pkg.padEnd(15)}${colors.reset} ${version.padEnd(12)} ‚Üí ${colors.yellow}${latest}${colors.reset} (major)`);
        results.major.push({ pkg, version, latest });
      }
    }
    console.log();
  }

  // Process dev dependencies
  if (Object.keys(devDeps).length > 0 && !specificPackage) {
    console.log(colors.bold + `DEV DEPENDENCIES (${Object.keys(devDeps).length} total)` + colors.reset);
    console.log(colors.cyan + '‚îÄ'.repeat(66) + colors.reset);

    for (const [pkg, version] of Object.entries(devDeps)) {
      const latest = await getLatestVersion(pkg);
      if (!latest) {
        console.log(`  ${warn} ${pkg.padEnd(15)} ${version.padEnd(12)} (unable to check)`);
        continue;
      }

      const updateType = getUpdateType(version.replace(/^[^0-9]*/, ''), latest);

      if (updateType === 'current') {
        if (!showOutdated && !showSecurity) {
          console.log(`  ${check} ${colors.green}${pkg.padEnd(15)}${colors.reset} ${version.padEnd(12)} (current)`);
        }
        results.current.push({ pkg, version, latest, dev: true });
      } else if (updateType === 'patch') {
        console.log(`  ${warn} ${colors.yellow}${pkg.padEnd(15)}${colors.reset} ${version.padEnd(12)} ‚Üí ${colors.green}${latest}${colors.reset} (patch)`);
        results.patch.push({ pkg, version, latest, dev: true });
      } else if (updateType === 'minor') {
        console.log(`  ${warn} ${colors.yellow}${pkg.padEnd(15)}${colors.reset} ${version.padEnd(12)} ‚Üí ${colors.green}${latest}${colors.reset} (minor)`);
        results.minor.push({ pkg, version, latest, dev: true });
      } else {
        console.log(`  ${fail} ${colors.red}${pkg.padEnd(15)}${colors.reset} ${version.padEnd(12)} ‚Üí ${colors.yellow}${latest}${colors.reset} (major)`);
        results.major.push({ pkg, version, latest, dev: true });
      }
    }
    console.log();
  }

  // Summary
  console.log(colors.cyan + '‚îÄ'.repeat(66) + colors.reset);
  console.log(colors.bold + 'SUMMARY' + colors.reset);
  console.log(colors.cyan + '‚îÄ'.repeat(66) + colors.reset);
  console.log(`  ${colors.green}Up to date:${colors.reset}      ${results.current.length}`);
  console.log(`  ${colors.blue}Patch updates:${colors.reset}   ${results.patch.length}`);
  console.log(`  ${colors.yellow}Minor updates:${colors.reset}   ${results.minor.length}`);
  console.log(`  ${colors.red}Major updates:${colors.reset}   ${results.major.length}`);
  console.log();

  if (results.major.length > 0) {
    console.log(colors.yellow + '‚ö† Consider updating packages with major version updates' + colors.reset);
  }
}

// Main
scanDependencies(targetDir);

#!/usr/bin/env node
/**
 * Webhook Tester - HTTP endpoint testing tool
 * Inspired by OpenClaw webhook automation
 */

import { execSync } from 'child_process';
import fs from 'fs';
import path from 'path';

// Colors
const colors = {
  reset: '\x1b[0m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  cyan: '\x1b[36m',
  bold: '\x1b[1m'
};

function parseArgs(args) {
  const config = {
    url: '',
    method: 'GET',
    headers: {},
    data: null,
    file: null,
    repeat: 1,
    followRedirects: false,
    verbose: false,
    showHeaders: false,
    showBody: false,
    timeout: 30
  };

  let i = 0;
  while (i < args.length) {
    const arg = args[i];
    if (!arg.startsWith('--')) {
      if (!config.url) {
        config.url = arg;
      }
    } else {
      switch (arg) {
        case '--method':
        case '-X':
          config.method = args[++i]?.toUpperCase() || 'GET';
          break;
        case '--header':
        case '-H':
          const header = args[++i];
          if (header?.includes(':')) {
            const [key, ...valueParts] = header.split(':');
            config.headers[key] = valueParts.join(':').trim();
          }
          break;
        case '--data':
        case '-d':
          config.data = args[++i];
          break;
        case '--file':
        case '-f':
          config.file = args[++i];
          break;
        case '--repeat':
          config.repeat = parseInt(args[++i]) || 1;
          break;
        case '--follow-redirects':
        case '-L':
          config.followRedirects = true;
          break;
        case '--verbose':
        case '-v':
          config.verbose = true;
          break;
        case '--show-headers':
        case '-i':
          config.showHeaders = true;
          break;
        case '--show-body':
          config.showBody = true;
          break;
        case '--timeout':
          config.timeout = parseInt(args[++i]) || 30;
          break;
      }
    }
    i++;
  }
  return config;
}

function makeRequest(url, options) {
  const cmd = ['curl', '-s', '-w', '\n%{http_code}\n%{time_total}\n%{size_download}'];

  cmd.push('-X', options.method);

  for (const [key, value] of Object.entries(options.headers)) {
    cmd.push('-H', `${key}: ${value}`);
  }

  if (options.followRedirects) {
    cmd.push('-L');
  }

  cmd.push('--connect-timeout', options.timeout.toString());
  cmd.push('--max-time', (options.timeout * 2).toString());

  if (options.data) {
    cmd.push('-d', options.data);
  } else if (options.file && fs.existsSync(options.file)) {
    cmd.push('-d', '@' + options.file);
    if (!options.headers['Content-Type']) {
      const ext = path.extname(options.file);
      if (ext === '.json') {
        options.headers['Content-Type'] = 'application/json';
        cmd.push('-H', 'Content-Type: application/json');
      }
    }
  }

  cmd.push(url);

  const startTime = Date.now();
  try {
    const output = execSync(cmd.join(' '), { encoding: 'utf8', maxBuffer: 10 * 1024 * 1024 });
    const lines = output.trim().split('\n');
    const httpCode = parseInt(lines[lines.length - 3]) || 0;
    const timeTotal = lines[lines.length - 2];
    const sizeDownload = lines[lines.length - 1];

    lines.pop();
    lines.pop();
    lines.pop();
    const body = lines.join('\n');

    const endTime = Date.now();
    return {
      success: httpCode >= 200 && httpCode < 400,
      statusCode: httpCode,
      body: body,
      time: endTime - startTime,
      size: parseInt(sizeDownload) || 0
    };
  } catch (e) {
    return {
      success: false,
      statusCode: 0,
      error: e.message || 'Request failed',
      time: 0,
      size: 0
    };
  }
}

function getStatusText(code) {
  const statuses = {
    200: 'OK',
    201: 'Created',
    204: 'No Content',
    301: 'Moved Permanently',
    302: 'Found',
    304: 'Not Modified',
    400: 'Bad Request',
    401: 'Unauthorized',
    403: 'Forbidden',
    404: 'Not Found',
    500: 'Internal Server Error',
    502: 'Bad Gateway',
    503: 'Service Unavailable'
  };
  return statuses[code] || '';
}

function formatBytes(bytes) {
  if (bytes === 0) return '0 B';
  const k = 1024;
  const sizes = ['B', 'KB', 'MB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
}

function printResult(result, config) {
  if (!result.success) {
    console.log(colors.red + '‚úó Request failed' + colors.reset);
    console.log('Error: ' + result.error);
    return;
  }

  console.log(`${colors.bold}URL:${colors.reset} ${colors.blue}${config.url}${colors.reset}`);
  console.log(`${colors.bold}Method:${colors.reset} ${config.method}`);
  const statusColor = result.statusCode < 300 ? colors.green :
                      result.statusCode < 400 ? colors.yellow : colors.red;
  console.log(`${colors.bold}Status:${colors.reset} ${statusColor}${result.statusCode} ${getStatusText(result.statusCode)}${colors.reset}`);
  console.log(`${colors.bold}Time:${colors.reset} ${result.time}ms`);
  console.log(`${colors.bold}Size:${colors.reset} ${formatBytes(result.size)}`);

  if (config.showHeaders || config.verbose) {
    console.log('\nResponse Body:');
    if (result.body.length > 500) {
      console.log(result.body.substring(0, 500) + '...');
    } else {
      console.log(result.body || '(empty body)');
    }
  }
}

// Main
const args = process.argv.slice(2);
if (args.length === 0 || args[0].startsWith('--help')) {
  console.log('Usage: webhook.js <url> [options]');
  console.log();
  console.log('Options:');
  console.log('  --method, -X <method>   HTTP method (GET, POST, PUT, DELETE)');
  console.log('  --header, -H <header>   Add header ("Name: Value")');
  console.log('  --data, -d <data>       Request body data');
  console.log('  --file, -f <file>       Read body from file');
  console.log('  --timeout <sec>         Request timeout (default: 30)');
  console.log('  --verbose, -v           Show full details');
  process.exit(1);
}

const config = parseArgs(args);
if (!config.url) {
  console.error(colors.red + 'Error: URL required' + colors.reset);
  process.exit(1);
}

console.log(colors.cyan + `Testing ${config.url}...` + colors.reset);
console.log();

const result = makeRequest(config.url, config);
printResult(result, config);

