#!/usr/bin/env node

/**
 * Simple Email Sender - Send emails via nodemailer or mail command
 */

const nodemailer = require('nodemailer');
const { execSync } = require('child_process');
const fs = require('fs');

const EMAIL_FROM = process.env.EMAIL_FROM || 'thepopebot@example.com';
const SMTP_HOST = process.env.SMTP_HOST;
const SMTP_PORT = process.env.SMTP_PORT || 587;
const SMTP_USER = process.env.SMTP_USER;
const SMTP_PASS = process.env.SMTP_PASS;

/**
 * Send email using nodemailer
 */
async function sendWithNodemailer(to, subject, body, isImportant = false) {
  // Create transporter
  let transporter;

  if (SMTP_HOST && SMTP_USER && SMTP_PASS) {
    transporter = nodemailer.createTransport({
      host: SMTP_HOST,
      port: SMTP_PORT,
      secure: SMTP_PORT === 465,
      auth: {
        user: SMTP_USER,
        pass: SMTP_PASS
      }
    });
  } else {
    // Try using mail command as fallback
    throw new Error('SMTP not configured. Set SMTP_HOST, SMTP_USER, SMTP_PASS environment variables.');
  }

  // Send mail
  const info = await transporter.sendMail({
    from: EMAIL_FROM,
    to,
    subject: isImportant ? `â­ ${subject}` : subject,
    text: body,
    html: body.replace(/\n/g, '<br>')
  });

  console.log(`Email sent: ${info.messageId}`);
  return info;
}

/**
 * Send email using mail command (fallback)
 */
function sendWithMail(to, subject, body, isImportant = false) {
  const prefix = isImportant ? 'â­ ' : '';
  const fullSubject = prefix + subject;

  // Write body to temp file
  const tempFile = '/tmp/email_body.txt';
  fs.writeFileSync(tempFile, body);

  try {
    execSync(`mail -s "${fullSubject}" "${to}" < ${tempFile}`, {
      stdio: 'pipe'
    });
    console.log('Email sent via mail command');
    return true;
  } catch (error) {
    console.error('Failed to send email via mail:', error.message);
    return false;
  }
}

/**
 * Main send function
 */
async function sendEmail({ to, subject, body, isImportant = false }) {
  console.log(`Sending email to: ${to}`);
  console.log(`Subject: ${subject}`);
  console.log(`Important: ${isImportant}`);

  try {
    // Try nodemailer first
    if (SMTP_HOST && SMTP_USER && SMTP_PASS) {
      return await sendWithNodemailer(to, subject, body, isImportant);
    }

    // Try mail command
    if (fs.existsSync('/usr/bin/mail') || fs.existsSync('/usr/sbin/sendmail')) {
      return sendWithMail(to, subject, body, isImportant);
    }

    throw new Error('No email sending method available');
  } catch (error) {
    console.error('Email send failed:', error.message);
    throw error;
  }
}

// CLI interface
async function main() {
  const args = process.argv.slice(2);

  if (args.length < 2) {
    console.log(`
Email Sender

Usage:
  node send-email.js <to> <subject> <body>
  node send-email.js --test

Environment variables (for SMTP):
  SMTP_HOST - SMTP server hostname
  SMTP_PORT - SMTP port (default: 587)
  SMTP_USER - SMTP username
  SMTP_PASS - SMTP password
  EMAIL_FROM - From address (default: thepopebot@example.com)
`);
    process.exit(0);
  }

  if (args[0] === '--test') {
    console.log('Testing email configuration...');
    console.log('SMTP configured:', !!(SMTP_HOST && SMTP_USER && SMTP_PASS));
    if (SMTP_HOST) {
      console.log(`SMTP Host: ${SMTP_HOST}:${SMTP_PORT}`);
    }
    process.exit(0);
  }

  const [to, subject, ...bodyParts] = args;
  const body = bodyParts.join(' ');

  try {
    await sendEmail({ to, subject, body });
    console.log('âœ… Email sent successfully!');
  } catch (error) {
    console.error('âŒ Failed to send email:', error.message);
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

module.exports = { sendEmail };

#!/usr/bin/env node

/**
 * Link Scraper - PopeBot Skill
 * 
 * Fetches, extracts, and summarizes content from URLs.
 * Created based on research from OpenClaw's link-understanding feature.
 * 
 * Location: /job/.pi/skills/link-scraper/
 */

const http = require('http');
const https = require('https');
const { URL } = require('url');

const MAX_CONTENT_LENGTH = 50000;
const DEFAULT_TIMEOUT = 15000;

// Try to load cheerio
let cheerio;
try {
  cheerio = require('cheerio');
} catch (e) {
  cheerio = null;
}

/**
 * Fetch URL content
 */
function fetch(url, options = {}) {
  return new Promise((resolve, reject) => {
    const parsedUrl = new URL(url);
    const client = parsedUrl.protocol === 'https:' ? https : http;

    const req = client.get(url, {
      timeout: options.timeout || DEFAULT_TIMEOUT,
      headers: {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8',
        'Accept-Language': 'en-US,en;q=0.5',
        ...options.headers
      }
    }, (res) => {
      if (res.statusCode >= 300 && res.statusCode < 400 && res.headers.location) {
        const redirectUrl = new URL(res.headers.location, url).href;
        fetch(redirectUrl, options).then(resolve).catch(reject);
        return;
      }

      if (res.statusCode !== 200) {
        reject(new Error(`HTTP ${res.statusCode}`));
        return;
      }

      let data = [];
      res.on('data', chunk => data.push(chunk));
      res.on('end', () => resolve(Buffer.concat(data).toString('utf8')));
    });

    req.on('error', reject);
    req.on('timeout', () => {
      req.destroy();
      reject(new Error('Request timeout'));
    });
  });
}

/**
 * Extract data from HTML
 */
function extractData(html, baseUrl) {
  if (cheerio) {
    const $ = cheerio.load(html);
    const title = $('title').text().trim() || 
                  $('meta[property="og:title"]').attr('content') || 
                  $('h1').first().text().trim() || '';
    const description = $('meta[name="description"]').attr('content') || 
                       $('meta[property="og:description"]').attr('content') || '';
    const siteName = $('meta[property="og:site_name"]').attr('content') || '';

    // Content - try common selectors
    let content = '';
    const selectors = ['article', 'main', '[role="main"]', '.post-content', 
                       '.article-content', '.entry-content', '.content', 
                       '.post', '.article', '#content', '#main', 'body'];
    for (const sel of selectors) {
      const text = $(sel).text().trim();
      if (text.length > 100) {
        content = text.replace(/\s+/g, ' ').trim();
        break;
      }
    }
    if (!content) content = $('body').text().replace(/\s+/g, ' ').trim();

    // Links
    const links = [];
    $('a[href]').each((i, el) => {
      if (links.length >= 20) return;
      const href = $(el).attr('href');
      if (href && !href.startsWith('#') && !href.startsWith('javascript:')) {
        try { links.push(new URL(href, baseUrl).href); } catch {}
      }
    });

    // Images
    const images = [];
    $('img[src]').each((i, el) => {
      if (images.length >= 10) return;
      const src = $(el).attr('src');
      if (src) {
        try { images.push(new URL(src, baseUrl).href); } catch {}
      }
    });

    return { title, description, siteName, content, links, images };
  }

  // Fallback without cheerio
  const titleMatch = html.match(/<title[^>]*>([^<]+)<\/title>/i);
  const descMatch = html.match(/<meta[^>]+name=["']description["'][^>]+content=["']([^"']+)["']/i);
  let content = html.replace(/<script[^>]*>[\s\S]*?<\/script>/gi, '');
  content = content.replace(/<style[^>]*>[\s\S]*?<\/style>/gi, '');
  const bodyMatch = content.match(/<body[^>]*>([\s\S]*)<\/body>/i);

  return {
    title: titleMatch ? titleMatch[1].trim() : '',
    description: descMatch ? descMatch[1] : '',
    siteName: '',
    content: bodyMatch ? bodyMatch[1].replace(/<[^>]+>/g, ' ').replace(/\s+/g, ' ').trim() : '',
    links: [],
    images: []
  };
}

/**
 * Generate extractive summary
 */
function generateSummary(content, maxLength = 300) {
  if (!content || content.length <= maxLength) return content;
  const sentences = content.split(/[.!?]+/).filter(s => s.trim().length > 20);
  let summary = '';
  for (const sentence of sentences) {
    if ((summary + sentence).length > maxLength) break;
    summary += (summary ? '. ' : '') + sentence.trim();
  }
  return summary + '.';
}

/**
 * Extract key points
 */
function extractKeyPoints(content, maxPoints = 5) {
  const sentences = content.split(/[.!?]+/).filter(s => s.trim().length > 30);
  const scored = sentences.map((s, i) => ({
    sentence: s.trim(),
    score: (1 - i/sentences.length) * 0.3 + Math.min(s.trim().length/100, 1) * 0.7
  }));
  scored.sort((a, b) => b.score - a.score);
  return scored.slice(0, maxPoints).map(s => s.sentence.replace(/\s+/g, ' ').trim()).filter(s => s.length > 20);
}

/**
 * Estimate reading time
 */
function estimateReadTime(wordCount) {
  const minutes = Math.ceil(wordCount / 200);
  if (minutes < 1) return '< 1 min';
  if (minutes === 1) return '1 min';
  return `${minutes} min`;
}

/**
 * Main extract function
 */
async function extract(url) {
  const html = await fetch(url);
  const data = extractData(html, url);

  const truncatedContent = data.content.length > MAX_CONTENT_LENGTH 
    ? data.content.substring(0, MAX_CONTENT_LENGTH) + '...'
    : data.content;

  const wordCount = truncatedContent.split(/\s+/).filter(w => w.length > 0).length;

  return {
    url,
    title: data.title,
    description: data.description,
    siteName: data.siteName,
    content: truncatedContent,
    wordCount,
    links: data.links,
    images: [...new Set(data.images)],
    summary: generateSummary(truncatedContent),
    keyPoints: extractKeyPoints(truncatedContent),
    readTime: estimateReadTime(wordCount)
  };
}

// CLI
async function main() {
  const args = process.argv.slice(2);
  const url = args[0];

  if (!url) {
    console.log('Usage: node index.js <url>');
    process.exit(1);
  }

  try {
    const result = await extract(url);
    console.log(JSON.stringify(result, null, 2));
  } catch (error) {
    console.error('Error:', error.message);
    process.exit(1);
  }
}

if (require.main === module) {
  main();
}

module.exports = { extract, fetch };

