#!/usr/bin/env node

/**
 * Trello CLI - Manage Trello boards, lists, and cards
 */

const https = require('https');
const fs = require('fs');
const path = require('path');

const BASE_URL = 'api.trello.com';
const API_VERSION = 1;

// Colors
const colors = {
  reset: '\x1b[0m',
  bright: '\x1b[1m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  cyan: '\x1b[36m'
};

function log(msg, color = 'reset') {
  console.log(`${colors[color]}${msg}${colors.reset}`);
}

function getCreds() {
  return {
    key: process.env.TRELLO_API_KEY,
    token: process.env.TRELLO_TOKEN
  };
}

function trelloRequest(endpoint, options = {}) {
  return new Promise((resolve, reject) => {
    const { key, token } = getCreds();

    if (!key || !token) {
      return reject(new Error('TRELLO_API_KEY and TRELLO_TOKEN environment variables required'));
    }

    const params = new URLSearchParams({ key, token, ...options.query });
    const url = new URL(`/${API_VERSION}${endpoint}?${params}`);

    const reqOptions = {
      hostname: url.hostname,
      path: url.pathname + url.search,
      method: options.method || 'GET',
      headers: {
        'Content-Type': 'application/json',
        'User-Agent': 'thepopebot-trello'
      }
    };

    const req = https.request(reqOptions, (res) => {
      let data = '';
      res.on('data', chunk => data += chunk);
      res.on('end', () => {
        try {
          const json = JSON.parse(data);
          if (res.statusCode >= 200 && res.statusCode < 300) {
            resolve(json);
          } else {
            reject(new Error(`HTTP ${res.statusCode}: ${JSON.stringify(json)}`));
          }
        } catch (e) {
          reject(new Error(`HTTP ${res.statusCode}: ${data}`));
        }
      });
    });

    req.on('error', reject);

    if (options.body) {
      req.write(JSON.stringify(options.body));
    }

    req.end();
  });
}

function parseArgs(args) {
  const parsed = {
    positional: [],
    options: {}
  };

  let i = 0;
  while (i < args.length) {
    const arg = args[i];
    if (arg.startsWith('--')) {
     .replace('--', '');
