#!/usr/bin/env node

/**
 * Simple Email Sender for Progress Report
 * Uses nodemailer - requires SMTP configuration
 */

const nodemailer = require('nodemailer');

const colors = {
  reset: '\x1b[0m',
  bright: '\x1b[1m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  cyan: '\x1b[36m'
};

function log(msg, color = 'reset') {
  console.log(`${colors[color]}${msg}${colors.reset}`);
}

async function sendReport() {
  // Read the report
  const report = require('fs').readFileSync('/tmp/email_summary.txt', 'utf8');

  // Extract body (skip headers)
  const body = report.split('\n\n').slice(2).join('\n\n');

  // Check for SMTP credentials
  const smtpHost = process.env.SMTP_HOST;
  const smtpPort = process.env.SMTP_PORT;
  const smtpUser = process.env.SMTP_USER;
  const smtpPass = process.env.SMTP_PASS;
  const fromEmail = process.env.SMTP_FROM || 'popebot@localhost';

  if (!smtpHost || !smtpUser || !smtpPass) {
    log('\n=== SMTP Configuration Required ===\n', 'yellow');
    log('To send this report, please set the following environment variables:', 'yellow');
    log('  SMTP_HOST    - SMTP server hostname', 'cyan');
    log('  SMTP_PORT    - SMTP port (default: 587)', 'cyan');
    log('  SMTP_USER    - SMTP username', 'cyan');
    log('  SMTP_PASS    - SMTP password', 'cyan');
    log('  SMTP_FROM    - From email address (optional)', 'cyan');
    log('\nOr configure Gmail in the PopeBot environment:', 'yellow');
    log('  POPEBOT_EMAIL_USER    - Gmail address', 'cyan');
    log('  POPEBOT_EMAIL_PASS    - App password', 'cyan');
    log('\n--- Report Content ---\n', 'blue');
    console.log(body);
    log('\n--- End of Report ---\n', 'blue');
    return;
  }

  log('\nSending progress report via email...\n', 'cyan');

  // Create transporter
  const transporter = nodemailer.createTransport({
    host: smtpHost,
    port: parseInt(smtpPort) || 587,
    secure: smtpPort === '465',
    auth: {
      user: smtpUser,
      pass: smtpPass
    }
  });

  try {
    const info = await transporter.sendMail({
      from: fromEmail,
      to: 'winsorllc@yahoo.com',
      subject: 'PopeBot Skills Implementation Progress Report',
      text: body
    });

    log(`\n‚úÖ Email sent successfully!`, 'green');
    log(`Message ID: ${info.messageId}`, 'blue');
  } catch (error) {
    log(`\n‚ùå Failed to send email: ${error.message}`, 'red');
    process.exit(1);
  }
}

sendReport();

