#!/usr/bin/env node
/**
 * Skill Scout - Main Entry Point
 * 
 * Auto-discover, evaluate, and integrate Pi skills from public repositories
 * Adapted from ZeroClaw's SkillForge concept
 * 
 * @module skill-scout
 * @author PopeBot
 * @version 1.0.0
 */

const { Scout } = require('./lib/scout');
const { Evaluator } = require('./lib/evaluator');
const { Installer } = require('./lib/installer');
const { Registry } = require('./lib/registry');
const { Security } = require('./lib/security');

class SkillScout {
  constructor(options = {}) {
    this.options = {
      minScore: options.minScore || 0.7,
      autoInstall: options.autoInstall || false,
      cacheDir: options.cacheDir || './.scout/cache',
      registryPath: options.registryPath || './.scout/registry.json',
      sources: options.sources || ['github'],
      githubToken: options.githubToken || process.env.GITHUB_TOKEN,
      ...options
    };

    this.scout = new Scout(this.options);
    this.evaluator = new Evaluator(this.options);
    this.installer = new Installer(this.options);
    this.registry = new Registry(this.options);
    this.security = new Security(this.options);
  }

  /**
   * Run the full pipeline: Scout ‚Üí Evaluate ‚Üí Integrate
   * @param {Object} params - Pipeline parameters
   * @returns {Promise<Object>} Pipeline results
   */
  async runPipeline(params = {}) {
    console.log('üöÄ Starting Skill Scout pipeline...\n');

    // 1. Scout - Discover candidates
    const discovered = await this.discover({
      query: params.query || 'pi-skill OR popebot-skill',
      limit: params.limit || 20
    });

    if (discovered.length === 0) {
      console.log('‚ÑπÔ∏è No skills discovered');
      return { discovered: 0, evaluated: 0, installed: 0 };
    }

    // 2. Evaluate - Score each candidate
    const evaluated = await this.evaluate({ 
      urls: discovered.map(d => d.url) 
    });

    // 3. Integrate - Install qualified candidates
    const installed = [];
    for (const result of evaluated) {
      if (result.score >= this.options.minScore) {
        if (this.options.autoInstall) {
          const installResult = await this.install({ url: result.url });
          installed.push(installResult);
        } else {
          console.log(`‚è∏Ô∏è Queued for review: ${result.name} (${result.score.toFixed(2)})`);
        }
      }
    }

    return {
      discovered: discovered.length,
      evaluated: evaluated.length,
      installed: installed.length,
      results: evaluated
    };
  }

  /**
   * Discover skills from public repositories
   * @param {Object} params - Discovery parameters
   * @returns {Promise<Array>} Discovered skills
   */
  async discover(params = {}) {
    return await this.scout.discover(params);
  }

  /**
   * Evaluate skill quality and compatibility
   * @param {Object} params - Evaluation parameters
   * @returns {Promise<Array>} Evaluation results
   */
  async evaluate(params = {}) {
    return await this.evaluator.evaluate(params);
  }

  /**
   * Install a skill
   * @param {Object} params - Installation parameters
   * @returns {Promise<Object>} Installation result
   */
  async install(params = {}) {
    return await this.installer.install(params);
  }

  /**
   * Update installed skills
   * @param {Object} params - Update parameters
   * @returns {Promise<Array>} Update results
   */
  async update(params = {}) {
    return await this.installer.update(params);
  }

  /**
   * List discovered or installed skills
   * @param {Object} params - List parameters
   * @returns {Promise<Array>} Skills list
   */
  async list(params = {}) {
    if (params.type === 'installed') {
      return await this.registry.getInstalledSkills();
    } else if (params.type === 'discovered') {
      return await this.scout.getCachedDiscoveries();
    }
    return [];
  }

  /**
   * Remove an installed skill
   * @param {Object} params - Removal parameters
   * @returns {Promise<Object>} Removal result
   */
  async remove(params = {}) {
    return await this.installer.remove(params);
  }

  /**
   * Get status and statistics
   * @param {Object} params - Status parameters
   * @returns {Promise<Object>} Status information
   */
  async status(params = {}) {
    const registry = await this.registry.getRegistry();
    const cache = await this.scout.getCachedDiscoveries();

    return {
      installed: Object.keys(registry.installed).length,
      discovered: cache.length,
      lastUpdate: registry.lastUpdate,
      sources: this.options.sources,
      autoInstall: this.options.autoInstall,
      minScore: this.options.minScore,
      registry
    };
  }

  /**
   * Run security audit on a skill
   * @param {Object} params - Audit parameters
   * @returns {Promise<Object>} Audit results
   */
  async audit(params = {}) {
    return await this.security.audit(params);
  }
}

// Export classes and API
module.exports = {
  SkillScout,
  Scout,
  Evaluator,
  Installer,
  Registry,
  Security
};

// CLI execution
if (require.main === module) {
  const scout = new SkillScout();

  // Parse command line arguments
  const args = process.argv.slice(2);
  const command = args[0];

  switch (command) {
    case 'discover':
      scout.discover({ query: args[1] }).then(results => {
        console.log('\n‚úÖ Discovery complete!');
        console.log(`Found ${results.length} skills\n`);
        results.forEach((skill, i) => {
          console.log(`${i + 1}. ${skill.name}`);
          console.log(`   URL: ${skill.url}`);
          console.log(`   Description: ${skill.description?.substring(0, 60)}...`);
          console.log(`   Stars: ${skill.stars || 'N/A'}\n`);
        });
      }).catch(err => {
        console.error('‚ùå Discovery failed:', err.message);
        process.exit(1);
      });
      break;

    case 'status':
      scout.status().then(status => {
        console.log('\nüìä Skill Scout Status\n');
        console.log(`Installed skills: ${status.installed}`);
        console.log(`Cached discoveries: ${status.discovered}`);
        console.log(`Sources: ${status.sources.join(', ')}`);
        console.log(`Auto-install: ${status.autoInstall ? 'enabled' : 'disabled'}`);
        console.log(`Min score: ${status.minScore}`);
        console.log(`Last update: ${status.lastUpdate || 'Never'}`);
      }).catch(err => {
        console.error('‚ùå Status check failed:', err.message);
        process.exit(1);
      });
      break;

    case 'pipeline':
      scout.runPipeline().then(results => {
        console.log('\n' + '='.repeat(50));
        console.log('Pipeline Complete!');
        console.log('='.repeat(50));
        console.log(`Discovered: ${results.discovered}`);
        console.log(`Evaluated: ${results.evaluated}`);
        console.log(`Installed: ${results.installed}`);
      }).catch(err => {
        console.error('‚ùå Pipeline failed:', err.message);
        process.exit(1);
      });
      break;

    default:
      console.log(`
Skill Scout - Auto-discover Pi skills

Usage: skill-scout <command> [options]

Commands:
  discover <query>    Search for skills on GitHub
  status             Show current status
  pipeline           Run full discovery pipeline

Examples:
  skill-scout discover "notification skill"
  skill-scout status
  skill-scout pipeline

For detailed help, see SKILL.md
`);
  }
}

#!/usr/bin/env node
/**
 * Scout Module - Skill Discovery from GitHub
 * 
 * Searches GitHub for Pi-skill repositories
 * Based on ZeroClaw's GitHubScout
 * 
 * @module scout
 * @author PopeBot (adapted from ZeroClaw)
 */

const fs = require('fs').promises;
const path = require('path');
const https = require('https');

// Cache configuration
const CACHE_TTL = 24 * 60 * 60 * 1000; // 24 hours
const DEFAULT_QUERIES = [
  'pi-skill',
  'popebot-skill',
  'claude-skill',
  'ai-agent-skill',
  'SKILL.md in:readme'
];

class Scout {
  constructor(options = {}) {
    this.cacheDir = options.cacheDir || './.scout/cache';
    this.queries = options.queries || DEFAULT_QUERIES;
    this.githubToken = options.githubToken;
    this.ensureCacheDir();
  }

  async ensureCacheDir() {
    try {
      await fs.mkdir(this.cacheDir, { recursive: true });
    } catch (err) {
      // Directory may already exist
    }
  }

  /**
   * Discover skills from GitHub
   * @param {Object} params - Discovery parameters
   * @returns {Promise<Array>} Discovered skills
   */
  async discover(params = {}) {
    const { query, limit = 30 } = params;
    const searchQueries = query ? [query] : this.queries;

    console.log(`üîç Searching for skills...`);

    const allResults = [];

    for (const q of searchQueries) {
      try {
        const results = await this.searchGitHub(q, limit);
        allResults.push(...results);
      } catch (err) {
        console.warn(`‚ö†Ô∏è Query "${q}" failed: ${err.message}`);
      }
    }

    // Deduplicate by URL
    const deduped = this.deduplicate(allResults);

    // Cache results
    await this.cacheResults(deduped);

    // Filter for SKILL.md presence (basic check)
    const withSkillMd = await this.filterWithSkillMd(deduped);

    console.log(`‚úÖ Found ${withSkillMd.length} potential skills (from ${deduped.length} repos)`);

    return withSkillMd.slice(0, limit);
  }

  /**
   * Search GitHub API for repositories
   * @param {string} query - Search query
   * @param {number} limit - Max results per query
   * @returns {Promise<Array>} Repository results
   */
  async searchGitHub(query, limit = 30) {
    const encodedQuery = encodeURIComponent(query);
    const url = `https://api.github.com/search/repositories?q=${encodedQuery}&sort=stars&order=desc&per_page=${limit}`;

    const options = {
      headers: {
        'Accept': 'application/vnd.github+json',
        'User-Agent': 'PopeBot-SkillScout/1.0',
        ...(this.githubToken && { 'Authorization': `Bearer ${this.githubToken}` })
      }
    };

    return new Promise((resolve, reject) => {
      const req = https.get(url, options, (res) => {
        let data = '';

        res.on('data', (chunk) => {
          data += chunk;
        });

        res.on('end', () => {
          try {
            const json = JSON.parse(data);

            if (json.message) {
              // GitHub API error
              reject(new Error(json.message));
              return;
            }

            const items = json.items || [];
            const results = items.map(item => ({
              id: item.id,
              name: item.name,
              full_name: item.full_name,
              url: item.html_url,
              description: item.description || '',
              stars: item.stargazers_count || 0,
              language: item.language,
              updated_at: item.updated_at,
              created_at: item.created_at,
              owner: item.owner?.login || 'unknown',
              owner_type: item.owner?.type,
              forks: item.forks_count,
              open_issues: item.open_issues_count,
              has_wiki: item.has_wiki,
              has_pages: item.has_pages,
              has_license: item.license ? true : false,
              license: item.license?.spdx_id,
              topics: item.topics || [],
              archived: item.archived,
              fork: item.fork,
              size: item.size,
              source: 'github',
              discovered_at: new Date().toISOString()
            }));

            resolve(results);
          } catch (err) {
            reject(new Error(`Failed to parse GitHub response: ${err.message}`));
          }
        });
      });

      req.on('error', (err) => {
        reject(new Error(`GitHub API request failed: ${err.message}`));
      });

      req.setTimeout(30000, () => {
        req.destroy();
        reject(new Error('GitHub API request timed out'));
      });
    });
  }

  /**
   * Filter repositories that likely have SKILL.md
   * @param {Array} repos - Repository list
   * @returns {Array} Repos with probable SKILL.md
   */
  async filterWithSkillMd(repos) {
    // For each repo, check if it has a SKILL.md file
    const checked = await Promise.all(
      repos.map(async (repo) => {
        try {
          const hasSkillMd = await this.checkHasSkillMd(repo.full_name);
          return hasSkillMd ? repo : null;
        } catch {
          return null;
        }
      })
    );

    return checked.filter(Boolean);
  }

  /**
   * Check if a GitHub repo has SKILL.md
   * @param {string} fullName - Repository full name (owner/repo)
   * @returns {Promise<boolean>}
   */
  async checkHasSkillMd(fullName) {
    // Try multiple paths where SKILL.md might be found
    const paths = [
      'SKILL.md',
      '.pi/skills/SKILL.md',
      'skills/SKILL.md',
      'pi-skills/SKILL.md'
    ];

    for (const p of paths) {
      try {
        const exists = await this.checkFileExists(fullName, p);
        if (exists) return true;
      } catch {
        // Continue to next path
      }
    }

    return false;
  }

  /**
   * Check if a file exists in a GitHub repo
   * @param {string} fullName - Repository full name
   * @param {string} filePath - File path
   * @returns {Promise<boolean>}
   */
  async checkFileExists(fullName, filePath) {
    const url = `https://api.github.com/repos/${fullName}/contents/${filePath}`;

    const options = {
      headers: {
        'Accept': 'application/vnd.github+json',
        'User-Agent': 'PopeBot-SkillScout/1.0'
      }
    };

    return new Promise((resolve, reject) => {
      const req = https.get(url, options, (res) => {
        resolve(res.statusCode === 200);
      });

      req.on('error', () => reject(false));
      req.setTimeout(5000, () => {
        req.destroy();
        reject(false);
      });
    });
  }

  /**
   * Deduplicate results by URL
   * @param {Array} results - Results array
   * @returns {Array} Deduplicated array
   */
  deduplicate(results) {
    const seen = new Map();

    for (const result of results) {
      if (!seen.has(result.url) || seen.get(result.url).stars < result.stars) {
        seen.set(result.url, result);
      }
    }

    return Array.from(seen.values());
  }

  /**
   * Cache results to disk
   * @param {Array} results - Results to cache
   */
  async cacheResults(results) {
    try {
      const cacheFile = path.join(this.cacheDir, 'discoveries.json');
      const data = {
        timestamp: new Date().toISOString(),
        count: results.length,
        results
      };
      await fs.writeFile(cacheFile, JSON.stringify(data, null, 2));
    } catch (err) {
      console.warn('Failed to cache results:', err.message);
    }
  }

  /**
   * Get cached discoveries
   * @returns {Promise<Array>} Cached results
   */
  async getCachedDiscoveries() {
    try {
      const cacheFile = path.join(this.cacheDir, 'discoveries.json');
      const data = await fs.readFile(cacheFile, 'utf-8');
      const parsed = JSON.parse(data);

      // Check cache is not stale
      const cacheAge = Date.now() - new Date(parsed.timestamp).getTime();
      if (cacheAge > CACHE_TTL) {
        console.log('‚ö†Ô∏è Cache is stale, consider running discover again');
      }

      return parsed.results || [];
    } catch {
      return [];
    }
  }

  /**
   * Get discovery statistics
   * @returns {Promise<Object>} Statistics
   */
  async getStats() {
    const discoveries = await this.getCachedDiscoveries();

    const bySource = {};
    const byLanguage = {};
    const byDate = {};

    for (const d of discoveries) {
      bySource[d.source] = (bySource[d.source] || 0) + 1;
      byLanguage[d.language || 'unknown'] = (byLanguage[d.language || 'unknown'] || 0) + 1;
      const date = d.discovered_at?.split('T')[0];
      if (date) byDate[date] = (byDate[date] || 0) + 1;
    }

    return {
      total: discoveries.length,
      bySource,
      byLanguage,
      byDate
    };
  }
}

module.exports = { Scout };

#!/usr/bin/env node
/**
 * Evaluator Module - Skill Scoring and Quality Assessment
 * 
 * Evaluates discovered skills for quality, security, and compatibility
 * Based on ZeroClaw's Evaluator
 * 
 * @module evaluator
 * @author PopeBot
 */

const fs = require('fs').promises;
const path = require('path');
const https = require('https');

// Scoring weights
const SCORING = {
  hasSkillMd: 2.0,
  hasPackageJson: 1.0,
  hasReadme: 0.5,
  hasTests: 1.0,
  securityPassed: 1.0,
  recentUpdate: 1.0,
  stars10Plus: 0.5,
  hasLicense: 0.5,
  compatibility: 1.0
};

class Evaluator {
  constructor(options = {}) {
    this.minScore = options.minScore || 0.7;
    this.maxScore = options.maxScore || 10;
    this.cacheDir = options.cacheDir || './.scout/cache';
  }

  /**
   * Evaluate one or more skills
   * @param {Object} params - Evaluation parameters
   * @returns {Promise<Array>} Evaluation results
   */
  async evaluate(params = {}) {
    const { urls, url, name } = params;

    const targets = [];

    if (url) {
      targets.push({ url, name: name || this.extractRepoName(url) });
    } else if (urls && Array.isArray(urls)) {
      targets.push(...urls.map(u => ({ url: u, name: this.extractRepoName(u) })));
    } else {
      throw new Error('No URLs to evaluate');
    }

    const results = [];

    for (const target of targets) {
      console.log(`üî¨ Evaluating: ${target.name}`);

      const result = await this.evaluateSingle(target);
      results.push(result);

      // Log progress
      const scorePct = (result.score * 100).toFixed(0);
      console.log(`   Score: ${scorePct}% (${result.recommendation})\n`);
    }

    // Cache evaluations
    await this.cacheEvaluations(results);

    return results;
  }

  /**
   * Evaluate a single skill
   * @param {Object} target - Skill target
   * @returns {Promise<Object>} Evaluation result
   */
  async evaluateSingle(target) {
    const { url, name } = target;
    const fullName = this.extractFullName(url);

    if (!fullName) {
      return {
        name,
        url,
        score: 0,
        recommendation: 'skip',
        reasons: ['Could not parse repository URL'],
        details: {}
      };
    }

    try {
      // Fetch repository details
      const repoData = await this.fetchRepoData(fullName);

      // Fetch key files
      const skillMd = await this.fetchSkillMd(fullName);
      const packageJson = await this.fetchPackageJson(fullName);
      const readme = await this.fetchReadme(fullName);
      const testFiles = await this.fetchTestFiles(fullName);

      // Validate SKILL.md
      const skillValidation = this.validateSkillMd(skillMd);

      // Security scan
      const securityScan = this.scanSecurity(skillMd, packageJson);

      // Calculate scores
      const scores = {
        hasSkillMd: skillValidation.valid ? SCORING.hasSkillMd : 0,
        hasPackageJson: packageJson ? SCORING.hasPackageJson : 0,
        hasReadme: readme ? SCORING.hasReadme : 0,
        hasTests: testFiles.length > 0 ? SCORING.hasTests : 0,
        securityPassed: securityScan.passed ? SCORING.securityPassed : 0,
        recentUpdate: this.isRecentUpdate(repoData.updated_at) ? SCORING.recentUpdate : 0,
        stars10Plus: (repoData.stargazers_count || 0) >= 10 ? SCORING.stars10Plus : 0,
        hasLicense: repoData.license ? SCORING.hasLicense : 0,
        compatibility: this.checkCompatibility(skillValidation, packageJson)
      };

      // Calculate total score (normalized to 0-1)
      const maxPossible = Object.values(SCORING).reduce((a, b) => a + b, 0);
      const totalScore = Object.values(scores).reduce((a, b) => a + b, 0);
      const normalizedScore = Math.min(totalScore / maxPossible, 1);

      // Determine recommendation
      const recommendation = this.getRecommendation(normalizedScore);

      return {
        name: skillValidation.name || name,
        url,
        score: normalizedScore,
        recommendation,
        reasons: this.generateReasons(scores, skillValidation, securityScan),
        details: {
          scores,
          github: repoData,
          skillMd: skillValidation,
          security: securityScan,
          fileStats: {
            hasSkillMd: !!skillMd,
            hasPackageJson: !!packageJson,
            hasReadme: !!readme,
            testFileCount: testFiles.length
          }
        }
      };

    } catch (err) {
      return {
        name,
        url,
        score: 0,
        recommendation: 'skip',
        reasons: [`Evaluation failed: ${err.message}`],
        details: {}
      };
    }
  }

  /**
   * Fetch GitHub repository data
   * @param {string} fullName - Owner/repo
   * @returns {Promise<Object>} Repo data
   */
  async fetchRepoData(fullName) {
    const url = `https://api.github.com/repos/${fullName}`;

    return new Promise((resolve, reject) => {
      const req = https.get(url, {
        headers: {
          'Accept': 'application/vnd.github+json',
          'User-Agent': 'PopeBot-SkillScout/1.0'
        }
      }, (res) => {
        let data = '';
        res.on('data', chunk => data += chunk);
        res.on('end', () => {
          try {
            const json = JSON.parse(data);
            if (json.message) reject(new Error(json.message));
            else resolve(json);
          } catch (err) {
            reject(err);
          }
        });
      });

      req.on('error', reject);
      req.setTimeout(10000, () => reject(new Error('Timeout')));
    });
  }

  /**
   * Fetch SKILL.md content
   * @param {string} fullName - Owner/repo
   * @returns {Promise<string|null>} SKILL.md content
   */
  async fetchSkillMd(fullName) {
    const paths = ['SKILL.md', '.pi/skills/SKILL.md', 'skills/SKILL.md'];

    for (const path of paths) {
      const content = await this.fetchFile(fullName, path);
      if (content) return content;
    }

    return null;
  }

  /**
   * Fetch package.json
   * @param {string} fullName - Owner/repo
   * @returns {Promise<Object|null>} Package data
   */
  async fetchPackageJson(fullName) {
    try {
      const content = await this.fetchFile(fullName, 'package.json');
      if (content) return JSON.parse(content);
    } catch {
      // Ignore parse errors
    }
    return null;
  }

  /**
   * Fetch README
   * @param {string} fullName - Owner/repo
   * @returns {Promise<string|null>} README content
   */
  async fetchReadme(fullName) {
    const files = ['README.md', 'readme.md', 'Readme.md'];

    for (const file of files) {
      const content = await this.fetchFile(fullName, file);
      if (content) return content;
    }

    return null;
  }

  /**
   * Fetch test files
   * @param {string} fullName - Owner/repo
   * @returns {Promise<Array>} Test files
   */
  async fetchTestFiles(fullName) {
    // Simplified - check for common test patterns
    const testPatterns = ['test', 'tests', '__tests__', '*.test.js', '*.spec.js'];
    const files = [];

    for (const pattern of testPatterns) {
      try {
        const exists = await this.checkPathExists(fullName, pattern);
        if (exists) files.push(pattern);
      } catch {}
    }

    return files;
  }

  /**
   * Fetch a file from GitHub
   * @param {string} fullName - Owner/repo
   * @param {string} filePath - File path
   * @returns {Promise<string|null>} File content
   */
  async fetchFile(fullName, filePath) {
    const url = `https://api.github.com/repos/${fullName}/contents/${filePath}`;

    return new Promise((resolve, reject) => {
      const req = https.get(url, {
        headers: {
          'Accept': 'application/vnd.github+json',
          'User-Agent': 'PopeBot-SkillScout/1.0'
        }
      }, (res) => {
        if (res.statusCode !== 200) {
          resolve(null);
          return;
        }

        let data = '';
        res.on('data', chunk => data += chunk);
        res.on('end', () => {
          try {
            const json = JSON.parse(data);
            if (json.content) {
              // Base64 decode content
              const decoded = Buffer.from(json.content, 'base64').toString('utf-8');
              resolve(decoded);
            } else {
              resolve(null);
            }
          } catch {
            resolve(null);
          }
        });
      });

      req.on('error', () => resolve(null));
      req.setTimeout(5000, () => {
        req.destroy();
        resolve(null);
      });
    });
  }

  /**
   * Check if a path exists
   * @param {string} fullName - Owner/repo
   * @param {string} path - Path to check
   * @returns {Promise<boolean>}
   */
  async checkPathExists(fullName, path) {
    try {
      const url = `https://api.github.com/repos/${fullName}/contents/${path}`;
      const res = await new Promise((resolve) => {
        const req = https.get(url, {
          headers: { 'User-Agent': 'PopeBot-SkillScout/1.0' }
        }, (res) => resolve(res.statusCode === 200));
        req.on('error', () => resolve(false));
        req.setTimeout(3000, () => { req.destroy(); resolve(false); });
      });
      return res;
    } catch {
      return false;
    }
  }

  /**
   * Validate SKILL.md structure
   * @param {string} content - SKILL.md content
   * @returns {Object} Validation result
   */
  validateSkillMd(content) {
    if (!content) {
      return { valid: false, errors: ['SKILL.md not found'] };
    }

    const errors = [];

    // Check frontmatter
    const frontmatterMatch = content.match(/^---\n([\s\S]*?)\n---/);
    if (!frontmatterMatch) {
      errors.push('No YAML frontmatter');
    } else {
      const frontmatter = frontmatterMatch[1];
      if (!frontmatter.includes('name:')) errors.push('Missing name in frontmatter');
      if (!frontmatter.includes('description:')) errors.push('Missing description in frontmatter');
    }

    // Check for key sections
    if (!content.includes('# ')) errors.push('No H1 heading');

    // Extract name
    const nameMatch = content.match(/name:\s*(.+)/);
    const name = nameMatch ? nameMatch[1].trim() : null;

    return {
      valid: errors.length === 0,
      name,
      errors,
      hasFrontmatter: !!frontmatterMatch
    };
  }

  /**
   * Scan for security issues
   * @param {string} skillMd - SKILL.md content
   * @param {Object} packageJson - Package data
   * @returns {Object} Security scan result
   */
  scanSecurity(skillMd, packageJson) {
    const issues = [];
    const dangerousPatterns = [
      /eval\s*\(/g,
      /exec\s*\(/g,
      /child_process/g,
      /spawn\s*\(/g,
      /rm\s+-rf\s+\//g,
      /curl\s*\|.*bash/g,
      /wget.*-O-\s*\|/g
    ];

    // Check SKILL.md for dangerous patterns
    if (skillMd) {
      for (const pattern of dangerousPatterns) {
        if (pattern.test(skillMd)) {
          issues.push(`Dangerous pattern: ${pattern}`);
        }
      }
    }

    // Check dependencies for known risky packages
    if (packageJson) {
      const deps = Object.keys(packageJson.dependencies || {});
      const riskyPackages = ['eval', 'child_process', 'vm2'];
      for (const pkg of riskyPackages) {
        if (deps.includes(pkg)) {
          issues.push(`Potentially risky dependency: ${pkg}`);
        }
      }
    }

    return {
      passed: issues.length === 0,
      issues,
      score: issues.length === 0 ? 1.0 : Math.max(0, 1 - issues.length * 0.2)
    };
  }

  /**
   * Check if update is recent (within 30 days)
   * @param {string} updatedAt - ISO date
   * @returns {boolean}
   */
  isRecentUpdate(updatedAt) {
    if (!updatedAt) return false;
    const updateDate = new Date(updatedAt);
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);
    return updateDate > thirtyDaysAgo;
  }

  /**
   * Check compatibility with PopeBot
   * @param {Object} skillValidation - Skill validation result
   * @param {Object} packageJson - Package data
   * @returns {number} Compatibility score
   */
  checkCompatibility(skillValidation, packageJson) {
    let score = 0;

    // Has proper SKILL.md
    if (skillValidation.valid) score += 0.5;

    // Has package.json with appropriate fields
    if (packageJson) {
      if (packageJson.description) score += 0.2;
      if (packageJson.keywords?.includes('pi') || packageJson.keywords?.includes('popebot')) {
        score += 0.3;
      }
    }

    return score;
  }

  /**
   * Get recommendation based on score
   * @param {number} score - Normalized score
   * @returns {string} Recommendation
   */
  getRecommendation(score) {
    if (score >= 0.9) return 'excellent';
    if (score >= 0.7) return 'good';
    if (score >= 0.5) return 'fair';
    return 'skip';
  }

  /**
   * Generate human-readable reasons
   * @param {Object} scores - Scores object
   * @param {Object} skillValidation - Validation result
   * @param {Object} securityScan - Security result
   * @returns {Array} Reasons
   */
  generateReasons(scores, skillValidation, securityScan) {
    const reasons = [];

    if (scores.hasSkillMd > 0) reasons.push('Has proper SKILL.md');
    else reasons.push('Missing or invalid SKILL.md');

    if (skillValidation.errors?.length > 0) {
      reasons.push(...skillValidation.errors);
    }

    if (scores.hasPackageJson > 0) reasons.push('Has package.json');
    if (scores.hasTests > 0) reasons.push('Has test files');
    if (scores.recentUpdate > 0) reasons.push('Recently updated');
    if (scores.stars10Plus > 0) reasons.push('Community popular (10+ stars)');

    if (!securityScan.passed) {
      reasons.push(...securityScan.issues);
    }

    return reasons;
  }

  /**
   * Extract full name from GitHub URL
   * @param {string} url - GitHub URL
   * @returns {string|null} owner/repo
   */
  extractFullName(url) {
    const match = url.match(/github\.com\/([^\/]+\/[^\/]+)/);
    return match ? match[1].replace(/\.git$/, '') : null;
  }

  /**
   * Extract repo name from URL
   * @param {string} url - GitHub URL
   * @returns {string} Repo name
   */
  extractRepoName(url) {
    const fullName = this.extractFullName(url);
    return fullName ? fullName.split('/')[1] : 'unknown';
  }

  /**
   * Cache evaluations
   * @param {Array} results - Evaluation results
   */
  async cacheEvaluations(results) {
    try {
      const cacheFile = path.join(this.cacheDir, 'evaluations.json');
      await fs.mkdir(this.cacheDir, { recursive: true });

      const existing = await this.loadCachedEvaluations();
      const combined = [...existing, ...results];

      // Deduplicate
      const deduped = Array.from(
        new Map(combined.map(r => [r.url, r])).values()
      );

      await fs.writeFile(cacheFile, JSON.stringify({
        timestamp: new Date().toISOString(),
        count: deduped.length,
        results: deduped
      }, null, 2));
    } catch (err) {
      console.warn('Failed to cache evaluations:', err.message);
    }
  }

  /**
   * Load cached evaluations
   * @returns {Promise<Array>} Cached evaluations
   */
  async loadCachedEvaluations() {
    try {
      const cacheFile = path.join(this.cacheDir, 'evaluations.json');
      const data = await fs.readFile(cacheFile, 'utf-8');
      const parsed = JSON.parse(data);
      return parsed.results || [];
    } catch {
      return [];
    }
  }

  /**
   * Get evaluation criteria
   * @returns {Object} Scoring criteria
   */
  getCriteria() {
    return {
      weights: SCORING,
      thresholds: {
        excellent: 0.9,
        good: 0.7,
        fair: 0.5,
        skip: 0.0
      },
      maxScore: Object.values(SCORING).reduce((a, b) => a + b, 0)
    };
  }
}

module.exports = { Evaluator };

#!/usr/bin/env node
/**
 * Installer Module - Skill Installation and Management
 * 
 * Handles cloning, symlinking, and updates for Pi skills
 * 
 * @module installer
 * @author PopeBot
 */

const fs = require('fs').promises;
const path = require('path');
const { exec } = require('child_process');
const { promisify } = require('util');

const execAsync = promisify(exec);

class Installer {
  constructor(options = {}) {
    this.skillsDir = options.skillsDir || '/job/.pi/skills';
    this.cacheDir = options.cacheDir || '/job/.pi/skills/skill-scout/.scout';
    this.backupDir = path.join(this.cacheDir, 'backups');
  }

  /**
   * Install a skill
   * @param {Object} params - Installation parameters
   * @returns {Promise<Object>} Installation result
   */
  async install(params = {}) {
    const { url, name: skillName, version, cachedId } = params;

    // Determine source
    let source = url;
    if (cachedId) {
      source = await this.resolveCachedId(cachedId);
    }

    if (!source) {
      throw new Error('No valid source for installation');
    }

    // Parse repo name if not provided
    const repoName = skillName || this.extractRepoName(source);
    const fullName = this.extractFullName(source);

    console.log(`üì¶ Installing: ${repoName}`);
    console.log(`   From: ${source}`);

    try {
      // Check if already installed
      const existing = await this.checkExisting(repoName);
      if (existing) {
        console.log(`‚ö†Ô∏è ${repoName} is already installed`);
        return {
          name: repoName,
          status: 'already-installed',
          path: existing,
          installed: false
        };
      }

      // Clone to cache
      const clonePath = await this.cloneToCache(source, repoName);

      // Create symlink
      const skillPath = path.join(this.skillsDir, repoName);
      const relativeClonePath = path.relative(
        path.dirname(skillPath),
        clonePath
      );

      // Remove existing if broken symlink
      try {
        await fs.unlink(skillPath);
      } catch {}

      await fs.symlink(clonePath, skillPath, 'dir');

      // Install dependencies if package.json exists
      await this.installDependencies(clonePath);

      // Update registry
      await this.updateRegistry({
        name: repoName,
        url: source,
        fullName,
        path: clonePath,
        symlink: skillPath,
        installed_at: new Date().toISOString(),
        version: version || 'latest'
      });

      console.log(`   ‚úÖ Installed to: ${skillPath}`);
      console.log(`   Clone cache: ${clonePath}`);

      return {
        name: repoName,
        status: 'installed',
        path: skillPath,
        symlink: skillPath,
        clonePath,
        installed: true
      };

    } catch (err) {
      console.error(`   ‚ùå Installation failed: ${err.message}`);
      return {
        name: repoName,
        status: 'failed',
        error: err.message,
        installed: false
      };
    }
  }

  /**
   * Update installed skills
   * @param {Object} params - Update parameters
   * @returns {Promise<Array>} Update results
   */
  async update(params = {}) {
    const { name, all = false, check = false } = params;

    if (check) {
      return await this.checkUpdates();
    }

    if (all) {
      return await this.updateAll();
    }

    if (name) {
      return [await this.updateSingle(name)];
    }

    throw new Error('Specify --name, --all, or --check');
  }

  /**
   * Update a single skill
   * @param {string} name - Skill name
   * @returns {Promise<Object>} Update result
   */
  async updateSingle(name) {
    console.log(`üîÑ Updating: ${name}`);

    const registry = await this.getRegistry();
    const entry = registry.installed[name];

    if (!entry) {
      console.log(`   ‚ö†Ô∏è ${name} is not installed`);
      return { name, status: 'not-installed', updated: false };
    }

    try {
      // Check for updates
      const hasUpdate = await this.checkHasUpdate(entry);

      if (!hasUpdate) {
        console.log(`   ‚úÖ Already up to date`);
        return { name, status: 'up-to-date', updated: false };
      }

      if (check) {
        return { name, status: 'update-available', updated: false };
      }

      // Backup current version
      await this.backupSkill(name, entry);

      // Pull latest
      const clonePath = entry.path;
      await execAsync('git pull', { cwd: clonePath });

      // Update dependencies
      await this.installDependencies(clonePath);

      // Update registry
      entry.updated_at = new Date().toISOString();
      await this.saveRegistry(registry);

      console.log(`   ‚úÖ Updated successfully`);

      return {
        name,
        status: 'updated',
        path: entry.symlink,
        updated: true
      };

    } catch (err) {
      console.error(`   ‚ùå Update failed: ${err.message}`);
      return {
        name,
        status: 'failed',
        error: err.message,
        updated: false
      };
    }
  }

  /**
   * Update all installed skills
   * @returns {Promise<Array>} Update results
   */
  async updateAll() {
    const registry = await this.getRegistry();
    const skills = Object.keys(registry.installed);

    console.log(`üîÑ Updating ${skills.length} skills...\n`);

    const results = [];
    for (const name of skills) {
      const result = await this.updateSingle(name);
      results.push(result);
    }

    return results;
  }

  /**
   * Check for available updates
   * @returns {Promise<Array>} Skills with updates
   */
  async checkUpdates() {
    const registry = await this.getRegistry();
    const skills = Object.entries(registry.installed);

    const updates = [];

    for (const [name, entry] of skills) {
      try {
        const hasUpdate = await this.checkHasUpdate(entry);
        if (hasUpdate) {
          updates.push({
            name,
            current: entry.installed_at,
            available: 'newer'
          });
        }
      } catch {}
    }

    return updates;
  }

  /**
   * Remove an installed skill
   * @param {Object} params - Removal parameters
   * @returns {Promise<Object>} Removal result
   */
  async remove(params = {}) {
    const { name, backup = true, force = false } = params;

    console.log(`üóëÔ∏è Removing: ${name}`);

    const registry = await this.getRegistry();
    const entry = registry.installed[name];

    if (!entry) {
      if (!force) {
        throw new Error(`${name} is not installed`);
      }
      // Try to find by symlink
      const skillPath = path.join(this.skillsDir, name);
      try {
        const stats = await fs.lstat(skillPath);
        if (stats.isSymbolicLink()) {
          await fs.unlink(skillPath);
          console.log(`   ‚úÖ Removed symlink: ${skillPath}`);
        }
      } catch {}
      return { name, status: 'removed', backup: false };
    }

    try {
      // Backup if requested
      if (backup) {
        await this.backupSkill(name, entry);
      }

      // Remove symlink
      await fs.unlink(entry.symlink);

      // Remove clone (optional, can keep for backup)
      if (!backup) {
        await fs.rm(entry.path, { recursive: true, force: true });
      }

      // Remove from registry
      delete registry.installed[name];
      await this.saveRegistry(registry);

      console.log(`   ‚úÖ Removed successfully${backup ? ' (backed up)' : ''}`);

      return {
        name,
        status: 'removed',
        backup,
        backedUp: backup
      };

    } catch (err) {
      console.error(`   ‚ùå Removal failed: ${err.message}`);
      throw err;
    }
  }

  /**
   * Clone a repository to cache
   * @param {string} url - Repository URL
   * @param {string} name - Repository name
   * @returns {Promise<string>} Clone path
   */
  async cloneToCache(url, name) {
    // Normalize URL
    let repoUrl = url;
    if (!repoUrl.endsWith('.git')) {
      repoUrl = repoUrl + '.git';
    }

    const cachePath = path.join(this.cacheDir, 'clones', name);

    // Ensure cache directory exists
    await fs.mkdir(path.dirname(cachePath), { recursive: true });

    // Check if already cloned
    const exists = await this.pathExists(path.join(cachePath, '.git'));

    if (exists) {
      console.log(`   Using cached clone: ${cachePath}`);
      // Pull latest
      try {
        await execAsync('git pull', { cwd: cachePath });
      } catch {
        // Ignore git pull errors
      }
      return cachePath;
    }

    // Clone
    console.log(`   Cloning to: ${cachePath}`);
    await execAsync(`git clone --depth 1 "${repoUrl}" "${cachePath}"`, {
      timeout: 60000
    });

    return cachePath;
  }

  /**
   * Install dependencies for a skill
   * @param {string} skillPath - Skill directory
   */
  async installDependencies(skillPath) {
    const packageJsonPath = path.join(skillPath, 'package.json');

    if (await this.pathExists(packageJsonPath)) {
      console.log(`   Installing dependencies...`);
      try {
        await execAsync('npm install --production', { cwd: skillPath });
      } catch (err) {
        console.error(`   ‚ö†Ô∏è Dependency install failed: ${err.message}`);
      }
    }
  }

  /**
   * Backup a skill before update/remove
   * @param {string} name - Skill name
   * @param {Object} entry - Registry entry
   */
  async backupSkill(name, entry) {
    const backupName = `${name}_backup_${Date.now()}`;
    const backupPath = path.join(this.backupDir, backupName);

    await fs.mkdir(this.backupDir, { recursive: true });

    // Copy clone to backup
    await execAsync(`cp -r "${entry.path}" "${backupPath}"`);

    console.log(`   üíæ Backed up to: ${backupPath}`);

    return backupPath;
  }

  /**
   * Check if skill has updates
   * @param {Object} entry - Registry entry
   * @returns {Promise<boolean>}
   */
  async checkHasUpdate(entry) {
    try {
      const result = await execAsync('git fetch --dry-run', {
        cwd: entry.path
      });

      // Check if there are new commits
      const status = await execAsync('git status -uno', {
        cwd: entry.path
      });

      return status.stdout.includes('behind');
    } catch {
      return false;
    }
  }

  /**
   * Check if skill is already installed
   * @param {string} name - Skill name
   * @returns {Promise<string|null>} Path if exists
   */
  async checkExisting(name) {
    const skillPath = path.join(this.skillsDir, name);
    try {
      const stats = await fs.lstat(skillPath);
      if (stats.isSymbolicLink()) {
        return skillPath;
      }
    } catch {
      return null;
    }
  }

  /**
   * Resolve cached ID to URL
   * @param {string} cachedId - Cache ID
   * @returns {Promise<string|null>} URL
   */
  async resolveCachedId(cachedId) {
    try {
      const cacheFile = path.join(this.cacheDir, 'evaluations.json');
      const data = await fs.readFile(cacheFile, 'utf-8');
      const parsed = JSON.parse(data);
      const found = parsed.results.find(r => r.name === cachedId || r.url.includes(cachedId));
      return found?.url || null;
    } catch {
      return null;
    }
  }

  /**
   * Update registry with new installation
   * @param {Object} entry - Registry entry
   */
  async updateRegistry(entry) {
    const registry = await this.getRegistry();
    registry.installed[entry.name] = entry;
    registry.lastUpdate = new Date().toISOString();
    await this.saveRegistry(registry);
  }

  /**
   * Get registry
   * @returns {Promise<Object>} Registry data
   */
  async getRegistry() {
    try {
      const registryFile = path.join(this.cacheDir, 'registry.json');
      const data = await fs.readFile(registryFile, 'utf-8');
      return JSON.parse(data);
    } catch {
      return { installed: {}, lastUpdate: null };
    }
  }

  /**
   * Save registry
   * @param {Object} registry - Registry data
   */
  async saveRegistry(registry) {
    const registryFile = path.join(this.cacheDir, 'registry.json');
    await fs.mkdir(this.cacheDir, { recursive: true });
    await fs.writeFile(registryFile, JSON.stringify(registry, null, 2));
  }

  /**
   * Get installed skills
   * @returns {Promise<Array>} Installed skills
   */
  async getInstalledSkills() {
    const registry = await this.getRegistry();
    return Object.entries(registry.installed).map(([name, entry]) => ({
      name,
      ...entry
    }));
  }

  /**
   * Extract repo name from URL
   * @param {string} url - GitHub URL
   * @returns {string} Repo name
   */
  extractRepoName(url) {
    const match = url.match(/github\.com\/[^\/]+\/([^\/]+)/);
    return match ? match[1].replace(/\.git$/, '') : 'unknown';
  }

  /**
   * Extract full name from URL
   * @param {string} url - GitHub URL
   * @returns {string|null} owner/repo
   */
  extractFullName(url) {
    const match = url.match(/github\.com\/([^\/]+\/[^\/]+)/);
    return match ? match[1].replace(/\.git$/, '') : null;
  }

  /**
   * Check if path exists
   * @param {string} p - Path
   * @returns {Promise<boolean>}
   */
  async pathExists(p) {
    try {
      await fs.access(p);
      return true;
    } catch {
      return false;
    }
  }
}

module.exports = { Installer };

#!/usr/bin/env node
/**
 * Registry Module - Skill State Management
 * 
 * Tracks installed skills, their versions, and metadata
 * 
 * @module registry
 * @author PopeBot
 */

const fs = require('fs').promises;
const path = require('path');

class Registry {
  constructor(options = {}) {
    this.cacheDir = options.cacheDir || './.scout';
    this.registryPath = path.join(this.cacheDir, 'registry.json');
    this.skillsDir = options.skillsDir || '/job/.pi/skills';
  }

  /**
   * Get full registry
   * @returns {Promise<Object>} Registry data
   */
  async getRegistry() {
    try {
      const data = await fs.readFile(this.registryPath, 'utf-8');
      return JSON.parse(data);
    } catch {
      return {
        version: '1.0.0',
        installed: {},
        metadata: {},
        history: [],
        lastUpdate: null
      };
    }
  }

  /**
   * Save registry
   * @param {Object} registry - Registry data
   */
  async saveRegistry(registry) {
    await fs.mkdir(this.cacheDir, { recursive: true });
    await fs.writeFile(this.registryPath, JSON.stringify(registry, null, 2));
  }

  /**
   * Register a new skill
   * @param {Object} entry - Skill entry
   */
  async register(entry) {
    const registry = await this.getRegistry();

    const record = {
      ...entry,
      registered_at: new Date().toISOString()
    };

    registry.installed[entry.name] = record;
    registry.history.push({
      action: 'install',
      name: entry.name,
      timestamp: entry.registered_at,
      url: entry.url
    });

    registry.lastUpdate = new Date().toISOString();
    await this.saveRegistry(registry);

    return record;
  }

  /**
   * Unregister a skill
   * @param {string} name - Skill name
   * @param {string} reason - Removal reason
   */
  async unregister(name, reason = 'manual') {
    const registry = await this.getRegistry();

    if (registry.installed[name]) {
      registry.history.push({
        action: 'remove',
        name,
        timestamp: new Date().toISOString(),
        reason
      });

      delete registry.installed[name];
      registry.lastUpdate = new Date().toISOString();
      await this.saveRegistry(registry);
    }
  }

  /**
   * Update skill metadata
   * @param {string} name - Skill name
   * @param {Object} updates - Updates to apply
   */
  async update(name, updates) {
    const registry = await this.getRegistry();

    if (registry.installed[name]) {
      registry.installed[name] = {
        ...registry.installed[name],
        ...updates,
        updated_at: new Date().toISOString()
      };

      registry.history.push({
        action: 'update',
        name,
        timestamp: new Date().toISOString(),
        from: registry.installed[name].version,
        to: updates.version
      });

      registry.lastUpdate = new Date().toISOString();
      await this.saveRegistry(registry);
    }
  }

  /**
   * Get installed skills
   * @param {Object} filters - Optional filters
   * @returns {Promise<Array>} Installed skills
   */
  async getInstalledSkills(filters = {}) {
    const registry = await this.getRegistry();

    let skills = Object.entries(registry.installed).map(([name, data]) => ({
      name,
      ...data
    }));

    if (filters.source) {
      skills = skills.filter(s => s.source === filters.source);
    }

    if (filters.active) {
      skills = skills.filter(s => !s.disabled);
    }

    // Sort by installed date
    skills.sort((a, b) => {
      const aDate = new Date(a.installed_at || a.registered_at || 0);
      const bDate = new Date(b.installed_at || b.registered_at || 0);
      return bDate - aDate;
    });

    return skills;
  }

  /**
   * Get skill by name
   * @param {string} name - Skill name
   * @returns {Promise<Object|null>} Skill data
   */
  async getSkill(name) {
    const registry = await this.getRegistry();
    return registry.installed[name] || null;
  }

  /**
   * Check if skill is installed
   * @param {string} name - Skill name
   * @returns {Promise<boolean>}
   */
  async isInstalled(name) {
    const registry = await this.getRegistry();
    return !!registry.installed[name];
  }

  /**
   * Get installation history
   * @param {number} limit - Max results
   * @returns {Promise<Array>} History entries
   */
  async getHistory(limit = 50) {
    const registry = await this.getRegistry();
    return (registry.history || [])
      .slice(-limit)
      .reverse();
  }

  /**
   * Get registry statistics
   * @returns {Promise<Object>} Statistics
   */
  async getStats() {
    const registry = await this.getRegistry();
    const skills = Object.values(registry.installed);

    const bySource = skills.reduce((acc, skill) => {
      acc[skill.source || 'unknown'] = (acc[skill.source || 'unknown'] || 0) + 1;
      return acc;
    }, {});

    const byDate = {};
    for (const skill of skills) {
      const date = (skill.installed_at || skill.registered_at)?.split('T')[0];
      if (date) {
        byDate[date] = (byDate[date] || 0) + 1;
      }
    }

    return {
      total: skills.length,
      bySource,
      byDate,
      lastUpdate: registry.lastUpdate
    };
  }

  /**
   * Set skill enabled/disabled state
   * @param {string} name - Skill name
   * @param {boolean} enabled - Enabled state
   */
  async setEnabled(name, enabled) {
    const registry = await this.getRegistry();

    if (registry.installed[name]) {
      registry.installed[name].disabled = !enabled;
      registry.installed[name].updated_at = new Date().toISOString();
      await this.saveRegistry(registry);
    }
  }

  /**
   * Clean up broken entries
   * @returns {Promise<number>} Removed count
   */
  async cleanup() {
    const registry = await this.getRegistry();
    const before = Object.keys(registry.installed).length;

    for (const [name, entry] of Object.entries(registry.installed)) {
      const skillPath = path.join(this.skillsDir, name);
      try {
        await fs.access(skillPath);
      } catch {
        // Symlink broken, remove from registry
        delete registry.installed[name];
        console.log(`üßπ Cleaned up broken entry: ${name}`);
      }
    }

    await this.saveRegistry(registry);
    return before - Object.keys(registry.installed).length;
  }
}

module.exports = { Registry };

#!/usr/bin/env node
/**
 * Security Module - Skill Security Auditing
 * 
 * Scans skills for dangerous patterns and security issues
 * 
 * @module security
 * @author PopeBot
 */

const fs = require('fs').promises;
const path = require('path');

// Dangerous patterns to scan for
const DANGEROUS_PATTERNS = [
  {
    pattern: /eval\s*\(/,
    severity: 'critical',
    description: 'Uses eval() which can execute arbitrary code'
  },
  {
    pattern: /new\s+Function\s*\(/,
    severity: 'critical',
    description: 'Uses Function constructor which can execute arbitrary code'
  },
  {
    pattern: /child_process/,
    severity: 'warning',
    description: 'Uses child_process module'
  },
  {
    pattern: /spawn\s*\(/,
    severity: 'warning',
    description: 'Spawns child processes'
  },
  {
    pattern: /exec\s*\(/,
    severity: 'warning',
    description: 'Executes shell commands'
  },
  {
    pattern: /rm\s+-rf\s+\//,
    severity: 'critical',
    description: 'Dangerous delete pattern'
  },
  {
    pattern: /rm\s+-rf\s+\/\s*/,
    severity: 'critical',
    description: 'Dangerous recursive delete'
  },
  {
    pattern: /\|\s*bash/,
    severity: 'critical',
    description: 'Pipes to bash (remote code execution risk)'
  },
  {
    pattern: /\|\s*sh/,
    severity: 'critical',
    description: 'Pipes to shell (remote code execution risk)'
  },
  {
    pattern: /\|\s*exec/,
    severity: 'critical',
    description: 'Pipes to exec (command injection risk)'
  },
  {
    pattern: /download.*\|\s*(?:bash|sh)/,
    severity: 'critical',
    description: 'Download pipe to shell pattern'
  },
  {
    pattern: /curl.*-o-\s*\|/,
    severity: 'critical',
    description: 'curl pipe pattern'
  },
  {
    pattern: /wget.*-O-\s*\|/,
    severity: 'critical',
    description: 'wget pipe pattern'
  },
  {
    pattern: /setInterval\s*\(\s*eval/,
    severity: 'critical',
    description: 'Periodic eval execution'
  },
  {
    pattern: /require\s*\(\s*[^'"]*\+[^'"]*\)/,
    severity: 'warning',
    description: 'Dynamic require (code injection risk)'
  },
  {
    pattern: /import\s*\(\s*[^'"]*\+[^'"]*\)/,
    severity: 'warning',
    description: 'Dynamic import (code injection risk)'
  },
  {
    pattern: /fs\.[access|chmod|chown|rename].*\+/,
    severity: 'warning',
    description: 'Dynamic filesystem pattern'
  },
  {
    pattern: /process\.env/,
    severity: 'info',
    description: 'Accesses environment variables'
  },
  {
    pattern: /delete\s+require\./,
    severity: 'warning',
    description: 'Modifies require cache'
  },
  {
    pattern: /Object\.prototype/,
    severity: 'warning',
    description: 'Modifies Object prototype'
  },
  {
    pattern: /prototype\.pollute/,
    severity: 'critical',
    description: 'Prototype pollution pattern'
  },
  {
    pattern: /__proto__/,
    severity: 'warning',
    description: 'Accesses __proto__'
  },
  {
    pattern: /constructor\s*\[\s*"prototype"\s*\]/,
    severity: 'critical',
    description: 'Prototype access pattern'
  }
];

// Risky npm dependencies
const RISKY_PACKAGES = [
  'eval',
  'safe-eval',
  'vm2',
  'vm',
  'child_process',
  'cluster',
  'dgram',
  'net',
  'tls',
  'repl',
  'readline'
];

class Security {
  constructor(options = {}) {
    this.strictMode = options.strictMode || false;
    this.allowNetwork = options.allowNetwork || true;
    this.allowFilesystem = options.allowFilesystem || true;
  }

  /**
   * Run security audit
   * @param {Object} params - Audit parameters
   * @returns {Promise<Object>} Audit results
   */
  async audit(params = {}) {
    const { skillPath, url, name } = params;

    const results = {
      passed: true,
      issues: [],
      warnings: [],
      info: [],
      score: 1.0
    };

    if (skillPath) {
      // Audit local files
      const fileResults = await this.auditDirectory(skillPath);
      results.issues.push(...fileResults.issues);
      results.warnings.push(...fileResults.warnings);
      results.info.push(...fileResults.info);
    } else if (url) {
      // Audit remote repository
      // This would need additional implementation
      results.info.push('Remote audit not fully implemented');
    }

    // Calculate score
    results.score = this.calculateScore(results);
    results.passed = results.score >= 0.8;

    return results;
  }

  /**
   * Audit a directory
   * @param {string} dirPath - Directory to audit
   * @returns {Promise<Object>} Audit results
   */
  async auditDirectory(dirPath) {
    const results = {
      issues: [],
      warnings: [],
      info: [],
      filesScanned: 0
    };

    const files = await this.getJavaScriptFiles(dirPath);

    for (const file of files) {
      results.filesScanned++;
      try {
        const content = await fs.readFile(file, 'utf-8');
        const fileResults = this.auditFile(content, file);
        results.issues.push(...fileResults.issues);
        results.warnings.push(...fileResults.warnings);
        results.info.push(...fileResults.info);
      } catch (err) {
        results.warnings.push(`Could not read ${file}: ${err.message}`);
      }
    }

    // Audit package.json
    const packageResult = await this.auditPackageJson(dirPath);
    results.issues.push(...packageResult.issues);
    results.warnings.push(...packageResult.warnings);
    results.info.push(...packageResult.info);

    return results;
  }

  /**
   * Get all JavaScript files in directory
   * @param {string} dirPath - Directory path
   * @returns {Promise<Array>} File paths
   */
  async getJavaScriptFiles(dirPath) {
    const files = [];

    const entries = await fs.readdir(dirPath, { withFileTypes: true });

    for (const entry of entries) {
      const fullPath = path.join(dirPath, entry.name);

      if (entry.isDirectory() && entry.name !== 'node_modules' && entry.name !== '.git') {
        const subFiles = await this.getJavaScriptFiles(fullPath);
        files.push(...subFiles);
      } else if (entry.isFile() && 
                 (entry.name.endsWith('.js') || 
                  entry.name.endsWith('.mjs') || 
                  entry.name.endsWith('.cjs'))) {
        files.push(fullPath);
      }
    }

    return files;
  }

  /**
   * Audit a single file
   * @param {string} content - File content
   * @param {string} filepath - File path
   * @returns {Object} Audit results for file
   */
  auditFile(content, filepath) {
    const results = {
      issues: [],
      warnings: [],
      info: [],
      lines: content.split('\n').length
    };

    const lines = content.split('\n');
    const filename = path.basename(filepath);

    for (const pattern of DANGEROUS_PATTERNS) {
      // Check line by line for pattern matches
      lines.forEach((line, index) => {
        if (pattern.pattern.test(line)) {
          const message = `${filename}:${index + 1}: ${pattern.description}`;

          switch (pattern.severity) {
            case 'critical':
              results.issues.push(message);
              break;
            case 'warning':
              results.warnings.push(message);
              break;
            case 'info':
              results.info.push(message);
              break;
          }
        }
      });
    }

    return results;
  }

  /**
   * Audit package.json
   * @param {string} dirPath - Directory containing package.json
   * @returns {Promise<Object>} Audit results
   */
  async auditPackageJson(dirPath) {
    const results = {
      issues: [],
      warnings: [],
      info: [],
      hasPackageJson: false
    };

    try {
      const packagePath = path.join(dirPath, 'package.json');
      const content = await fs.readFile(packagePath, 'utf-8');
      const pkg = JSON.parse(content);
      results.hasPackageJson = true;

      // Check dependencies
      const deps = Object.keys(pkg.dependencies || {});
      const devDeps = Object.keys(pkg.devDependencies || {});

      for (const dep of [...deps, ...devDeps]) {
        if (RISKY_PACKAGES.includes(dep)) {
          results.warnings.push(`package.json: Potentially risky dependency: ${dep}`);
        }
      }

      // Check for scripts with dangerous patterns
      const scripts = Object.entries(pkg.scripts || {});
      for (const [name, script] of scripts) {
        for (const pattern of DANGEROUS_PATTERNS) {
          if (pattern.pattern.test(script)) {
            results.issues.push(`package.json script "${name}" contains dangerous pattern`);
          }
        }
      }

      // Check post-install scripts
      if (pkg.scripts?.postinstall) {
        results.warnings.push('package.json has postinstall script - review for security');
      }

    } catch {
      // No package.json
    }

    return results;
  }

  /**
   * Calculate security score
   * @param {Object} results - Audit results
   * @returns {number} Score between 0 and 1
   */
  calculateScore(results) {
    const criticalIssues = results.issues.filter(i => 
      DANGEROUS_PATTERNS.some(p => p.severity === 'critical' && i.includes(p.description))
    ).length;

    const warnings = results.warnings.length;

    // Start at 1.0
    let score = 1.0;

    // Deduct for critical issues
    score -= criticalIssues * 0.3;

    // Deduct for warnings
    score -= warnings * 0.1;

    // Don't go below 0
    return Math.max(0, score);
  }

  /**
   * Get security report summary
   * @param {Object} auditResults - Audit results
   * @returns {string} Summary
   */
  getSummary(auditResults) {
    const parts = [];

    if (auditResults.issues.length > 0) {
      parts.push(`${auditResults.issues.length} critical issues`);
    }
    if (auditResults.warnings.length > 0) {
      parts.push(`${auditResults.warnings.length} warnings`);
    }
    if (auditResults.info.length > 0) {
      parts.push(`${auditResults.info.length} info`);
    }

    return parts.join(', ') || 'Clean';
  }
}

module.exports = { Security };

#!/usr/bin/env node
/**
 * Skill Scout CLI - Main Entry Point
 */

const { Command } = require('commander');
const { SkillScout } = require('../index');
const chalk = require('chalk');
const { Scout } = require('../lib/scout');
const { Evaluator } = require('../lib/evaluator');
const { Installer } = require('../lib/installer');
const { Registry } = require('../lib/registry');

const program = new Command();

program
  .name('skill-scout')
  .description('Auto-discover, evaluate, and integrate Pi skills')
  .version('1.0.0');

// Discover command
program
  .command('discover [query]')
  .description('Search for skills on GitHub')
  .option('-l, --limit <number>', 'Maximum results', '20')
  .option('--json', 'Output as JSON')
  .action(async (query, options) => {
    try {
      const scout = new Scout();
      const results = await scout.discover({
        query,
        limit: parseInt(options.limit)
      });

      if (options.json) {
        console.log(JSON.stringify(results, null, 2));
      } else {
        console.log(chalk.green(`\n‚úÖ Found ${results.length} skills\n`));

        results.forEach((skill, i) => {
          console.log(chalk.cyan(`${i + 1}. ${skill.name}`));
          console.log(`   URL: ${chalk.blue(skill.url)}`);
          console.log(`   ‚≠ê ${skill.stars || 0} | ${skill.language || 'N/A'} | Updated: ${skill.updated_at?.split('T')[0] || 'N/A'}`);
          if (skill.description) {
            console.log(`   ${skill.description.substring(0, 80)}${skill.description.length > 80 ? '...' : ''}`);
          }
          console.log();
        });
      }
    } catch (err) {
      console.error(chalk.red(`‚ùå Error: ${err.message}`));
      process.exit(1);
    }
  });

// Evaluate command
program
  .command('evaluate <url>')
  .description('Evaluate a skill before installing')
  .option('-v, --verbose', 'Verbose output')
  .action(async (url, options) => {
    try {
      const evaluator = new Evaluator();
      const results = await evaluator.evaluate({ url });

      const result = results[0];
      const scorePct = (result.score * 100).toFixed(1);

      console.log(chalk.cyan(`\nüî¨ Evaluation: ${result.name}`));
      console.log(chalk.gray('‚îÄ'.repeat(50)));
      console.log(`Score: ${result.score >= 0.7 ? chalk.green(scorePct + '%') : chalk.yellow(scorePct + '%')}`);
      console.log(`Recommendation: ${chalk.cyan(result.recommendation.toUpperCase())}`);
      console.log();

      if (result.reasons?.length > 0) {
        console.log(chalk.bold('Reasons:'));
        result.reasons.forEach(reason => {
          const color = reason.includes('dangerous') || reason.includes('Missing') 
            ? chalk.yellow : chalk.gray;
          console.log(`  ‚Ä¢ ${color(reason)}`);
        });
        console.log();
      }

      if (options.verbose) {
        console.log(chalk.bold('Details:'));
        console.log(JSON.stringify(result.details, null, 2));
      }

      console.log();

    } catch (err) {
      console.error(chalk.red(`‚ùå Error: ${err.message}`));
      process.exit(1);
    }
  });

// Install command
program
  .command('install <name-or-url>')
  .description('Install a skill')
  .option('-v, --version <version>', 'Specific version')
  .action(async (source, options) => {
    try {
      const installer = new Installer();
      const isUrl = source.includes('github.com');

      const result = await installer.install({
        url: isUrl ? source : undefined,
        name: isUrl ? undefined : source,
        version: options.version
      });

      if (result.installed) {
        console.log(chalk.green(`\n‚úÖ Successfully installed: ${result.name}`));
        console.log(`   Path: ${result.symlink}`);
      } else {
        console.log(chalk.yellow(`\n‚ö†Ô∏è ${result.status}: ${result.name}`));
      }
    } catch (err) {
      console.error(chalk.red(`‚ùå Error: ${err.message}`));
      process.exit(1);
    }
  });

// Update command
program
  .command('update [name]')
  .description('Update installed skills')
  .option('-a, --all', 'Update all skills')
  .option('-c, --check', 'Check for updates only')
  .action(async (name, options) => {
    try {
      const installer = new Installer();

      const params = {};
      if (options.all) params.all = true;
      if (options.check) params.check = true;
      if (name && !options.all) params.name = name;

      const results = await installer.update(params);

      if (options.check) {
        if (results.length === 0) {
          console.log(chalk.green('\n‚úÖ All skills are up to date'));
        } else {
          console.log(chalk.yellow(`\nüì¶ ${results.length} update(s) available:`));
          results.forEach(r => console.log(`  ‚Ä¢ ${r.name}`));
        }
      } else {
        console.log(chalk.green(`\n‚úÖ Updated ${results.filter(r => r.updated).length} skill(s)`));
      }
    } catch (err) {
      console.error(chalk.red(`‚ùå Error: ${err.message}`));
      process.exit(1);
    }
  });

// List command
program
  .command('list')
  .description('List installed or discovered skills')
  .option('-i, --installed', 'List installed skills')
  .option('-d, --discovered', 'List discovered skills')
  .option('-s, --with-scores', 'Show evaluation scores')
  .action(async (options) => {
    try {
      const scout = new SkillScout();

      if (options.discovered || (!options.installed && !options.discovered)) {
        // List discovered
        const results = await scout.list({ type: 'discovered' });
        console.log(chalk.cyan(`\nüìã Discovered Skills (${results.length})`));
        console.log(chalk.gray('‚îÄ'.repeat(50)));

        results.slice(0, 20).forEach((skill, i) => {
          console.log(`${i + 1}. ${chalk.bold(skill.name)}`);
          console.log(`   ${chalk.blue(skill.url)}`);
          console.log(`   ‚≠ê ${skill.stars || 0} | ${chalk.gray(skill.description?.substring(0, 60) || 'No description')}`);
        });
      }

      if (options.installed) {
        // List installed
        const results = await scout.list({ type: 'installed' });
        console.log(chalk.cyan(`\nüì¶ Installed Skills (${results.length})`));
        console.log(chalk.gray('‚îÄ'.repeat(50)));

        results.forEach((skill, i) => {
          const status = skill.disabled ? chalk.yellow('[disabled]') : chalk.green('[active]');
          console.log(`${i + 1}. ${chalk.bold(skill.name)} ${status}`);
          console.log(`   ${chalk.gray(skill.symlink)}`);
        });
      }

      console.log();
    } catch (err) {
      console.error(chalk.red(`‚ùå Error: ${err.message}`));
      process.exit(1);
    }
  });

// Remove command
program
  .command('remove <name>')
  .description('Remove an installed skill')
  .option('--no-backup', 'Remove without backup')
  .action(async (name, options) => {
    try {
      const installer = new Installer();
      const result = await installer.remove({ name, backup: options.backup });

      if (result.status === 'removed') {
        console.log(chalk.green(`\n‚úÖ Successfully removed: ${result.name}`));
        if (result.backedUp) {
          console.log(chalk.gray('   (Backed up before removal)'));
        }
      }
    } catch (err) {
      console.error(chalk.red(`‚ùå Error: ${err.message}`));
      process.exit(1);
    }
  });

// Status command
program
  .command('status')
  .description('Show current status')
  .action(async () => {
    try {
      const scout = new SkillScout();
      const status = await scout.status({ detailed: true });

      console.log(chalk.cyan('\nüìä Skill Scout Status'));
      console.log(chalk.gray('‚îÄ'.repeat(50)));
      console.log(`Installed skills: ${chalk.bold(status.installed)}`);
      console.log(`Cached discoveries: ${chalk.bold(status.discovered)}`);
      console.log(`Sources: ${status.sources.join(', ')}`);
      console.log(`Auto-install: ${status.autoInstall ? chalk.green('enabled') : chalk.gray('disabled')}`);
      console.log(`Min score: ${status.minScore}`);
      console.log(`Last update: ${status.lastUpdate ? chalk.gray(status.lastUpdate) : chalk.yellow('Never')}`);
      console.log();
    } catch (err) {
      console.error(chalk.red(`‚ùå Error: ${err.message}`));
      process.exit(1);
    }
  });

// Pipeline command
program
  .command('pipeline')
  .description('Run full discovery pipeline')
  .action(async () => {
    try {
      const scout = new SkillScout();

      console.log(chalk.cyan('\nüöÄ Starting Skill Scout Pipeline...'));
      console.log(chalk.gray('‚îÄ'.repeat(50)) + '\n');

      const results = await scout.runPipeline();

      console.log(chalk.green('‚úÖ Pipeline Complete!'));
      console.log(chalk.gray('‚îÄ'.repeat(50)));
      console.log(`Discovered: ${chalk.bold(results.discovered)}`);
      console.log(`Evaluated: ${chalk.bold(results.evaluated)}`);
      console.log(`Installed: ${chalk.bold(results.installed)}`);
      console.log();
    } catch (err) {
      console.error(chalk.red(`‚ùå Error: ${err.message}`));
      process.exit(1);
    }
  });

// Default action (no command)
if (process.argv.length === 2) {
  console.log(chalk.cyan(`
‚ïî‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïê‚ïó
‚ïë     Skill Scout - Pi Skill Discovery   ‚ïë
