/**
 * Canvas A2UI Skill - Main Entry Point
 * 
 * Exports all canvas functionality for integration with PopeBot
 */

import { CanvasManager } from './lib/canvas.js';
import { DiagramGenerator } from './lib/diagram-templates.js';
import { ChartRenderer } from './lib/chart-renderer.js';

const manager = new CanvasManager();

/**
 * Create a new canvas instance
 */
export async function canvas_create(options) {
  return await manager.create(options);
}

/**
 * Draw elements on canvas
 */
export async function canvas_draw(options) {
  return await manager.draw(options.canvasId, options.commands);
}

/**
 * Render a chart
 */
export async function canvas_chart(options) {
  const chartRenderer = new ChartRenderer(manager);
  return await chartRenderer.render(options);
}

/**
 * Add text to canvas
 */
export async function canvas_text(options) {
  return await manager.addText(options);
}

/**
 * Add image to canvas
 */
export async function canvas_image(options) {
  return await manager.addImage(options);
}

/**
 * Take screenshot of canvas
 */
export async function canvas_screenshot(options) {
  return await manager.screenshot(options.canvasId, options.outputPath, options);
}

/**
 * Create grid layout
 */
export async function canvas_grid(options) {
  return await manager.createGrid(options);
}

/**
 * Create flowchart
 */
export async function canvas_flowchart(options) {
  const diagram = new DiagramGenerator(manager);
  return await diagram.createFlowchart(options);
}

/**
 * Create system diagram
 */
export async function canvas_diagram(options) {
  const diagram = new DiagramGenerator(manager);
  return await diagram.createSystemDiagram(options);
}

/**
 * Query canvas state
 */
export async function canvas_query(options) {
  return await manager.query(options.canvasId, options.query);
}

/**
 * Execute JavaScript on canvas
 */
export async function canvas_eval(options) {
  return await manager.evaluate(options.canvasId, options.code);
}

/**
 * Reset canvas
 */
export async function canvas_reset(options) {
  return await manager.reset(options.canvasId, options.backgroundColor);
}

/**
 * List all canvases
 */
export async function canvas_list(options = {}) {
  return await manager.list();
}

/**
 * Close canvas
 */
export async function canvas_close(options) {
  return await manager.close(options.canvasId, options.saveScreenshot);
}

/**
 * Start canvas server (for real-time viewing)
 */
export async function canvas_server_start(options) {
  const { startServer } = await import('./server.js');
  return await startServer(manager, options);
}

// Default exports
export default {
  canvas_create,
  canvas_draw,
  canvas_chart,
  canvas_text,
  canvas_image,
  canvas_screenshot,
  canvas_grid,
  canvas_flowchart,
  canvas_diagram,
  canvas_query,
  canvas_eval,
  canvas_reset,
  canvas_list,
  canvas_close,
  canvas_server_start
};

/**
 * Canvas A2UI - Core Canvas Manager
 * Manages headless browser instances and canvas operations
 */

import puppeteer from 'puppeteer';
import { promises as fs } from 'fs';
import { fileURLToPath } from 'url';
import { dirname, join } from 'path';

const __dirname = dirname(fileURLToPath(import.meta.url));

/**
 * Canvas class representing a single canvas instance
 */
class CanvasInstance {
  constructor(id, page, width, height, backgroundColor = '#ffffff') {
    this.id = id;
    this.page = page;
    this.width = width;
    this.height = height;
    this.backgroundColor = backgroundColor;
    this.createdAt = new Date().toISOString();
    this.elements = [];
  }

  async initialize() {
    await this.page.setViewport({ width: this.width, height: this.height });
    await this.page.setContent(this.getHTML());
    await this.waitForCanvas();
  }

  getHTML() {
    return `
      <!DOCTYPE html>
      <html>
      <head>
        <style>
          body { margin: 0; padding: 0; overflow: hidden; }
          canvas { display: block; }
        </style>
      </head>
      <body>
        <canvas id="main-canvas" width="${this.width}" height="${this.height}"></canvas>
        <script>
          window.canvasReady = true;
          window.canvas = document.getElementById('main-canvas');
          window.ctx = window.canvas.getContext('2d');
          window.ctx.fillStyle = '${this.backgroundColor}';
          window.ctx.fillRect(0, 0, ${this.width}, ${this.height});
        </script>
      </body>
      </html>
    `;
  }

  async waitForCanvas() {
    await this.page.waitForFunction(() => window.canvasReady === true, { timeout: 5000 });
  }

  async execute(commands) {
    return await this.page.evaluate((cmds) => {
      const ctx = window.ctx;
      const canvas = window.canvas;
      const results = [];

      for (const cmd of cmds) {
        try {
          // Save context state
          ctx.save();

          // Apply styles
          if (cmd.fill) ctx.fillStyle = cmd.fill;
          if (cmd.stroke) ctx.strokeStyle = cmd.stroke;
          if (cmd.strokeWidth) ctx.lineWidth = cmd.strokeWidth;
          if (cmd.opacity) ctx.globalAlpha = cmd.opacity;
          if (cmd.shadow) {
            ctx.shadowColor = cmd.shadow.color || '#000';
            ctx.shadowBlur = cmd.shadow.blur || 0;
            ctx.shadowOffsetX = cmd.shadow.offsetX || 0;
            ctx.shadowOffsetY = cmd.shadow.offsetY || 0;
          }

          // Font settings
          if (cmd.font) ctx.font = cmd.font;
          if (cmd.align) ctx.textAlign = cmd.align;
          if (cmd.baseline) ctx.textBaseline = cmd.baseline;

          // Execute command
          switch (cmd.type) {
            case 'rect':
              if (cmd.fill) {
                ctx.fillRect(cmd.x, cmd.y, cmd.width, cmd.height);
              }
              if (cmd.stroke) {
                ctx.strokeRect(cmd.x, cmd.y, cmd.width, cmd.height);
              }
              break;

            case 'circle':
              ctx.beginPath();
              ctx.arc(cmd.x, cmd.y, cmd.radius, 0, Math.PI * 2);
              if (cmd.fill) ctx.fill();
              if (cmd.stroke) ctx.stroke();
              break;

            case 'line':
              ctx.beginPath();
              ctx.moveTo(cmd.x1, cmd.y1);
              ctx.lineTo(cmd.x2, cmd.y2);
              if (cmd.stroke) ctx.stroke();
              break;

            case 'arrow':
              // Draw line
              ctx.beginPath();
              ctx.moveTo(cmd.fromX, cmd.fromY);
              ctx.lineTo(cmd.toX, cmd.toY);
              if (cmd.stroke) ctx.stroke();

              // Draw arrowhead
              const angle = Math.atan2(cmd.toY - cmd.fromY, cmd.toX - cmd.fromX);
              const headLen = cmd.headLength || 10;
              ctx.beginPath();
              ctx.moveTo(cmd.toX, cmd.toY);
              ctx.lineTo(cmd.toX - headLen * Math.cos(angle - Math.PI / 6), cmd.toY - headLen * Math.sin(angle - Math.PI / 6));
              ctx.lineTo(cmd.toX - headLen * Math.cos(angle + Math.PI / 6), cmd.toY - headLen * Math.sin(angle + Math.PI / 6));
              ctx.closePath();
              if (cmd.fill) ctx.fill();
              if (cmd.stroke) ctx.stroke();
              break;

            case 'text':
              if (cmd.fill) ctx.fillText(cmd.text, cmd.x, cmd.y, cmd.maxWidth);
              if (cmd.stroke) ctx.strokeText(cmd.text, cmd.x, cmd.y, cmd.maxWidth);
              break;

            case 'polygon':
              ctx.beginPath();
              ctx.moveTo(cmd.points[0].x, cmd.points[0].y);
              for (let i = 1; i < cmd.points.length; i++) {
                ctx.lineTo(cmd.points[i].x, cmd.points[i].y);
              }
              ctx.closePath();
              if (cmd.fill) ctx.fill();
              if (cmd.stroke) ctx.stroke();
              break;

            case 'image':
              // Images are handled via canvas_draw with image data
              if (cmd.imgData) {
                const img = new Image();
                img.onload = () => {
                  if (cmd.width && cmd.height) {
                    ctx.drawImage(img, cmd.x, cmd.y, cmd.width, cmd.height);
                  } else {
                    ctx.drawImage(img, cmd.x, cmd.y);
                  }
                };
                img.src = cmd.imgData;
              }
              break;

            case 'path':
              ctx.beginPath();
              const path = new Path2D(cmd.d);
              if (cmd.fill) ctx.fill(path);
              if (cmd.stroke) ctx.stroke(path);
              break;

            default:
              results.push({ ok: false, error: `Unknown command type: ${cmd.type}` });
              continue;
          }

          // Restore context
          ctx.restore();
          results.push({ ok: true });

        } catch (err) {
          results.push({ ok: false, error: err.message });
        }
      }

      return results;
    }, commands);
  }

  async screenshot(format = 'png', quality = 0.95) {
    const screenshotBuffer = await this.page.screenshot({
      type: format,
      quality: format === 'png' ? undefined : quality,
      encoding: 'binary',
      clip: { x: 0, y: 0, width: this.width, height: this.height }
    });
    return screenshotBuffer;
  }

  async evaluate(code) {
    return await this.page.evaluate((codeStr) => {
      try {
        // eslint-disable-next-line no-eval
        const result = eval(codeStr);
        return { success: true, result: String(result) };
      } catch (err) {
        return { success: false, error: err.message };
      }
    }, code);
  }

  async reset(backgroundColor) {
    if (backgroundColor) {
      this.backgroundColor = backgroundColor;
    }
    await this.page.evaluate((bg) => {
      const ctx = window.ctx;
      const canvas = window.canvas;
      ctx.fillStyle = bg;
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    }, this.backgroundColor);
    this.elements = [];
  }

  async getInfo() {
    return {
      id: this.id,
      width: this.width,
      height: this.height,
      backgroundColor: this.backgroundColor,
      createdAt: this.createdAt,
      elementCount: this.elements.length
    };
  }
}

/**
 * Canvas Manager - Manages all canvas instances
 */
export class CanvasManager {
  constructor() {
    this.canvases = new Map();
    this.browser = null;
    this.browserPromise = null;
  }

  async getBrowser() {
    if (this.browser) {
      return this.browser;
    }

    if (this.browserPromise) {
      return await this.browserPromise;
    }

    this.browserPromise = puppeteer.launch({
      headless: 'new',
      args: [
        '--no-sandbox',
        '--disable-setuid-sandbox',
        '--disable-dev-shm-usage',
        '--disable-accelerated-2d-canvas',
        '--disable-gpu'
      ]
    });

    this.browser = await this.browserPromise;
    return this.browser;
  }

  async create(options = {}) {
    const browser = await this.getBrowser();
    const page = await browser.newPage();

    const canvas = new CanvasInstance(
      options.name || `canvas-${Date.now()}`,
      page,
      options.width || 800,
      options.height || 600,
      options.backgroundColor || '#ffffff'
    );

    await canvas.initialize();
    this.canvases.set(canvas.id, canvas);

    return {
      id: canvas.id,
      width: canvas.width,
      height: canvas.height,
      message: `Canvas created: ${canvas.width}x${canvas.height}`
    };
  }

  async draw(canvasId, commands) {
    const canvas = this.canvases.get(canvasId);
    if (!canvas) {
      throw new Error(`Canvas not found: ${canvasId}`);
    }

    const results = await canvas.execute(commands);
    canvas.elements.push(...commands);

    return {
      executed: commands.length,
      results,
      canvasId
    };
  }

  async addText(options) {
    const { canvasId, x, y, text, fontSize = 14, font = 'Arial', fill = '#000000', align = 'left', maxWidth } = options;

    const lines = text.split('\n');
    const commands = lines.map((line, index) => ({
      type: 'text',
      x,
      y: y + (index * fontSize * 1.2),
      text: line,
      font: `${fontSize}px ${font}`,
      fill,
      align,
      maxWidth
    }));

    return await this.draw(canvasId, commands);
  }

  async addImage(options) {
    const { canvasId, src, x, y, width, height, opacity = 1.0 } = options;

    // Read image as base64
    const imageData = await fs.readFile(src, { encoding: 'base64' });
    const ext = src.split('.').pop().toLowerCase();
    const mimeType = ext === 'png' ? 'image/png' : ext === 'jpg' || ext === 'jpeg' ? 'image/jpeg' : 'image/png';
    const dataUrl = `data:${mimeType};base64,${imageData}`;

    const command = {
      type: 'image',
      x, y, width, height, opacity,
      imgData: dataUrl
    };

    return await this.draw(canvasId, [command]);
  }

  async screenshot(canvasId, outputPath, options = {}) {
    const canvas = this.canvases.get(canvasId);
    if (!canvas) {
      throw new Error(`Canvas not found: ${canvasId}`);
    }

    const buffer = await canvas.screenshot(options.format || 'png', options.quality);

    if (outputPath) {
      await fs.writeFile(outputPath, buffer);
      return { saved: true, path: outputPath, size: buffer.length };
    }

    return { buffer, size: buffer.length };
  }

  async createGrid(options) {
    const { canvasId, rows, cols, cellWidth, cellHeight, gap = 0, items = [] } = options;

    const commands = [];

    for (const item of items) {
      if (item.row >= rows || item.col >= cols) continue;

      const x = item.col * (cellWidth + gap);
      const y = item.row * (cellHeight + gap);

      // Cell background
      commands.push({
        type: 'rect',
        x,
        y,
        width: cellWidth,
        height: cellHeight,
        fill: item.color || '#f0f0f0',
        stroke: '#ccc',
        strokeWidth: 1
      });

      // Title text
      commands.push({
        type: 'text',
        x: x + cellWidth / 2,
        y: y + cellHeight / 2,
        text: item.title || '',
        font: 'bold 14px Arial',
        fill: '#333',
        align: 'center'
      });
    }

    return await this.draw(canvasId, commands);
  }

  async query(canvasId, queryType) {
    const canvas = this.canvases.get(canvasId);
    if (!canvas) {
      throw new Error(`Canvas not found: ${canvasId}`);
    }

    switch (queryType) {
      case 'bounds':
      case 'dimensions':
        return { width: canvas.width, height: canvas.height };
      case 'info':
        return await canvas.getInfo();
      default:
        return await canvas.getInfo();
    }
  }

  async evaluate(canvasId, code) {
    const canvas = this.canvases.get(canvasId);
    if (!canvas) {
      throw new Error(`Canvas not found: ${canvasId}`);
    }
    return await canvas.evaluate(code);
  }

  async reset(canvasId, backgroundColor) {
    const canvas = this.canvases.get(canvasId);
    if (!canvas) {
      throw new Error(`Canvas not found: ${canvasId}`);
    }
    await canvas.reset(backgroundColor);
    return { reset: true, canvasId };
  }

  async list() {
    const canvasList = [];
    for (const [id, canvas] of this.canvases) {
      canvasList.push(await canvas.getInfo());
    }
    return canvasList;
  }

  async close(canvasId, saveScreenshot) {
    const canvas = this.canvases.get(canvasId);
    if (!canvas) {
      throw new Error(`Canvas not found: ${canvasId}`);
    }

    let screenshotPath = null;
    if (saveScreenshot) {
      await this.screenshot(canvasId, saveScreenshot);
      screenshotPath = saveScreenshot;
    }

    await canvas.page.close();
    this.canvases.delete(canvasId);

    return { closed: true, canvasId, screenshotPath };
  }

  async closeAll() {
    for (const [id] of this.canvases) {
      await this.close(id);
    }
    if (this.browser) {
      await this.browser.close();
      this.browser = null;
    }
  }
}

// Handle cleanup on process exit
process.on('exit', () => {
  // Cleanup will happen automatically
});

process.on('SIGINT', async () => {
  process.exit(0);
});

/**
 * Canvas A2UI - Diagram Templates
 * Pre-built diagram generators for flowcharts, system diagrams, etc.
 */

import { promises as fs } from 'fs';

/**
 * Diagram Generator for creating common diagram types
 */
export class DiagramGenerator {
  constructor(manager) {
    this.manager = manager;
    this.colors = {
      primary: '#4A90D9',
      success: '#5CB85C',
      warning: '#F0AD4E',
      danger: '#D9534F',
      info: '#5BC0DE',
      neutral: '#777777',
      light: '#F5F5F5',
      dark: '#333333',
      white: '#FFFFFF',
      // Tier colors
      edge: '#4A90D9',
      api: '#5BC0DE',
      app: '#5CB85C',
      data: '#F0AD4E',
      cache: '#D9534F'
    };
  }

  /**
   * Create a flowchart
   */
  async createFlowchart(options) {
    const { canvasId, nodes, edges, nodeSize = { width: 120, height: 60 } } = options;

    const commands = [];

    // Draw nodes
    for (const node of nodes) {
      const nodeCommands = this.getNodeCommands(node, nodeSize);
      commands.push(...nodeCommands);
    }

    // Draw edges
    for (const edge of edges) {
      const fromNode = nodes.find(n => n.id === edge.from);
      const toNode = nodes.find(n => n.id === edge.to);
      if (fromNode && toNode) {
        const edgeCommands = this.getEdgeCommands(fromNode, toNode, edge, nodeSize);
        commands.push(...edgeCommands);
      }
    }

    return await this.manager.draw(canvasId, commands);
  }

  /**
   * Generate drawing commands for a node
   */
  getNodeCommands(node, size) {
    const commands = [];
    const { x, y, label, type = 'process' } = node;

    const width = node.width || size.width;
    const height = node.height || size.height;

    switch (type) {
      case 'terminator':
        // Rounded rectangle (pill shape)
        commands.push({
          type: 'rect',
          x: x - width / 2,
          y: y - height / 2,
          width,
          height,
          fill: this.colors.primary,
          stroke: this.colors.dark,
          strokeWidth: 2,
          radius: height / 2
        });
        break;

      case 'decision':
        // Diamond shape
        commands.push({
          type: 'polygon',
          points: [
            { x, y: y - height / 2 },
            { x: x + width / 2, y },
            { x, y: y + height / 2 },
            { x: x - width / 2, y }
          ],
          fill: this.colors.warning,
          stroke: this.colors.dark,
          strokeWidth: 2
        });
        break;

      case 'process':
      default:
        // Rectangle
        commands.push({
          type: 'rect',
          x: x - width / 2,
          y: y - height / 2,
          width,
          height,
          fill: this.colors.light,
          stroke: this.colors.dark,
          strokeWidth: 2
        });
        break;
    }

    // Add label
    commands.push({
      type: 'text',
      x,
      y,
      text: label,
      font: '12px Arial',
      fill: type === 'process' ? this.colors.dark : this.colors.white,
      align: 'center'
    });

    return commands;
  }

  /**
   * Generate drawing commands for an edge/connection
   */
  getEdgeCommands(fromNode, toNode, edge, size) {
    const commands = [];
    const { label, style = 'solid' } = edge;

    // Calculate edge points
    const fromX = fromNode.x;
    const fromY = fromNode.y + (fromNode.height || size.height) / 2;
    const toX = toNode.x;
    const toY = toNode.y - (toNode.height || size.height) / 2;

    // Draw line
    commands.push({
      type: 'line',
      x1: fromX,
      y1: fromY,
      x2: toX,
      y2: toY,
      stroke: this.colors.dark,
      strokeWidth: style === 'dashed' ? 1 : 2,
      dash: style === 'dashed' ? [5, 5] : undefined
    });

    // Draw arrow
    commands.push({
      type: 'arrow',
      fromX,
      fromY,
      toX,
      toY,
      stroke: this.colors.dark,
      fill: this.colors.dark
    });

    // Draw label if present
    if (label) {
      commands.push({
        type: 'text',
        x: (fromX + toX) / 2 + 10,
        y: (fromY + toY) / 2 - 5,
        text: label,
        font: '10px Arial',
        fill: this.colors.neutral
      });
    }

    return commands;
  }

  /**
   * Create a system architecture diagram
   */
  async createSystemDiagram(options) {
    const { canvasId, type, title, components, connections } = options;

    const commands = [];

    // Title
    commands.push({
      type: 'text',
      x: 50,
      y: 30,
      text: title,
      font: 'bold 18px Arial',
      fill: this.colors.dark
    });

    // Group components by tier
    const tiers = {};
    for (const comp of components) {
      const tier = comp.tier || 'default';
      if (!tiers[tier]) tiers[tier] = [];
      tiers[tier].push(comp);
    }

    const tierOrder = ['edge', 'api', 'app', 'service', 'data', 'cache', 'default'];
    let currentY = 80;
    const tierHeight = 100;
    const startX = 100;
    const spacing = 200;

    // Position components
    const componentPositions = {};

    for (const tier of tierOrder) {
      if (!tiers[tier]) continue;

      const tierComponents = tiers[tier];
      let currentX = startX;

      for (const comp of tierComponents) {
        componentPositions[comp.name] = { x: currentX, y: currentY };

        // Draw component box
        commands.push({
          type: 'rect',
          x: currentX - 60,
          y: currentY - 30,
          width: 120,
          height: 60,
          fill: this.colors[tier] || this.colors.neutral,
          stroke: this.colors.dark,
          strokeWidth: 2
        });

        // Component name
        commands.push({
          type: 'text',
          x: currentX,
          y: currentY,
          text: comp.name,
          font: 'bold 12px Arial',
          fill: this.colors.white,
          align: 'center'
        });

        // Component type
        commands.push({
          type: 'text',
          x: currentX,
          y: currentY + 15,
          text: comp.type,
          font: '10px Arial',
          fill: 'rgba(255,255,255,0.8)',
          align: 'center'
        });

        currentX += spacing;
      }

      // Draw tier label
      commands.push({
        type: 'text',
        x: 30,
        y: currentY,
        text: tier.toUpperCase(),
        font: '10px Arial',
        fill: this.colors.neutral,
        align: 'right'
      });

      currentY += tierHeight;
    }

    // Draw connections
    for (const conn of connections) {
      const from = componentPositions[conn.from];
      const to = componentPositions[conn.to];

      if (from && to) {
        commands.push({
          type: 'arrow',
          fromX: from.x + 60,
          fromY: from.y,
          toX: to.x - 60,
          toY: to.y,
          stroke: this.colors.neutral,
          fill: this.colors.neutral
        });
      }
    }

    return await this.manager.draw(canvasId, commands);
  }

  /**
   * Create UML class diagram
   */
  async createClassDiagram(canvasId, classes) {
    const commands = [];
    let x = 100;
    let y = 100;

    for (const cls of classes) {
      const width = 200;
      const lineHeight = 20;
      const headerHeight = 30;
      const totalHeight = headerHeight + (cls.fields?.length || 0) * lineHeight + (cls.methods?.length || 0) * lineHeight + 10;

      // Class box
      commands.push({
        type: 'rect',
        x,
        y,
        width,
        height: totalHeight,
        fill: this.colors.light,
        stroke: this.colors.dark,
        strokeWidth: 2
      });

      // Header
      commands.push({
        type: 'rect',
        x,
        y,
        width,
        height: headerHeight,
        fill: this.colors.primary,
        stroke: this.colors.dark,
        strokeWidth: 1
      });

      // Class name
      commands.push({
        type: 'text',
        x: x + width / 2,
        y: y + 20,
        text: cls.name,
        font: 'bold 14px Arial',
        fill: this.colors.white,
        align: 'center'
      });

      // Fields
      let fieldY = y + headerHeight + lineHeight;
      for (const field of cls.fields || []) {
        commands.push({
          type: 'text',
          x: x + 10,
          y: fieldY,
          text: `â€¢ ${field}`,
          font: '12px Arial',
          fill: this.colors.dark,
          align: 'left'
        });
        fieldY += lineHeight;
      }

      // Separator
      if (cls.methods?.length > 0) {
        commands.push({
          type: 'line',
          x1: x,
          y1: fieldY,
          x2: x + width,
          y2: fieldY,
          stroke: this.colors.neutral,
          strokeWidth: 1
        });
        fieldY += 5;

        // Methods
        for (const method of cls.methods) {
          commands.push({
            type: 'text',
            x: x + 10,
            y: fieldY + lineHeight,
            text: `âž¤ ${method}`,
            font: '12px Arial',
            fill: this.colors.dark,
            align: 'left'
          });
          fieldY += lineHeight;
        }
      }

      x += 250;
      if (x > 800) {
        x = 100;
        y += 250;
      }
    }

    return await this.manager.draw(canvasId, commands);
  }

  /**
   * Create sequence diagram
   */
  async createSequenceDiagram(canvasId, actors, sequences) {
    const commands = [];
    const actorWidth = 100;
    const actorHeight = 40;
    const startY = 80;
    const lineHeight = 60;

    // Draw actors
    let x = 100;
    const actorPositions = {};

    for (const actor of actors) {
      actorPositions[actor] = x;

      // Actor box
      commands.push({
        type: 'rect',
        x: x - actorWidth / 2,
        y: 40,
        width: actorWidth,
        height: actorHeight,
        fill: this.colors.primary,
        stroke: this.colors.dark,
        strokeWidth: 2
      });

      // Actor name
      commands.push({
        type: 'text',
        x,
        y: 65,
        text: actor,
        font: 'bold 12px Arial',
        fill: this.colors.white,
        align: 'center'
      });

      // Lifeline
      commands.push({
        type: 'line',
        x1: x,
        y1: startY,
        x2: x,
        y2: startY + sequences.length * lineHeight + 50,
        stroke: this.colors.neutral,
        strokeWidth: 1,
        dash: [5, 5]
      });

      x += 150;
    }

    // Draw sequences
    let currentY = startY + 20;

    for (const seq of sequences) {
      const fromX = actorPositions[seq.from];
      const toX = actorPositions[seq.to];

      // Message line
      commands.push({
        type: 'arrow',
        fromX: fromX,
        fromY: currentY,
        toX: toX - (fromX < toX ? 50 : -50),
        toY: currentY,
        stroke: this.colors.dark,
        fill: this.colors.dark
      });

      // Message label
      commands.push({
        type: 'text',
        x: (fromX + toX) / 2,
        y: currentY - 5,
        text: seq.message,
        font: '11px Arial',
        fill: this.colors.dark,
        align: 'center'
      });

      currentY += lineHeight;
    }

    return await this.manager.draw(canvasId, commands);
  }
}

