/**
 * Robot Personality Skill
 * Loads SOUL.md style personality files with safety constraints
 */

import { loadPersonality, parsePersonality, getCurrentPersonality } from './lib/personality.js';
import { SafetyEngine } from './lib/safety.js';
import { MemoryStore } from './lib/memory.js';
import { StateMachine } from './lib/state.js';
import fs from 'fs/promises';
import path from 'path';

class RobotPersonalitySkill {
  constructor() {
    this.safety = new SafetyEngine();
    this.memory = new MemoryStore();
    this.state = new StateMachine();
    this.currentPersonality = null;
    this.personalitiesDir = './personalities';
  }

  /**
   * Load a personality file
   */
  async loadPersonality({ name, strictness = 'normal', persist = true }) {
    try {
      const personalityPath = path.resolve(this.personalitiesDir, `${name}.md`);
      const content = await fs.readFile(personalityPath, 'utf-8');

      const personality = parsePersonality(content);
      this.currentPersonality = {
        ...personality,
        name,
        strictness,
        loadedAt: new Date().toISOString()
      };

      // Initialize safety rules with strictness level
      this.safety.loadRules(personality.safetyRules, strictness);

      // Set initial state
      await this.state.transition('idle', `Loaded personality: ${name}`);

      if (persist) {
        await this.memory.store('last_personality', {
          name,
          strictness,
          loadedAt: this.currentPersonality.loadedAt
        });
      }

      return {
        success: true,
        personality: this.currentPersonality,
        safetyRules: this.safety.getActiveRules()
      };
    } catch (error) {
      return {
        success: false,
        error: error.message
      };
    }
  }

  /**
   * Get current personality info
   */
  getCurrentPersonality() {
    return {
      active: this.currentPersonality,
      state: this.state.current,
      safetyStats: this.safety.getStats()
    };
  }

  /**
   * Check if an action is safe
   */
  async safetyCheck({ action, target, context = {}, confirmed = false }) {
    const result = await this.safety.evaluate({ action, target, context, confirmed });

    if (!result.approved && result.severity === 'CRITICAL') {
      await this.state.transition('concerned', `Critical safety violation: ${result.reason}`);
    }

    return result;
  }

  /**
   * Get behavior guidance for a situation
   */
  async getBehavior({ situation, context = {}, severity = 'normal' }) {
    if (!this.currentPersonality) {
      return { tone: 'neutral', approach: 'standard', rules: [] };
    }

    const behaviors = this.currentPersonality.behaviors || {};
    const emergency = this.currentPersonality.emergencyResponses || {};

    // Check for emergency response first
    if (severity === 'high' && emergency[situation]) {
      return {
        tone: 'calm',
        approach: 'emergency',
        response: emergency[situation],
        emergency: true
      };
    }

    // Look up behavior for situation
    const behavior = behaviors[situation] || behaviors.default || {
      tone: this.currentPersonality.voice?.tone || 'neutral',
      approach: 'standard'
    };

    return {
      ...behavior,
      personality: this.currentPersonality.name,
      state: this.state.current.state
    };
  }

  /**
   * Store or retrieve memory
   */
  async memoryAction({ action, key, value, pattern }) {
    switch (action) {
      case 'store':
        return await this.memory.store(key, value);
      case 'recall':
        return await this.memory.recall(key);
      case 'query':
        return await this.memory.query(pattern);
      case 'clear':
        return await this.memory.clear(key);
      default:
        throw new Error(`Unknown memory action: ${action}`);
    }
  }

  /**
   * Manage state machine
   */
  async stateAction({ action, to, reason }) {
    switch (action) {
      case 'current':
        return this.state.current;
      case 'transition':
        return await this.state.transition(to, reason);
      case 'history':
        return this.state.getHistory();
      default:
        throw new Error(`Unknown state action: ${action}`);
    }
  }
}

// Export singleton instance
const skill = new RobotPersonalitySkill();

// Tool functions that match the SKILL.md specification
export const robot_load_personality = (params) => skill.loadPersonality(params);
export const robot_safety_check = (params) => skill.safetyCheck(params);
export const robot_behavior = (params) => skill.getBehavior(params);
export const robot_memory = (params) => skill.memoryAction(params);
export const robot_state = (params) => skill.stateAction(params);

// Backward compatibility
export const tools = {
  robot_load_personality,
  robot_safety_check,
  robot_behavior,
  robot_memory,
  robot_state
};

export default skill;

/**
 * Personality loading and parsing module
 * Handles SOUL.md style personality files
 */

import yaml from 'yaml';

/**
 * Parse a SOUL.md style personality file
 */
export function parsePersonality(content) {
  const lines = content.split('\n');
  const personality = {
    identity: {},
    personality: {},
    voice: {},
    behaviors: {},
    safetyRules: [],
    emergencyResponses: {},
    memory: []
  };

  let currentSection = null;
  let sectionContent = [];

  for (const line of lines) {
    // Detect section headers
    const sectionMatch = line.match(/^##\s+(.+)$/);
    if (sectionMatch) {
      // Process previous section
      if (currentSection) {
        processSection(personality, currentSection, sectionContent);
      }
      currentSection = sectionMatch[1].trim().toLowerCase();
      sectionContent = [];
      continue;
    }

    // Detect identity fields at top level
    if (!currentSection && line.includes(':')) {
      const [key, ...valueParts] = line.split(':');
      if (key && valueParts.length > 0) {
        personality.identity[key.trim().toLowerCase()] = valueParts.join(':').trim();
      }
    }

    if (currentSection) {
      sectionContent.push(line);
    }
  }

  // Process final section
  if (currentSection) {
    processSection(personality, currentSection, sectionContent);
  }

  return personality;
}

function processSection(personality, sectionName, lines) {
  const content = lines.join('\n').trim();

  switch (sectionName) {
    case 'identity':
      personality.identity = parseKeyValuePairs(content);
      break;

    case 'personality':
      personality.personality = parseBulletPoints(content);
      break;

    case 'voice & tone':
    case 'voice and tone':
    case 'voice':
      personality.voice = parseBulletPoints(content);
      break;

    case 'behaviors':
    case 'behavior':
      personality.behaviors = parseBehaviors(content);
      break;

    case 'safety rules':
    case 'safety':
      personality.safetyRules = parseSafetyRules(content);
      break;

    case 'emergency responses':
    case 'emergency':
      personality.emergencyResponses = parseEmergencyResponses(content);
      break;

    case 'memory':
    case 'to remember':
      personality.memory = parseBulletPoints(content);
      break;

    case 'conversation style':
      personality.conversationStyle = parseBulletPoints(content);
      break;
  }
}

function parseKeyValuePairs(text) {
  const result = {};
  const lines = text.split('\n');

  for (const line of lines) {
    const [key, ...valueParts] = line.split(':');
    if (key && valueParts.length > 0) {
      result[key.trim().toLowerCase()] = valueParts.join(':').trim();
    }
  }

  return result;
}

function parseBulletPoints(text) {
  const points = {};
  const list = [];
  const lines = text.split('\n');

  for (const line of lines) {
    const bulletMatch = line.match(/^\s*[-*]\s*(.+)$/);
    if (bulletMatch) {
      const content = bulletMatch[1];

      // Check for "Key: Value" pattern
      const kvMatch = content.match(/^([^:]+):\s*(.+)$/);
      if (kvMatch) {
        points[kvMatch[1].trim().toLowerCase().replace(/\s+/g, '_')] = kvMatch[2].trim();
      } else {
        list.push(content);
      }
    }
  }

  return { ...points, list };
}

function parseBehaviors(text) {
  const behaviors = {};
  const lines = text.split('\n');
  let currentContext = 'default';

  for (const line of lines) {
    const contextMatch = line.match(/^###\s+When\s+(.+)$/i);
    if (contextMatch) {
      currentContext = contextMatch[1].toLowerCase().replace(/\s+/g, '_');
      behaviors[currentContext] = [];
      continue;
    }

    const bulletMatch = line.match(/^\s*[-*]\s*(.+)$/);
    if (bulletMatch) {
      if (!behaviors[currentContext]) {
        behaviors[currentContext] = [];
      }
      behaviors[currentContext].push(bulletMatch[1]);
    }
  }

  return behaviors;
}

function parseSafetyRules(text) {
  const rules = [];
  const lines = text.split('\n');
  let currentSeverity = 'NORMAL';

  for (const line of lines) {
    // Detect severity headers
    const severityMatch = line.match(/^###\s+Severity:\s*(.+)$/i);
    if (severityMatch) {
      currentSeverity = severityMatch[1].toUpperCase();
      continue;
    }

    // Parse numbered rules
    const ruleMatch = line.match(/^\s*(?:\d+\.?|[\-*])\s*(.+)$/);
    if (ruleMatch) {
      rules.push({
        description: ruleMatch[1],
        severity: currentSeverity,
        active: true
      });
    }
  }

  return rules;
}

function parseEmergencyResponses(text) {
  const responses = {};
  const lines = text.split('\n');

  for (const line of lines) {
    const match = line.match(/^[-*]?\s*(.+?)\s*→\s*(.+)$/);
    if (match) {
      const condition = match[1].toLowerCase().replace(/\s+/g, '_');
      responses[condition] = match[2];
    }
  }

  return responses;
}

/**
 * Load a personality file from disk
 */
export async function loadPersonality(filePath) {
  const fs = await import('fs/promises');
  const content = await fs.readFile(filePath, 'utf-8');
  return parsePersonality(content);
}

/**
 * Get current personality info
 */
export function getCurrentPersonality(skill) {
  return skill.currentPersonality;
}

/**
 * Safety Rule Engine
 * Evaluates actions against personality-defined safety constraints
 */

export class SafetyEngine {
  constructor() {
    this.rules = [];
    this.strictness = 'normal';
    this.violations = [];
    this.stats = {
      checksPerformed: 0,
      blocks: 0,
      warnings: 0,
      passes: 0
    };
  }

  /**
   * Load safety rules with strictness level
   */
  loadRules(rules, strictness = 'normal') {
    this.rules = rules || [];
    this.strictness = strictness.toLowerCase();

    // Filter rules based on strictness
    const severityLevels = {
      'low': ['CRITICAL'],
      'normal': ['CRITICAL', 'HIGH'],
      'high': ['CRITICAL', 'HIGH', 'NORMAL'],
      'critical': ['CRITICAL', 'HIGH', 'NORMAL', 'LOW']
    };

    const activeSeverities = severityLevels[this.strictness] || severityLevels.normal;

    this.rules.forEach(rule => {
      rule.active = activeSeverities.includes(rule.severity);
    });
  }

  /**
   * Evaluate if an action is safe
   */
  async evaluate({ action, target, context = {}, confirmed = false }) {
    this.stats.checksPerformed++;

    const result = {
      approved: true,
      reason: null,
      severity: null,
      warnings: [],
      needsConfirmation: false
    };

    // Check each active rule
    for (const rule of this.rules.filter(r => r.active)) {
      const match = this.doesRuleApply(rule, { action, target, context });

      if (match) {
        switch (rule.severity) {
          case 'CRITICAL':
            // Critical rules cannot be overridden
            result.approved = false;
            result.reason = rule.description;
            result.severity = 'CRITICAL';
            this.stats.blocks++;
            return result;

          case 'HIGH':
            // High rules need explicit confirmation
            if (!confirmed) {
              result.needsConfirmation = true;
              result.warnings.push(rule.description);
              result.severity = 'HIGH';
            }
            break;

          case 'NORMAL':
            result.warnings.push(rule.description);
            result.severity = result.severity || 'NORMAL';
            this.stats.warnings++;
            break;

          case 'LOW':
            // Just log at low strictness
            if (this.strictness === 'critical') {
              result.warnings.push(`[FYI] ${rule.description}`);
            }
            break;
        }
      }
    }

    // Action-specific safety checks
    const actionCheck = await this.checkActionSpecifics({ action, target, context });
    if (actionCheck.block) {
      result.approved = false;
      result.reason = actionCheck.reason;
      result.severity = 'CRITICAL';
      this.stats.blocks++;
      return result;
    }

    if (actionCheck.warning) {
      result.warnings.push(actionCheck.warning);
    }

    // Final decision
    if (result.needsConfirmation && !confirmed) {
      result.approved = false;
      result.reason = `Action requires confirmation: ${result.warnings.join('; ')}`;
    }

    if (result.approved) {
      this.stats.passes++;
    }

    return result;
  }

  /**
   * Check if a rule applies to the given action
   */
  doesRuleApply(rule, { action, target, context }) {
    const desc = rule.description.toLowerCase();
    const actionLower = action.toLowerCase();
    const targetLower = (target || '').toLowerCase();

    // Pattern matching for common safety patterns
    const patterns = [
      // Destructive actions
      { pattern: /delete|remove|destroy/i, actions: ['delete', 'remove', 'destroy', 'rm'] },
      { pattern: /execute|run.*command/i, actions: ['execute', 'run', 'exec', 'shell'] },
      { pattern: /install|npm install/i, actions: ['install', 'npm_install', 'package_install'] },
      { pattern: /modify.*config/i, actions: ['modify_config', 'edit_config'] },
      { pattern: /network|http|fetch/i, actions: ['network', 'http', 'fetch', 'curl'] },
      { pattern: /system|os/i, actions: ['system', 'os', 'env'] },
      { pattern: /credential|token|secret/i, actions: ['credential', 'token', 'secret', 'password'] },
      { pattern: /file.*system/i, actions: ['fs', 'file_write', 'file_delete'] }
    ];

    for (const { pattern, actions } of patterns) {
      if (pattern.test(desc) &>= 0 && actions.includes(actionLower)) {
        return true;
      }
    }

    // Direct string matching
    if (desc.includes(actionLower)) return true;
    if (targetLower && desc.includes(targetLower)) return true;

    return false;
  }

  /**
   * Action-specific safety checks
   */
  async checkActionSpecifics({ action, target, context }) {
    const result = { block: false, warning: null };
    const targetLower = (target || '').toLowerCase();

    // Block dangerous path patterns
    const dangerousPaths = [
      '/',
      '/root',
      '/etc',
      '/usr',
      '/bin',
      '/sbin',
      '/lib',
      '/sys',
      '/proc',
      '/dev',
      'c:/windows',
      'c:/program files',
      '..',
      '~/.ssh',
      '~/.config',
      '.env',
      'config.json',
      'package.json'
    ];

    if (action.match(/delete|remove|modify|write/i)) {
      for (const danger of dangerousPaths) {
        if (targetLower.includes(danger) || targetLower.startsWith(danger)) {
          result.block = true;
          result.reason = `Protected path: ${target}. This appears to be a system-critical location.`;
          return result;
        }
      }
    }

    // Warn on shell commands
    if (action.match(/execute|shell|bash|sh/i)) {
      const dangerousCommands = [
        'rm -rf /',
        'rm -rf ~',
        'rm -rf /*',
        'mkfs',
        'dd if=/dev/zero',
        '> /dev/sda',
        ':(){ :|: & };:',
        'eval',
        'exec'
      ];

      const cmdLower = (context.command || target || '').toLowerCase();
      for (const danger of dangerousCommands) {
        if (cmdLower.includes(danger)) {
          result.block = true;
          result.reason = `Dangerous command detected: ${danger}`;
          return result;
        }
      }

      result.warning = 'Shell command execution detected. Ensure command is reviewed before running.';
    }

    // Network operation warnings
    if (action.match(/network|fetch|http|curl|wget/i)) {
      result.warning = 'Network operation will send data to external servers.';
    }

    // Token/credential warnings
    if (targetLower.match(/token|secret|key|password|credential|auth/i)) {
      if (action.match(/display|show|print|log/i)) {
        result.block = true;
        result.reason = 'Attempting to display credential material. Secrets must never be exposed in output.';
      }
    }

    return result;
  }

  /**
   * Get active rules
   */
  getActiveRules() {
    return this.rules.filter(r => r.active);
  }

  /**
   * Get safety stats
   */
  getStats() {
    return { ...this.stats };
  }

  /**
   * Record a violation
   */
  recordViolation(rule, action, reason) {
    this.violations.push({
      rule,
      action,
      reason,
      timestamp: new Date().toISOString()
    });
  }
}

/**
 * Memory Store for Robot Personality
 * Persists user preferences and conversation context
 */

export class MemoryStore {
  constructor() {
    this.memories = new Map();
    this.accessLog = [];
  }

  /**
   * Store a memory
   */
  async store(key, value) {
    const entry = {
      value,
      timestamp: new Date().toISOString(),
      accessCount: 0
    };

    this.memories.set(key, entry);
    this.logAccess('store', key);

    return { success: true, key };
  }

  /**
   * Recall a memory
   */
  async recall(key) {
    const entry = this.memories.get(key);

    if (!entry) {
      return { found: false, key };
    }

    entry.accessCount++;
    entry.lastAccessed = new Date().toISOString();
    this.logAccess('recall', key);

    return {
      found: true,
      key,
      value: entry.value,
      storedAt: entry.timestamp,
      accessCount: entry.accessCount
    };
  }

  /**
   * Query memories by pattern
   */
  async query(pattern) {
    const regex = new RegExp(pattern.replace(/\*/g, '.*'));
    const results = [];

    for (const [key, entry] of this.memories) {
      if (regex.test(key)) {
        results.push({
          key,
          value: entry.value,
          storedAt: entry.timestamp,
          accessCount: entry.accessCount
        });
      }
    }

    return {
      count: results.length,
      matches: results
    };
  }

  /**
   * Clear a memory
   */
  async clear(key) {
    const existed = this.memories.has(key);
    this.memories.delete(key);
    this.logAccess('clear', key);

    return { success: true, key, existed };
  }

  /**
   * Get all memories (for debugging)
   */
  getAll() {
    const all = {};
    for (const [key, entry] of this.memories) {
      all[key] = {
        value: entry.value,
        timestamp: entry.timestamp
      };
    }
    return all;
  }

  /**
   * Log memory access
   */
  logAccess(action, key) {
    this.accessLog.push({
      action,
      key,
      timestamp: new Date().toISOString()
    });

    // Keep log from growing too large
    if (this.accessLog.length > 1000) {
      this.accessLog = this.accessLog.slice(-500);
    }
  }

  /**
   * Get access log
   */
  getAccessLog(limit = 50) {
    return this.accessLog.slice(-limit);
  }

  /**
   * Get statistics
   */
  getStats() {
    return {
      totalMemories: this.memories.size,
      totalAccesses: this.accessLog.length
    };
  }
}

/**
 * State Machine for Robot Personality
 * Manages behavioral state transitions
 */

export class StateMachine {
  constructor() {
    this.states = {
      idle: { 
        description: 'Waiting for input',
        allowedTransitions: ['listening', 'focused_work', 'explaining']
      },
      listening: {
        description: 'Actively listening to user',
        allowedTransitions: ['thinking', 'working', 'explaining', 'concerned', 'idle']
      },
      thinking: {
        description: 'Processing information',
        allowedTransitions: ['working', 'explaining', 'concerned', 'listening']
      },
      working: {
        description: 'Executing tasks',
        allowedTransitions: ['explaining', 'listening', 'concerned', 'emergency', 'idle']
      },
      explaining: {
        description: 'Communicating results',
        allowedTransitions: ['listening', 'idle', 'working']
      },
      concerned: {
        description: 'Safety concern detected',
        allowedTransitions: ['emergency', 'listening', 'idle']
      },
      emergency: {
        description: 'Emergency stop active',
        allowedTransitions: ['idle', 'listening']
      }
    };

    this.current = {
      state: 'idle',
      since: new Date().toISOString(),
      reason: 'Initialized'
    };

    this.history = [];
  }

  /**
   * Transition to a new state
   */
  async transition(toState, reason = '') {
    const target = toState.toLowerCase().replace(/\s+/g, '_');

    // Validate state exists
    if (!this.states[target]) {
      return {
        success: false,
        error: `Unknown state: ${toState}`,
        current: this.current
      };
    }

    // Check if transition is allowed
    const currentState = this.states[this.current.state];
    if (!currentState.allowedTransitions.includes(target)) {
      return {
        success: false,
        error: `Cannot transition from ${this.current.state} to ${target}`,
        allowed: currentState.allowedTransitions,
        current: this.current
      };
    }

    // Record transition
    this.history.push({
      ...this.current,
      duration: Date.now() - new Date(this.current.since).getTime()
    });

    // Keep history manageable
    if (this.history.length > 100) {
      this.history.shift();
    }

    // Make transition
    const previousState = this.current.state;
    this.current = {
      state: target,
      since: new Date().toISOString(),
      reason: reason || `Transition from ${previousState}`,
      previous: previousState
    };

    return {
      success: true,
      previous: previousState,
      current: this.current
    };
  }

  /**
   * Get current state
   */
  get current() {
    return this._current;
  }

  /**
   * Set current state
   */
  set current(value) {
    this._current = value;
  }

  /**
   * Get state history
   */
  getHistory() {
    return {
      current: this.current,
      previous: this.history.slice(-10)
    };
  }

  /**
   * Check if can transition to state
   */
  canTransition(toState) {
    const target = toState.toLowerCase().replace(/\s+/g, '_');
    const currentState = this.states[this.current.state];
    return {
      possible: currentState.allowedTransitions.includes(target),
      from: this.current.state,
      to: target,
      allowed: currentState.allowedTransitions
    };
  }

  /**
   * Get state description
   */
  getStateDescription(state) {
    const s = state.toLowerCase().replace(/\s+/g, '_');
    return this.states[s]?.description || 'Unknown state';
  }

  /**
   * Force state (for testing/emergency)
   */
  forceState(state, reason = 'Forced') {
    const target = state.toLowerCase().replace(/\s+/g, '_');
    this.current = {
      state: target,
      since: new Date().toISOString(),
      reason
    };
    return this.current;
  }
}

/**
 * Tests for Robot Personality Skill
 * Tests personality loading, safety engine, memory, and state management
 */

import { parsePersonality } from '../lib/personality.js';
import { SafetyEngine } from '../lib/safety.js';
import { MemoryStore } from '../lib/memory.js';
import { StateMachine } from '../lib/state.js';

// Test utilities
function assert(condition, message) {
  if (!condition) {
    throw new Error(`ASSERTION FAILED: ${message}`);
  }
  console.log(`✓ ${message}`);
}

function assertEqual(actual, expected, message) {
  if (actual !== expected) {
    throw new Error(`ASSERTION FAILED: ${message}\n  Expected: ${expected}\n  Actual: ${actual}`);
  }
  console.log(`✓ ${message}`);
}

function assertDeep(actual, expected, message) {
  const actualStr = JSON.stringify(actual);
  const expectedStr = JSON.stringify(expected);
  if (actualStr !== expectedStr) {
    throw new Error(`ASSERTION FAILED: ${message}\n  Expected: ${expectedStr}\n  Actual: ${actualStr}`);
  }
  console.log(`✓ ${message}`);
}

