#!/usr/bin/env node
/**
 * Robot Personality Skill - Demonstration Script
 * Shows how the skill loads personality files and enforces safety rules
 */

import { parsePersonality, loadPersonality } from '../.pi/skills/robot-personality/lib/personality.js';
import { SafetyEngine } from '../.pi/skills/robot-personality/lib/safety.js';
import { MemoryStore } from '../.pi/skills/robot-personality/lib/memory.js';
import { StateMachine } from '../.pi/skills/robot-personality/lib/state.js';
import fs from 'fs/promises';
import path from 'path';

console.log('\n╔════════════════════════════════════════════════════════════════════╗');
console.log('║       ROBOT PERSONALITY SKILL - DEMONSTRATION                    ║');
console.log('║       Inspired by ZeroClaw Robot Kit                             ║');
console.log('╚════════════════════════════════════════════════════════════════════╝\n');

async function demo() {
  // =========================================================================
  // DEMO 1: Load Personalities
  // =========================================================================
  console.log('📋 DEMO 1: Loading Personality Files\n');

  const personalities = ['guardian', 'builder', 'companion'];

  for (const name of personalities) {
    const filePath = path.resolve('./personalities', `${name}.md`);
    try {
      const content = await fs.readFile(filePath, 'utf-8');
      const personality = parsePersonality(content);

      console.log(`  ✓ ${name.toUpperCase()} Personality`);
      console.log(`    Identity: ${personality.identity.name || 'N/A'}`);
      console.log(`    Rules: ${personality.safetyRules.length} safety rules`);
      console.log(`    Traits: ${Object.keys(personality.personality).filter(k => !k.startsWith('list')).length} core traits\n`);
    } catch (e) {
      console.log(`  ✗ Failed to load ${name}: ${e.message}\n`);
    }
  }

  // =========================================================================
  // DEMO 2: Safety Engine in Action
  // =========================================================================
  console.log('🛡️  DEMO 2: Safety Engine Protection\n');

  const safety = new SafetyEngine();

  // Load Guardian's safety rules
  const guardianPath = path.resolve('./personalities', 'guardian.md');
  const guardianContent = await fs.readFile(guardianPath, 'utf-8');
  const guardian = parsePersonality(guardianContent);
  safety.loadRules(guardian.safetyRules, 'high');

  const testActions = [
    { action: 'delete', target: '/etc/passwd', desc: 'Delete system file' },
    { action: 'delete', target: '/job/tmp/old-file.txt', desc: 'Delete temp file' },
    { action: 'execute', context: { command: 'rm -rf /' }, desc: 'Destructive command' },
    { action: 'execute', context: { command: 'echo "hello"' }, desc: 'Safe command' },
    { action: 'display', target: 'api_token', desc: 'Expose credentials' },
  ];

  for (const test of testActions) {
    const result = await safety.evaluate(test);
    const status = result.approved 
      ? '✅ APPROVED' 
      : result.needsConfirmation 
        ? '⚠️ NEEDS CONFIRMATION' 
        : '❌ BLOCKED';
    console.log(`  ${status}: ${test.desc}`);
    if (result.reason) {
      console.log(`      Reason: ${result.reason.substring(0, 80)}...`);
    }
    console.log();
  }

  console.log(`  Safety Stats: ${JSON.stringify(safety.getStats())}\n`);

  // =========================================================================
  // DEMO 3: Memory System
  // =========================================================================
  console.log('💾 DEMO 3: Memory System\n');

  const memory = new MemoryStore();

  // Store some memories
  await memory.store('user_name', 'Developer');
  await memory.store('safety_strictness', 'high');
  await memory.store('last_personality', 'Guardian');

  // Recall
  const recalled = await memory.recall('user_name');
  console.log(`  ✓ Recalled: ${recalled.key} = "${recalled.value}"`);

  // Query
  const query = await memory.query('user_*');
  console.log(`  ✓ Found ${query.count} user-related memories`);

  console.log(`  Memory Stats: ${JSON.stringify(memory.getStats())}\n`);

  // =========================================================================
  // DEMO 4: State Machine
  // =========================================================================
  console.log('🎭 DEMO 4: Behavioral State Machine\n');

  const state = new StateMachine();

  console.log(`  Initial State: ${state.current.state}`);

  // User starts a task
  await state.transition('listening', 'User started speaking');
  console.log(`  → ${state.current.state}: Listening to user`);

  await state.transition('working', 'Starting task execution');
  console.log(`  → ${state.current.state}: Executing tasks`);

  // Try invalid transition (working -> idle directly not allowed in normal flow)
  const check = state.canTransition('emergency');
  if (check.possible) {
    await state.transition('emergency', 'Critical issue detected');
    console.log(`  → ${state.current.state}: EMERGENCY STOP ACTIVE`);
  } else {
    console.log(`  ✗ Cannot transition to emergency from working? Checking again...`);
    // Actually, working CAN transition to emergency
    await state.transition('emergency', 'Critical safety violation');
    console.log(`  → ${state.current.state}: EMERGENCY STOP ACTIVE (safety triggered)`);
  }

  // Recovery
  await state.transition('idle', 'Issue resolved');
  console.log(`  → ${state.current.state}: Back to ready\n`);

  // =========================================================================
  // DEMO 5: Emergency Response
  // =========================================================================
  console.log('🚨 DEMO 5: Emergency Response System\n');

  const scenarios = [
    { situation: 'user_shows_frustration', message: 'User expresses frustration' },
    { situation: 'potential_data_loss', message: 'Dangerous operation detected' },
    { situation: 'task_unclear', message: 'Ambiguous instructions received' },
  ];

  for (const scenario of scenarios) {
    const response = guardian.emergencyResponses[scenario.situation];
    if (response) {
      console.log(`  ⚡ ${scenario.message}`);
      console.log(`     Response: "${response}"\n`);
    }
  }

  // =========================================================================
  // SUMMARY
  // =========================================================================
  console.log('╔════════════════════════════════════════════════════════════════════╗');
  console.log('║       DEMO COMPLETE                                                ║');
  console.log('╚════════════════════════════════════════════════════════════════════╝\n');

  console.log('Features Demonstrated:');
  console.log('  • SOUL.md personality file parsing');
  console.log('  • Safety rule evaluation with severity levels');
  console.log('  • Path-based protection for critical files');
  console.log('  • Command-based safety scanning');
  console.log('  • Memory storage and retrieval');
  console.log('  • Behavioral state machine with transitions');
  console.log('  • Emergency response system\n');

  console.log('Personalities Created:');
  console.log('  • Guardian: Safety-first, methodical approach');
  console.log('  • Builder: Creative, encouraging, iterative');
  console.log('  • Companion: Friendly, supportive, conversational\n');
}

demo().catch(err => {
  console.error('Demo failed:', err);
  process.exit(1);
});

