/**
 * Hash Generator - Generate cryptographic hashes
 */
const crypto = require('crypto');
const fs = require('fs');

function parseArgs(args) {
  const result = {
    text: null,
    file: null,
    algo: 'sha256',
    format: 'hex',
    verify: null
  };

  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--text': result.text = args[++i]; break;
      case '--file': result.file = args[++i]; break;
      case '--algo': result.algo = args[++i]; break;
      case '--format': result.format = args[++i]; break;
      case '--verify': result.verify = args[++i]; break;
    }
  }
  return result;
}

function hashString(text, algo) {
  const hash = crypto.createHash(algo);
  hash.update(text, 'utf8');
  return hash.digest('hex');
}

function hashFile(filepath, algo) {
  const data = fs.readFileSync(filepath);
  const hash = crypto.createHash(algo);
  hash.update(data);
  return hash.digest('hex');
}

function toBase64(hexStr) {
  return Buffer.from(hexStr, 'hex').toString('base64');
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.text && !args.file) {
    console.log('Usage: hash.js --text "string" | --file ./path --algo md5|sha1|sha256|sha512|all [--format hex|base64]');
    process.exit(1);
  }

  const algos = args.algo === 'all' ? ['md5', 'sha1', 'sha256', 'sha512'] : [args.algo];
  const results = {};

  algos.forEach(algo => {
    try {
      let hash;
      if (args.file) {
        hash = hashFile(args.file, algo);
      } else {
        hash = hashString(args.text, algo);
      }
      results[algo] = args.format === 'base64' ? toBase64(hash) : hash;
    } catch (e) {
      console.error(`Error hashing with ${algo}:`, e.message);
    }
  });

  // Verification mode
  if (args.verify && args.file) {
    const computed = results[args.algo];
    const verified = computed.toLowerCase() === args.verify.toLowerCase();
    console.log(JSON.stringify({
      file: args.file,
      algorithm: args.algo,
      computed: computed,
      expected: args.verify,
      verified: verified,
      message: verified ? 'âœ“ Hash matches' : 'âœ— Hash does NOT match'
    }, null, 2));
    process.exit(verified ? 0 : 1);
  }

  // Normal output
  if (args.algo === 'all') {
    console.log(JSON.stringify(results, null, 2));
  } else {
    console.log(results[args.algo]);
  }
}

main();

#!/usr/bin/env node
/**
 * URL Encoder - Encode/decode URL components
 */

function showHelp() {
  console.log(`Usage: url.js <command> [options]
Commands:
  encode <string>       Encode a string for URL
  decode <string>       Decode a URL-encoded string
  parse <url>          Parse URL into components
  query ...           Build query string from key=value pairs
Options:
  --pretty            Pretty print JSON output
  --component         Use encodeURIComponent (default) or encodeURI`);
}

function parseArgs(args) {
  const cmd = args[0];
  const result = {
    command: cmd,
    value: null,
    params: {},
    pretty: args.includes('--pretty'),
    component: args.includes('--component') || !args.includes('--uri')
  };

  if (!cmd) return result;

  if (cmd === 'query') {
    for (let i = 1; i < args.length; i++) {
      if (args[i].startsWith('--') && !args[i].startsWith('---')) {
        const key = args[i].slice(2);
        const val = args[i + 1] && !args[i + 1].startsWith('--') ? args[++i] : 'true';
        result.params[key] = val;
      }
    }
  } else if (['encode', 'decode', 'parse'].includes(cmd)) {
    result.value = args[1];
  }

  return result;
}

function encodeUrl(str, componentMode) {
  if (componentMode) {
    return encodeURIComponent(str);
  }
  return encodeURI(str);
}

function decodeUrl(str, componentMode) {
  try {
    if (componentMode) {
      return decodeURIComponent(str);
    }
    return decodeURI(str);
  } catch (e) {
    return `Error: Invalid encoding - ${e.message}`;
  }
}

function parseUrl(url) {
  try {
    const parsed = new URL(url);
    const query = {};
    parsed.searchParams.forEach((v, k) => {
      query[k] = v;
    });

    return {
      href: parsed.href,
      protocol: parsed.protocol,
      host: parsed.host,
      hostname: parsed.hostname,
      port: parsed.port,
      pathname: parsed.pathname,
      search: parsed.search,
      hash: parsed.hash,
      username: parsed.username,
      password: parsed.password,
      query: query
    };
  } catch (e) {
    return { error: e.message };
  }
}

function buildQuery(params) {
  const pairs = Object.entries(params).map(([k, v]) => {
    return `${encodeURIComponent(k)}=${encodeURIComponent(v)}`;
  });
  return pairs.length > 0 ? '?' + pairs.join('&') : '';
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.command || args.command === 'help') {
    showHelp();
    return;
  }

  switch (args.command) {
    case 'encode': {
      if (!args.value) {
        console.error('Error: No string provided for encoding');
        process.exit(1);
      }
      console.log(encodeUrl(args.value, args.component));
      break;
    }

    case 'decode': {
      if (!args.value) {
        console.error('Error: No string provided for decoding');
        process.exit(1);
      }
      console.log(decodeUrl(args.value, args.component));
      break;
    }

    case 'parse': {
      if (!args.value) {
        console.error('Error: No URL provided to parse');
        process.exit(1);
      }
      const result = parseUrl(args.value);
      if (args.pretty) {
        console.log(JSON.stringify(result, null, 2));
      } else {
        console.log(JSON.stringify(result));
      }
      break;
    }

    case 'query': {
      const qs = buildQuery(args.params);
      console.log(qs);
      break;
    }

    default: {
      console.error(`Unknown command: ${args.command}`);
      showHelp();
      process.exit(1);
    }
  }
}

main();

