      result = fs.readFileSync(0, 'utf8');
    } catch (e) {
      // No stdin
    }
    if (result) {
      inputData = Buffer.from(result.trim(), 'utf8');
    }
  }

  if (!inputData) {
    console.error('Error: No input provided. Use --text, --file, or pipe data.');
    process.exit(1);
  }

  let output = null;

  switch (args.command) {
    case 'encode': {
      // Binary data encodes directly, text needs conversion
