        output = decode(str, args.urlSafe);
        if (!output) {
          process.exit(1);
        }
      } catch (e) {
        console.error(`Error: ${e.message}`);
        process.exit(1);
      }
      break;
    }

    default: {
      console.error(`Unknown command: ${args.command}`);
      process.exit(1);
    }
  }

  if (args.output) {
    try {
      fs.writeFileSync(args.output, output);
