#!/usr/bin/env node
/**
 * Diagnostic Doctor - System health checker
 * Inspired by ZeroClaw's doctor command
 */

const fs = require('fs');
const path = require('path');
const { execSync } = require('child_process');

// ANSI color codes
const colors = {
  reset: '\x1b[0m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  cyan: '\x1b[36m',
  gray: '\x1b[90m',
};

// Status levels
const STATUS = {
  OK: 'OK',
  WARNING: 'WARNING',
  ERROR: 'ERROR',
  INFO: 'INFO',
};

class DiagnosticDoctor {
  constructor() {
    this.results = [];
    this.options = {
      all: false,
      system: false,
      config: false,
      network: false,
      security: false,
      performance: false,
      report: null,
      verbose: false,
    };
  }

  parseArgs(args) {
    args.forEach((arg) => {
      if (arg === '--all') this.options.all = true;
      if (arg === '--system') this.options.system = true;
      if (arg === '--config') this.options.config = true;
      if (arg === '--network') this.options.network = true;
      if (arg === '--security') this.options.security = true;
      if (arg === '--performance') this.options.performance = true;
      if (arg === '--verbose') this.options.verbose = true;
      if (arg.startsWith('--report=')) this.options.report = arg.split('=')[1];
    });

    // If no specific check requested, run all
    if (!this.options.system && !this.options.config && !this.options.network && 
        !this.options.security && !this.options.performance) {
      this.options.all = true;
    }
  }

  addResult(status, category, message, recommendation = null) {
    this.results.push({
      status,
      category,
      message,
      recommendation,
      timestamp: new Date().toISOString(),
    });

    const statusColor = {
      [STATUS.OK]: colors.green,
      [STATUS.WARNING]: colors.yellow,
      [STATUS.ERROR]: colors.red,
      [STATUS.INFO]: colors.blue,
    }[status] || colors.reset;

    console.log(
      `${statusColor}[${status}]${colors.reset} ${colors.cyan}[${category}]${colors.reset} ${message}`
    );

    if (recommendation && status !== STATUS.OK) {
      console.log(`  ${colors.gray}→ ${recommendation}${colors.reset}`);
    }
  }

  // === SYSTEM CHECKS ===
  checkDiskSpace() {
    try {
      const output = execSync('df -h /', { encoding: 'utf8' });
      const lines = output.split('\n');
      const rootLine = lines.find(l => l.includes('/') && !l.includes('/boot'));

      if (rootLine) {
        const parts = rootLine.trim().split(/\s+/);
        const usagePercent = parseInt(parts[4].replace('%', ''));

        if (usagePercent > 90) {
          this.addResult(STATUS.ERROR, 'System', `Disk usage critical: ${usagePercent}%`, 
            'Free up disk space immediately or expand storage');
        } else if (usagePercent > 70) {
          this.addResult(STATUS.WARNING, 'System', `Disk usage high: ${usagePercent}%`, 
            'Consider cleaning up old logs and unused files');
        } else {
          this.addResult(STATUS.OK, 'System', `Disk usage normal: ${usagePercent}%`);
        }
      }
    } catch (e) {
      this.addResult(STATUS.ERROR, 'System', 'Unable to check disk space', e.message);
    }
  }

  checkMemory() {
    try {
      const output = execSync('free -m', { encoding: 'utf8' });
      const lines = output.split('\n');
      const memLine = lines.find(l => l.includes('Mem:'));

      if (memLine) {
        const parts = memLine.trim().split(/\s+/);
        const total = parseInt(parts[1]);
        const available = parseInt(parts[6]);
        const usagePercent = Math.round(((total - available) / total) * 100);

        if (usagePercent > 95) {
          this.addResult(STATUS.ERROR, 'System', `Memory usage critical: ${usagePercent}%`, 
            'Close unused processes or add more RAM');
        } else if (usagePercent > 80) {
          this.addResult(STATUS.WARNING, 'System', `Memory usage high: ${usagePercent}%`, 
            'Consider restarting services or reducing load');
        } else {
          this.addResult(STATUS.OK, 'System', `Memory usage normal: ${usagePercent}% (~${available}MB available)`);
        }
      }
    } catch (e) {
      // Try alternative method (macOS)
      try {
        const output = execSync('vm_stat', { encoding: 'utf8' });
        this.addResult(STATUS.INFO, 'System', 'Memory check (macOS) - manual review needed');
      } catch (e2) {
        this.addResult(STATUS.WARNING, 'System', 'Unable to check memory', 
          'Install procps or check memory manually');
      }
    }
  }

  checkNodeVersion() {
    try {
      const version = process.version;
      const major = parseInt(version.slice(1).split('.')[0]);

      if (major >= 20) {
        this.addResult(STATUS.OK, 'System', `Node.js version ${version} (supported)`);
      } else if (major >= 18) {
        this.addResult(STATUS.WARNING, 'System', `Node.js version ${version} (minimum supported)`, 
          'Upgrade to Node.js 20+ for best compatibility');
      } else {
        this.addResult(STATUS.ERROR, 'System', `Node.js version ${version} (not supported)`, 
          'Upgrade to Node.js 20+ immediately');
      }
    } catch (e) {
      this.addResult(STATUS.ERROR, 'System', 'Unable to check Node.js version');
    }
  }

  // === CONFIG CHECKS ===
  checkEnvFile() {
    const envPath = path.join('/job', '.env');

    if (!fs.existsSync(envPath)) {
      this.addResult(STATUS.WARNING, 'Config', '.env file not found', 
        'Create .env file from .env.example');
      return;
    }

    const envContent = fs.readFileSync(envPath, 'utf8');
    const requiredVars = ['APP_URL', 'AUTH_SECRET', 'GH_TOKEN'];
    const missing = [];

    requiredVars.forEach(v => {
      if (!envContent.includes(`${v}=`) || envContent.includes(`${v}=\n`)) {
        missing.push(v);
      }
    });

    if (missing.length > 0) {
      this.addResult(STATUS.ERROR, 'Config', `Missing required env vars: ${missing.join(', ')}`, 
        `Set ${missing.join(', ')} in .env file`);
    } else {
      this.addResult(STATUS.OK, 'Config', 'All required environment variables configured');
    }

    // Check for example values
    if (envContent.includes('your-') || envContent.includes('placeholder')) {
      this.addResult(STATUS.WARNING, 'Config', '.env file contains placeholder values', 
        'Replace all placeholder values with actual configuration');
    }
  }

  checkConfigFiles() {
    const configFiles = [
      '/job/config/CRONS.json',
      '/job/config/TRIGGERS.json',
    ];

    configFiles.forEach(file => {
      if (fs.existsSync(file)) {
        try {
          const content = JSON.parse(fs.readFileSync(file, 'utf8'));
          this.addResult(STATUS.OK, 'Config', `${path.basename(file)} is valid JSON`);
        } catch (e) {
          this.addResult(STATUS.ERROR, 'Config', `${path.basename(file)} has invalid JSON`, 
            `Fix JSON syntax: ${e.message}`);
        }
      } else {
        this.addResult(STATUS.INFO, 'Config', `${path.basename(file)} not found (optional)`);
      }
    });
  }

  // === NETWORK CHECKS ===
  checkInternetConnectivity() {
    try {
      execSync('curl -s --max-time 5 https://api.github.com', { stdio: 'pipe' });
      this.addResult(STATUS.OK, 'Network', 'Internet connectivity verified');
    } catch (e) {
      this.addResult(STATUS.ERROR, 'Network', 'No internet connectivity', 
        'Check network connection and DNS settings');
    }
  }

  checkGitHubAccess() {
    try {
      const token = process.env.GH_TOKEN;
      if (!token) {
        this.addResult(STATUS.WARNING, 'Network', 'GH_TOKEN not set, cannot verify GitHub access',
          'Set GH_TOKEN environment variable');
        return;
      }

      const output = execSync('gh auth status 2>&1', { encoding: 'utf8' });
      if (output.includes('Logged in')) {
        this.addResult(STATUS.OK, 'Network', 'GitHub CLI authenticated');
      } else {
        this.addResult(STATUS.WARNING, 'Network', 'GitHub CLI not authenticated');
      }
    } catch (e) {
      this.addResult(STATUS.WARNING, 'Network', 'Unable to verify GitHub access');
    }
  }

  // === SECURITY CHECKS ===
  checkFilePermissions() {
    const sensitiveFiles = [
      '/job/.env',
      '/job/data',
    ];

    sensitiveFiles.forEach(file => {
      if (fs.existsSync(file)) {
        try {
          const stats = fs.statSync(file);
          const mode = stats.mode;
          const isWorldReadable = mode & 0o044;
          const isWorldWritable = mode & 0o022;

          if (isWorldWritable) {
            this.addResult(STATUS.ERROR, 'Security', `${file} is world-writable`, 
              'Run: chmod o-w ' + file);
          } else if (isWorldReadable && file.includes('.env')) {
            this.addResult(STATUS.WARNING, 'Security', `${file} is world-readable`, 
              'Run: chmod 600 ' + file);
          } else {
            this.addResult(STATUS.OK, 'Security', `${file} permissions OK`);
          }
        } catch (e) {
          this.addResult(STATUS.INFO, 'Security', `Cannot check permissions for ${file}`);
        }
      }
    });
  }

  checkSecretsExposure() {
    try {
      const result = execSync('grep -r "password\|secret\|token" --include="*.md" /job/.pi/skills/ 2>/dev/null || true', 
        { encoding: 'utf8', maxBuffer: 10 * 1024 * 1024 });

      // Just a basic check - real implementation would be more sophisticated
      this.addResult(STATUS.INFO, 'Security', 'Manual review: Check for hardcoded secrets in source code');
    } catch (e) {
      // Ignore errors
    }
  }

  // === PERFORMANCE CHECKS ===
  checkStartupTime() {
    const start = Date.now();
    try {
      // Simple benchmark
      for (let i = 0; i < 100000; i++) {
        Math.sqrt(i);
      }
      const duration = Date.now() - start;

      if (duration < 50) {
        this.addResult(STATUS.OK, 'Performance', `CPU benchmark: ${duration}ms (fast)`);
      } else if (duration < 200) {
        this.addResult(STATUS.WARNING, 'Performance', `CPU benchmark: ${duration}ms (moderate)`);
      } else {
        this.addResult(STATUS.ERROR, 'Performance', `CPU benchmark: ${duration}ms (slow)`);
      }
    } catch (e) {
      this.addResult(STATUS.INFO, 'Performance', 'Unable to run performance check');
    }
  }

  // === MAIN ===
  run() {
    console.log(`${colors.cyan}╔════════════════════════════════════════════════════════╗`);
    console.log(`║           Diagnostic Doctor 🏥 v1.0.0                   ║`);
    console.log(`║   System health check inspired by ZeroClaw              ║`);
    console.log(`╚════════════════════════════════════════════════════════╝${colors.reset}\n`);

    if (this.options.all || this.options.system) {
      console.log(`${colors.cyan}━━━ System Checks ━━━${colors.reset}`);
      this.checkDiskSpace();
      this.checkMemory();
      this.checkNodeVersion();
      console.log();
    }

    if (this.options.all || this.options.config) {
      console.log(`${colors.cyan}━━━ Configuration Checks ━━━${colors.reset}`);
      this.checkEnvFile();
      this.checkConfigFiles();
      console.log();
    }

    if (this.options.all || this.options.network) {
      console.log(`${colors.cyan}━━━ Network Checks ━━━${colors.reset}`);
      this.checkInternetConnectivity();
      this.checkGitHubAccess();
      console.log();
    }

    if (this.options.all || this.options.security) {
      console.log(`${colors.cyan}━━━ Security Checks ━━━${colors.reset}`);
      this.checkFilePermissions();
      this.checkSecretsExposure();
      console.log();
    }

    if (this.options.all || this.options.performance) {
      console.log(`${colors.cyan}━━━ Performance Checks ━━━${colors.reset}`);
      this.checkStartupTime();
      console.log();
    }

    // Summary
    const errors = this.results.filter(r => r.status === STATUS.ERROR).length;
    const warnings = this.results.filter(r => r.status === STATUS.WARNING).length;
    const ok = this.results.filter(r => r.status === STATUS.OK).length;

    console.log(`${colors.cyan}━━━ Summary ━━━${colors.reset}`);
    console.log(`${colors.green}OK: ${ok}${colors.reset} | ${colors.yellow}WARNINGS: ${warnings}${colors.reset} | ${colors.red}ERRORS: ${errors}${colors.reset}`);
    console.log(`Total checks: ${this.results.length}`);

    if (errors > 0) {
      console.log(`\n${colors.red}⚠  Found ${errors} error(s) that need attention${colors.reset}`);
    } else if (warnings > 0) {
      console.log(`\n${colors.yellow}ℹ  Found ${warnings} warning(s) to review${colors.reset}`);
    } else {
      console.log(`\n${colors.green}✓ All checks passed!${colors.reset}`);
    }

    // Save report if requested
    if (this.options.report) {
      fs.writeFileSync(this.options.report, JSON.stringify({
        timestamp: new Date().toISOString(),
        summary: { ok, warnings, errors, total: this.results.length },
        results: this.results,
      }, null, 2));
      console.log(`\nReport saved to: ${this.options.report}`);
    }

    // Exit with appropriate code
    process.exit(errors > 0 ? 1 : 0);
  }
}

// Run if called directly
if (require.main === module) {
  const doctor = new DiagnosticDoctor();
  doctor.parseArgs(process.argv.slice(2));
  doctor.run();
}

module.exports = { DiagnosticDoctor, STATUS };

#!/usr/bin/env node
/**
 * Diff Viewer - File comparison tool
 * Inspired by OpenClaw's diff.ts extension
 */

const fs = require('fs');
const path = require('path');

// ANSI color codes
const colors = {
  reset: '\x1b[0m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  cyan: '\x1b[36m',
  gray: '\x1b[90m',
  bgRed: '\x1b[41m',
  bgGreen: '\x1b[42m',
};

class DiffViewer {
  constructor() {
    this.options = {
      unified: false,
      context: 3,
      sideBySide: false,
      patch: false,
      output: null,
      dir: false,
      color: true,
      noColor: false,
    };
  }

  parseArgs(args) {
    const files = [];

    for (let i = 0; i < args.length; i++) {
      const arg = args[i];

      if (arg === '--unified' || arg === '-u') this.options.unified = true;
      else if (arg === '--side-by-side' || arg === '-s') this.options.sideBySide = true;
      else if (arg === '--patch' || arg === '-p') this.options.patch = true;
      else if (arg === '--dir' || arg === '-d') this.options.dir = true;
      else if (arg === '--no-color') { this.options.noColor = true; this.options.color = false; }
      else if (arg.startsWith('--context=')) this.options.context = parseInt(arg.split('=')[1]) || 3;
      else if (arg === '--context' || arg === '-c') {
        i++;
        this.options.context = parseInt(args[i]) || 3;
      }
      else if (arg.startsWith('--output=')) this.options.output = arg.split('=')[1];
      else if (arg === '--output' || arg === '-o') {
        i++;
        this.options.output = args[i];
      }
      else if (!arg.startsWith('-')) files.push(arg);
    }

    return files;
  }

  colorize(text, color) {
    if (!this.options.color) return text;
    return `${colors[color]}${text}${colors.reset}`;
  }

  // Myers diff algorithm - simplified
  computeDiff(oldLines, newLines) {
    const m = oldLines.length;
    const n = newLines.length;

    if (m === 0) return { changes: newLines.map((line, i) => ({ type: 'insert', line, oldIndex: -1, newIndex: i })) };
    if (n === 0) return { changes: oldLines.map((line, i) => ({ type: 'delete', line, oldIndex: i, newIndex: -1 })) };

    // Simple LCS-based diff
    const changes = [];
    let oldIdx = 0, newIdx = 0;

    while (oldIdx < m || newIdx < n) {
      if (oldIdx < m && newIdx < n && oldLines[oldIdx] === newLines[newIdx]) {
        changes.push({ type: 'equal', line: oldLines[oldIdx], oldIndex: oldIdx, newIndex: newIdx });
        oldIdx++;
        newIdx++;
      } else if (newIdx < n && (oldIdx >= m || !oldLines.slice(oldIdx).includes(newLines[newIdx]))) {
        changes.push({ type: 'insert', line: newLines[newIdx], oldIndex: -1, newIndex: newIdx });
        newIdx++;
      } else if (oldIdx < m) {
        changes.push({ type: 'delete', line: oldLines[oldIdx], oldIndex: oldIdx, newIndex: -1 });
        oldIdx++;
      } else {
        newIdx++;
      }
    }

    return { changes };
  }

  formatUnifiedDiff(oldLines, newLines, oldPath, newPath, context) {
    const { changes } = this.computeDiff(oldLines, newLines);
    const output = [];

    // Unified diff header
    output.push(`--- ${oldPath}`);
    output.push(`+++ ${newPath}`);

    // Group changes into hunks
    const hunks = [];
    let currentHunk = [];
    let lastChange = -1;

    changes.forEach((change, idx) => {
      if (change.type !== 'equal') {
        // Start a new hunk if needed
        const startIndex = Math.max(0, idx - context);
        const endIndex = Math.min(changes.length, idx + context + 1);

        if (currentHunk.length === 0 || startIndex > lastChange + context) {
          if (currentHunk.length > 0) hunks.push(currentHunk);
          currentHunk = changes.slice(startIndex, endIndex);
        } else {
          // Extend current hunk
          currentHunk = changes.slice(Math.max(0, currentHunk[0] ? changes.indexOf(currentHunk[0]) : 0), endIndex);
        }
        lastChange = idx;
      }
    });

    if (currentHunk.length > 0) hunks.push(currentHunk);

    hunks.forEach(hunk => {
      const oldStart = hunk[0]?.oldIndex + 1 || 0;
      const newStart = hunk[0]?.newIndex + 1 || 0;
      const oldCount = hunk.filter(c => c.type !== 'insert').length;
      const newCount = hunk.filter(c => c.type !== 'delete').length;

      output.push(`@@ -${oldStart},${oldCount} +${newStart},${newCount} @@`);

      hunk.forEach(change => {
        if (change.type === 'equal') {
          output.push(` ${change.line}`);
        } else if (change.type === 'delete') {
          output.push(this.colorize(`-${change.line}`, 'red'));
        } else if (change.type === 'insert') {
          output.push(this.colorize(`+${change.line}`, 'green'));
        }
      });
    });

    return output.join('\n');
  }

  formatSideBySide(oldLines, newLines, width = 80) {
    const halfWidth = Math.floor((width - 7) / 2);
    const output = [];

    const { changes } = this.computeDiff(oldLines, newLines);

    output.push(`${'OLD'.padEnd(halfWidth)}  |  ${'NEW'.padEnd(halfWidth)}`);
    output.push('-'.repeat(width));

    changes.forEach(change => {
      const oldLine = change.type !== 'insert' 
        ? change.line.substring(0, halfWidth).padEnd(halfWidth)
        : ''.padEnd(halfWidth);
      const newLine = change.type !== 'delete' 
        ? change.line.substring(0, halfWidth).padEnd(halfWidth)
        : ''.padEnd(halfWidth);

      const marker = change.type === 'equal' ? ' ' : change.type === 'delete' ? '<' : '>';

      if (change.type === 'equal') {
        output.push(`${oldLine} ${marker} ${newLine}`);
      } else if (change.type === 'delete') {
        output.push(`${this.colorize(oldLine, 'bgRed')} ${marker} ${newLine}`);
      } else if (change.type === 'insert') {
        output.push(`${oldLine} ${marker} ${this.colorize(newLine, 'bgGreen')}`);
      }
    });

    return output.join('\n');
  }

  formatNormal(oldLines, newLines) {
    const { changes } = this.computeDiff(oldLines, newLines);
    const output = [];

    changes.forEach(change => {
      if (change.type === 'delete') {
        output.push(this.colorize(`< ${change.line}`, 'red'));
      } else if (change.type === 'insert') {
        output.push(this.colorize(`> ${change.line}`, 'green'));
      }
    });

    return output.join('\n');
  }

  compareFiles(oldPath, newPath) {
    if (!fs.existsSync(oldPath)) {
      console.error(`Error: ${oldPath} does not exist`);
      process.exit(1);
    }
    if (!fs.existsSync(newPath)) {
      console.error(`Error: ${newPath} does not exist`);
      process.exit(1);
    }

    const oldContent = fs.readFileSync(oldPath, 'utf8');
    const newContent = fs.readFileSync(newPath, 'utf8');

    // Check if identical
    if (oldContent === newContent) {
      console.log(this.colorize('Files are identical', 'green'));
      return '';
    }

    const oldLines = oldContent.split('\n');
    const newLines = newContent.split('\n');

    let output;
    if (this.options.sideBySide) {
      output = this.formatSideBySide(oldLines, newLines);
    } else if (this.options.unified || this.options.patch) {
      output = this.formatUnifiedDiff(oldLines, newLines, oldPath, newPath, this.options.context);
    } else {
      output = this.formatNormal(oldLines, newLines);
    }

    return output;
  }

  run() {
    const files = this.parseArgs(process.argv.slice(2));

    if (files.length < 2) {
      console.log('Diff Viewer - Compare files and generate diffs');
      console.log('');
      console.log('Usage: diff.js [options] <old-file> <new-file>');
      console.log('');
      console.log('Options:');
      console.log('  -u, --unified        Unified diff format (default)');
      console.log('  -s, --side-by-side   Side-by-side comparison');
      console.log('  -p, --patch          Generate patch format');
      console.log('  -c, --context N      Number of context lines (default: 3)');
      console.log('  -o, --output FILE    Write output to file');
      console.log('      --no-color       Disable color output');
      console.log('');
      process.exit(1);
    }

    const oldPath = files[0];
    const newPath = files[1];

    console.log(this.colorize(`Comparing ${oldPath} → ${newPath}`, 'cyan'));
    console.log();

    const output = this.compareFiles(oldPath, newPath);

    if (output) {
      if (this.options.output) {
        fs.writeFileSync(this.options.output, output.replace(/\x1b\[\d+m/g, ''));
        console.log(this.colorize(`\nDiff written to: ${this.options.output}`, 'green'));
      } else {
        console.log(output);

        const oldLines = fs.readFileSync(oldPath, 'utf8').split('\n').length;
        const newLines = fs.readFileSync(newPath, 'utf8').split('\n').length;
        console.log(`\n${this.colorize('Summary:', 'cyan')} ${oldLines} lines → ${newLines} lines`);
      }
    }
  }
}

// Run if called directly
if (require.main === module) {
  const viewer = new DiffViewer();
  viewer.run();
}

module.exports = { DiffViewer };

#!/usr/bin/env node
/**
 * Embedding Search - Semantic document search
 * Inspired by ZeroClaw's memory/embeddings system
 */

const fs = require('fs');
const path = require('path');

// Simple word-based embedding simulation (TF-IDF-like)
class EmbeddingSearch {
  constructor() {
    this.index = null;
    this.documents = [];
    this.wordIndex = new Map();
  }

  tokenize(text) {
    return text.toLowerCase()
      .replace(/[^\w\s]/g, ' ')
      .split(/\s+/)
      .filter(w => w.length >= 2 && !this.isStopWord(w));
  }

  isStopWord(word) {
    const stopWords = new Set([
      'the', 'a', 'an', 'in', 'on', 'at', 'to', 'for', 'of', 'with', 'by', 'is', 'are',
      'was', 'were', 'be', 'been', 'being', 'have', 'has', 'had', 'do', 'does', 'did',
      'will', 'would', 'could', 'should', 'may', 'might', 'must', 'can', 'and', 'or',
      'but', 'if', 'then', 'than', 'so', 'very', 'just', 'now', 'here', 'there',
      'this', 'that', 'these', 'those', 'i', 'you', 'he', 'she', 'it', 'we', 'they',
      'me', 'him', 'her', 'us', 'them', 'my', 'your', 'his', 'her', 'its', 'our', 'their',
    ]);
    return stopWords.has(word);
  }

  // Create a simple document vector representation
  vectorize(text) {
    const tokens = this.tokenize(text);
    const vector = new Map();

    // Term frequency with position weighting
    tokens.forEach((token, idx) => {
      const current = vector.get(token) || 0;
      // Words at the start get slightly higher weight
      const positionWeight = 1 + (tokens.length - idx) / tokens.length * 0.5;
      vector.set(token, current + positionWeight);
    });

    // Bigrams for context
    for (let i = 0; i < tokens.length - 1; i++) {
      const bigram = `${tokens[i]}_${tokens[i + 1]}`;
      vector.set(bigram, (vector.get(bigram) || 0) + 1.5);
    }

    return vector;
  }

  cosineSimilarity(vecA, vecB) {
    let dotProduct = 0;
    let normA = 0;
    let normB = 0;

    // Calculate dot product
    vecA.forEach((val, key) => {
      normA += val * val;
      if (vecB.has(key)) {
        dotProduct += val * vecB.get(key);
      }
    });

    vecB.forEach(val => {
      normB += val * val;
    });

    if (normA === 0 || normB === 0) return 0;
    return dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
  }

  keywordScore(query, text) {
    const queryTokens = this.tokenize(query);
    const textTokens = this.tokenize(text);
    const textSet = new Set(textTokens);

    let matches = 0;
    let totalScore = 0;

    queryTokens.forEach(token => {
      if (textSet.has(token)) {
        matches++;
        // Bonus for exact word boundary matches
        const regex = new RegExp(`\\b${token}\\b`, 'i');
        if (regex.test(text)) {
          totalScore += 2;
        } else {
          totalScore += 1;
        }
      }
    });

    return queryTokens.length > 0 ? totalScore / queryTokens.length : 0;
  }

  indexDocument(filePath) {
    const content = fs.readFileSync(filePath, 'utf8');
    const stats = fs.statSync(filePath);

    const doc = {
      id: path.basename(filePath),
      path: filePath,
      content: content.substring(0, 10000), // Limit stored content
      vector: this.vectorize(content),
      wordCount: content.split(/\s+/).length,
      modified: stats.mtime.toISOString(),
    };

    return doc;
  }

  indexDirectory(dirPath) {
    const files = this.getAllFiles(dirPath);
    this.documents = [];

    files.forEach(file => {
      try {
        if (file.endsWith('.md') || file.endsWith('.txt') || file.endsWith('.js') || 
            file.endsWith('.json') || file.endsWith('.ts')) {
          const doc = this.indexDocument(file);
          this.documents.push(doc);
        }
      } catch (e) {
        console.error(`Error indexing ${file}: ${e.message}`);
      }
    });

    return this.documents.length;
  }

  getAllFiles(dir, files = []) {
    const entries = fs.readdirSync(dir, { withFileTypes: true });

    entries.forEach(entry => {
      const fullPath = path.join(dir, entry.name);
      if (entry.isDirectory() && entry.name !== 'node_modules' && entry.name !== '.git') {
        this.getAllFiles(fullPath, files);
      } else if (entry.isFile()) {
        files.push(fullPath);
      }
    });

    return files;
  }

  search(query, options = {}) {
    const { 
      topK = 5, 
      hybrid = true, 
      keywordWeight = 0.3, 
      semanticWeight = 0.7,
      threshold = 0.1 
    } = options;

    const queryVector = this.vectorize(query);
    const results = [];

    this.documents.forEach(doc => {
      let score = 0;

      if (hybrid) {
        const semanticScore = this.cosineSimilarity(queryVector, doc.vector);
        const kwScore = this.keywordScore(query, doc.content);
        score = (semanticScore * semanticWeight) + (kwScore * keywordWeight);
      } else {
        score = this.cosineSimilarity(queryVector, doc.vector);
      }

      if (score >= threshold) {
        results.push({
          document: doc,
          score: Math.round(score * 1000) / 1000,
        });
      }
    });

    results.sort((a, b) => b.score - a.score);
    return results.slice(0, topK);
  }

  saveIndex(outputPath) {
    const data = {
      version: '1.0.0',
      created: new Date().toISOString(),
      documentCount: this.documents.length,
      documents: this.documents.map(d => ({
        ...d,
        vector: Array.from(d.vector.entries()), // Convert Map to array for JSON
      })),
    };
    fs.writeFileSync(outputPath, JSON.stringify(data, null, 2));
  }

  loadIndex(indexPath) {
    const data = JSON.parse(fs.readFileSync(indexPath, 'utf8'));
    this.documents = data.documents.map(d => ({
      ...d,
      vector: new Map(d.vector),
    }));
    return this.documents.length;
  }

  printResults(results, query) {
    const colors = {
      reset: '\x1b[0m',
      cyan: '\x1b[36m',
      green: '\x1b[32m',
      yellow: '\x1b[33m',
      gray: '\x1b[90m',
    };

    console.log(`\n${colors.cyan}Search Results for: "${query}"${colors.reset}\n`);

    if (results.length === 0) {
      console.log(`${colors.yellow}No results found${colors.reset}`);
      return;
    }

    results.forEach((result, idx) => {
      console.log(`${colors.cyan}[${idx + 1}]${colors.reset} ${colors.green}${result.document.id}${colors.reset}`);
      console.log(`${colors.gray}Score: ${result.score} | Words: ${result.document.wordCount}${colors.reset}`);

      // Show snippet
      const content = result.document.content.substring(0, 200);
      console.log(`${colors.gray}${content}...${colors.reset}`);
      console.log();
    });
  }

  run() {
    const args = process.argv.slice(2);
    const command = args[0];

    if (!command || command === '--help') {
      console.log('Embedding Search - Semantic document search');
      console.log('');
      console.log('Commands:');
      console.log('  index <directory>              Index all documents in directory');
      console.log('  search <query>                Search indexed documents');
      console.log('  add <file>                   Add single file to index');
      console.log('');
      console.log('Options:');
      console.log('  --output, -o FILE            Output file for index');
      console.log('  --index FILE                 Load existing index');
      console.log('  --top-k N                    Number of results (default: 5)');
      console.log('  --hybrid                     Use hybrid search (default: true)');
      console.log('  --keyword-weight W           Weight for keyword matching (0-1)');
      console.log('  --semantic-weight W          Weight for semantic matching (0-1)');
      console.log('  --threshold T                Minimum score threshold');
      console.log('');
      console.log('Examples:');
      console.log('  embed.js index ./docs --output index.json');
      console.log('  embed.js search "machine learning" --index index.json');
      console.log('  embed.js search "deployment" --hybrid --top-k 10');
      process.exit(0);
    }

    // Parse options
    let outputPath = null;
    let indexPath = null;
    let topK = 5;
    let hybrid = true;
    let keywordWeight = 0.3;
    let semanticWeight = 0.7;
    let threshold = 0.1;

    for (let i = 1; i < args.length; i++) {
      if (args[i] === '--output' || args[i] === '-o') outputPath = args[++i];
      else if (args[i] === '--index') indexPath = args[++i];
      else if (args[i] === '--top-k') topK = parseInt(args[++i]) || 5;
      else if (args[i] === '--hybrid') hybrid = true;
      else if (args[i] === '--no-hybrid') hybrid = false;
      else if (args[i] === '--keyword-weight') keywordWeight = parseFloat(args[++i]) || 0.3;
      else if (args[i] === '--semantic-weight') semanticWeight = parseFloat(args[++i]) || 0.7;
      else if (args[i] === '--threshold') threshold = parseFloat(args[++i]) || 0.1;
    }

    switch (command) {
      case 'index': {
        const dirPath = args[1];
        if (!dirPath) {
          console.error('Please provide a directory path');
          process.exit(1);
        }

        console.log(`Indexing directory: ${dirPath}`);
        const count = this.indexDirectory(dirPath);

        const outFile = outputPath || path.join(dirPath, 'embeddings-index.json');
        this.saveIndex(outFile);
        console.log(`\nIndexed ${count} documents`);
        console.log(`Index saved to: ${outFile}`);
        break;
      }

      case 'search': {
        const query = args[1];
        if (!query) {
          console.error('Please provide a search query');
          process.exit(1);
        }

        if (!indexPath) {
          console.error('Please provide --index FILE');
          process.exit(1);
        }

        this.loadIndex(indexPath);
        const results = this.search(query, { topK, hybrid, keywordWeight, semanticWeight, threshold });
        this.printResults(results, query);
        break;
      }

      case 'add': {
        const filePath = args[1];
        if (!filePath) {
          console.error('Please provide a file path');
          process.exit(1);
        }

        if (indexPath && fs.existsSync(indexPath)) {
          this.loadIndex(indexPath);
        }

        const doc = this.indexDocument(filePath);
        this.documents.push(doc);

        const outFile = indexPath || 'embeddings-index.json';
        this.saveIndex(outFile);
        console.log(`Added: ${doc.id} (total: ${this.documents.length})`);
        break;
      }

      default:
        console.error(`Unknown command: ${command}`);
        process.exit(1);
    }
  }
}

// Run if called directly
if (require.main === module) {
  const search = new EmbeddingSearch();
  search.run();
}

module.exports = { EmbeddingSearch };

#!/usr/bin/env node
/**
 * Session Manager - Multi-session context management
 * Inspired by OpenClaw's session tools
 */

const fs = require('fs');
const path = require('path');

class SessionManager {
  constructor() {
    this.sessionsDir = path.join('/job', 'tmp', '.sessions');
    this.ensureSessionsDir();
    this.currentSessionFile = path.join(this.sessionsDir, '.current');
  }

  ensureSessionsDir() {
    if (!fs.existsSync(this.sessionsDir)) {
      fs.mkdirSync(this.sessionsDir, { recursive: true });
    }
  }

  getSessionPath(name) {
    const safeName = name.replace(/[^a-zA-Z0-9-_]/g, '-');
    return path.join(this.sessionsDir, `${safeName}.json`);
  }

  getCurrentSession() {
    if (!fs.existsSync(this.currentSessionFile)) return null;
    return fs.readFileSync(this.currentSessionFile, 'utf8').trim();
  }

  setCurrentSession(name) {
    fs.writeFileSync(this.currentSessionFile, name);
  }

  listSessions() {
    const files = fs.readdirSync(this.sessionsDir)
      .filter(f => f.endsWith('.json') && f !== '.current');

    const current = this.getCurrentSession();
    const sessions = [];

    files.forEach(file => {
      try {
        const data = JSON.parse(fs.readFileSync(path.join(this.sessionsDir, file), 'utf8'));
        sessions.push({
          name: data.name,
          description: data.description,
          created: data.created,
          updated: data.updated,
          active: data.name === current,
          tasks: data.tasks?.length || 0,
        });
      } catch (e) {
        // Ignore invalid session files
      }
    });

    return sessions.sort((a, b) => new Date(b.updated) - new Date(a.updated));
  }

  createSession(name, description = '', metadata = {}) {
    const sessionPath = this.getSessionPath(name);

    if (fs.existsSync(sessionPath)) {
      throw new Error(`Session "${name}" already exists`);
    }

    const session = {
      name,
      description,
      created: new Date().toISOString(),
      updated: new Date().toISOString(),
      metadata,
      tasks: [],
      notes: [],
      state: {},
    };

    fs.writeFileSync(sessionPath, JSON.stringify(session, null, 2));
    this.setCurrentSession(name);

    return session;
  }

  getSession(name) {
    const sessionPath = this.getSessionPath(name);

    if (!fs.existsSync(sessionPath)) {
      return null;
    }

    return JSON.parse(fs.readFileSync(sessionPath, 'utf8'));
  }

  updateSession(name, updates) {
    const session = this.getSession(name);

    if (!session) {
      throw new Error(`Session "${name}" not found`);
    }

    Object.assign(session, updates);
    session.updated = new Date().toISOString();

    fs.writeFileSync(this.getSessionPath(name), JSON.stringify(session, null, 2));
    return session;
  }

  deleteSession(name) {
    const sessionPath = this.getSessionPath(name);

    if (!fs.existsSync(sessionPath)) {
      throw new Error(`Session "${name}" not found`);
    }

    fs.unlinkSync(sessionPath);

    const current = this.getCurrentSession();
    if (current === name) {
      fs.unlinkSync(this.currentSessionFile);
    }
  }

  addTask(sessionName, task) {
    const session = this.getSession(sessionName);

    if (!session) {
      throw new Error(`Session "${sessionName}" not found`);
    }

    session.tasks.push({
      id: Date.now().toString(),
      description: task,
      status: 'pending',
      created: new Date().toISOString(),
    });

    this.updateSession(sessionName, { tasks: session.tasks });
  }

  addNote(sessionName, note) {
    const session = this.getSession(sessionName);

    if (!session) {
      throw new Error(`Session "${sessionName}" not found`);
    }

    session.notes.push({
      content: note,
      timestamp: new Date().toISOString(),
    });

    this.updateSession(sessionName, { notes: session.notes });
  }

  listHistory(limit = 10) {
    const sessions = this.listSessions();
    return sessions.slice(0, limit);
  }

  formatTimeDiff(dateStr) {
    const date = new Date(dateStr);
    const now = new Date();
    const diffMs = now - date;
    const diffMins = Math.floor(diffMs / 60000);
    const diffHours = Math.floor(diffMs / 3600000);
    const diffDays = Math.floor(diffMs / 86400000);

    if (diffMins < 1) return 'just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;
    if (diffDays < 7) return `${diffDays}d ago`;
    return date.toLocaleDateString();
  }

  run() {
    const args = process.argv.slice(2);
    const command = args[0];

    const colors = {
      reset: '\x1b[0m',
      cyan: '\x1b[36m',
      green: '\x1b[32m',
      yellow: '\x1b[33m',
      gray: '\x1b[90m',
      bold: '\x1b[1m',
    };

    switch (command) {
      case 'list':
      case 'ls': {
        const sessions = this.listSessions();

        console.log(`\n${colors.cyan}Active Sessions:${colors.reset}\n`);

        if (sessions.length === 0) {
          console.log(`${colors.gray}No sessions found${colors.reset}`);
          break;
        }

        sessions.forEach(s => {
          const marker = s.active ? `${colors.green}●${colors.reset}` : `${colors.gray}○${colors.reset}`;
          const name = s.active ? `${colors.bold}${s.name}${colors.reset}` : s.name;
          const time = this.formatTimeDiff(s.updated);
          const tasks = s.tasks > 0 ? `${colors.yellow}(${s.tasks} tasks)${colors.reset}` : '';

          console.log(`${marker} ${name} ${colors.gray}${time}${colors.reset} ${tasks}`);
          if (s.description) {
            console.log(`  ${colors.gray}${s.description}${colors.reset}`);
          }
        });

        console.log(`\n${sessions.length} session(s) total`);
        break;
      }

      case 'create':
      case 'new': {
        const name = args[1];
        const description = args[2] || '';

        if (!name) {
          console.error('Usage: session.js create <name> [description]');
          process.exit(1);
        }

        try {
          const session = this.createSession(name, description);
          console.log(`${colors.green}✓ Created session "${session.name}"${colors.reset}`);
          console.log(`${colors.cyan}Switched to new session${colors.reset}`);
        } catch (e) {
          console.error(`${colors.yellow}Error: ${e.message}${colors.reset}`);
          process.exit(1);
        }
        break;
      }

      case 'switch':
      case 'use': {
        const name = args[1];

        if (!name) {
          console.error('Usage: session.js switch <name>');
          process.exit(1);
        }

        const session = this.getSession(name);
        if (!session) {
          console.error(`Session "${name}" not found`);
          process.exit(1);
        }

        this.setCurrentSession(name);
        console.log(`${colors.green}✓ Switched to session "${name}"${colors.reset}`);
        break;
      }

      case 'info': {
        const name = args[1] || this.getCurrentSession();

        if (!name) {
          console.error('No active session. Specify a session name or switch to one.');
          process.exit(1);
        }

        const session = this.getSession(name);
        if (!session) {
          console.error(`Session "${name}" not found`);
          process.exit(1);
        }

        console.log(`\n${colors.cyan}Session: ${colors.bold}${session.name}${colors.reset}`);
        console.log(`${colors.gray}${'─'.repeat(40)}${colors.reset}`);
        console.log(`Description: ${session.description || colors.gray + 'None' + colors.reset}`);
        console.log(`Created: ${session.created}`);
        console.log(`Updated: ${session.updated}`);
        console.log(`Tasks: ${session.tasks?.length || 0}`);
        console.log(`Notes: ${session.notes?.length || 0}`);

        if (session.tasks?.length > 0) {
          console.log(`\n${colors.cyan}Tasks:${colors.reset}`);
          session.tasks.forEach(t => {
            const status = t.status === 'done' ? `${colors.green}✓${colors.reset}` : `${colors.yellow}○${colors.reset}`;
            console.log(`  ${status} ${t.description}`);
          });
        }
        break;
      }

      case 'delete':
      case 'rm': {
        const name = args[1];

        if (!name) {
          console.error('Usage: session.js delete <name>');
          process.exit(1);
        }

        try {
          this.deleteSession(name);
          console.log(`${colors.green}✓ Deleted session "${name}"${colors.reset}`);
        } catch (e) {
          console.error(`${colors.yellow}Error: ${e.message}${colors.reset}`);
          process.exit(1);
        }
        break;
      }

      case 'task': {
        const subCmd = args[1];
        const sessionName = this.getCurrentSession();

        if (!sessionName) {
          console.error('No active session. Switch to a session first.');
          process.exit(1);
        }

        if (subCmd === 'add') {
          const description = args.slice(2).join(' ');
          if (!description) {
            console.error('Usage: session.js task add <description>');
            process.exit(1);
          }

          this.addTask(sessionName, description);
          console.log(`${colors.green}✓ Added task to "${sessionName}"${colors.reset}`);
        } else {
          // List tasks
          const session = this.getSession(sessionName);
          if (session.tasks?.length === 0) {
            console.log(`${colors.gray}No tasks in current session${colors.reset}`);
          } else {
            console.log(`\n${colors.cyan}Tasks for "${sessionName}":${colors.reset}`);
            session.tasks.forEach(t => {
              const status = t.status === 'done' ? `${colors.green}✓${colors.reset}` : `${colors.yellow}○${colors.reset}`;
              console.log(`  ${status} ${t.description}`);
            });
          }
        }
        break;
      }

      case 'note': {
        const content = args.slice(1).join(' ');
        const sessionName = this.getCurrentSession();

        if (!content) {
          console.error('Usage: session.js note <content>');
          process.exit(1);
        }

        this.addNote(sessionName, content);
        console.log(`${colors.green}✓ Added note to "${sessionName}"${colors.reset}`);
        break;
      }

      case 'history':
      case 'log': {
        const limit = parseInt(args[1]) || 10;
        const history = this.listHistory(limit);

        console.log(`\n${colors.cyan}Session Activity${colors.reset}\n`);

        history.forEach(s => {
          const active = s.active ? `${colors.green}[ACTIVE]${colors.reset}` : '';
          console.log(`${this.formatTimeDiff(s.updated)} ${s.name} ${active}`);
        });
        break;
      }

      case 'current': {
        const current = this.getCurrentSession();
        if (current) {
          console.log(current);
        } else {
          console.log('No active session');
        }
        break;
      }

      default: {
        console.log('Session Manager - Multi-session task coordination');
        console.log('');
        console.log('Commands:');
        console.log('  list, ls              List all sessions');
        console.log('  create <name>          Create new session');
        console.log('  switch <name>          Switch to session');
        console.log('  info [name]            Show session details');
        console.log('  delete <name>          Delete session');
        console.log('  task add <desc>        Add task to current session');
        console.log('  note <content>         Add note to current session');
        console.log('  history [limit]        Show recent activity');
        console.log('  current                Show active session');
        console.log('');
      }
    }
  }
}

// Run if called directly
if (require.main === module) {
  const manager = new SessionManager();
  manager.run();
}

module.exports = { SessionManager };

#!/usr/bin/env node
/**
 * Chat Command Processor
 * Terminal chat-style command processing
 */

const colors = {
  reset: '\x1b[0m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  magenta: '\x1b[35m',
  cyan: '\x1b[36m',
  gray: '\x1b[90m',
  bold: '\x1b[1m',
};

class ChatCommandProcessor {
  constructor() {
    this.commands = new Map();
    this.registerDefaults();
  }

  registerDefaults() {
    // /status - System status
    this.register('status', {
      description: 'Show system status',
      usage: '/status',
      execute: () => {
        const now = new Date().toISOString();
        return {
          text: `${colors.green}● System Status${colors.reset}
${colors.gray}━━━━━━━━━━━━━━━━━━━━━━━━${colors.reset}
Time: ${now}
Status: ${colors.green}Operational${colors.reset}
Commands: ${this.commands.size} available
Use /help for command list`,
        };
      },
    });

    // /help - Help menu
    this.register('help', {
      description: 'Show help menu',
      usage: '/help [command]',
      execute: (args) => {
        if (args.length > 0) {
          const cmd = args[0].replace(/^\//, '');
          const cmdInfo = this.commands.get(cmd);
          if (cmdInfo) {
            return {
              text: `${colors.cyan}Command: /${cmd}${colors.reset}
${colors.gray}${'─'.repeat(30)}${colors.reset}
Description: ${cmdInfo.description}
Usage: ${cmdInfo.usage}`,
            };
          }
          return { text: `${colors.red}Unknown command: /${cmd}${colors.reset}
Use /help to see available commands.` };
        }

        let output = `${colors.cyan}Available Commands${colors.reset}\n${colors.gray}${'━'.repeat(30)}${colors.reset}\n`;
        this.commands.forEach((cmd, name) => {
          output += `${colors.green}/${name.padEnd(12)}${colors.reset} ${colors.gray}${cmd.description}${colors.reset}\n`;
        });
        output += `\n${colors.gray}Use /help <command> for detailed info${colors.reset}`;
        return { text: output };
      },
    });

    // /info - Version info
    this.register('info', {
      description: 'Show version information',
      usage: '/info',
      execute: () => ({
        text: `${colors.cyan}PopeBot Chat Command Processor${colors.reset}
Version: 1.0.0
Author: PopeBot

A terminal chat-style command system`,
      }),
    });

    // /ping - Test connectivity
    this.register('ping', {
      description: 'Test system responsiveness',
      usage: '/ping',
      execute: () => {
        const start = Date.now();
        const latency = Date.now() - start;
        return {
          text: `${colors.green}pong!${colors.reset} ${colors.gray}(~${latency}ms)${colors.reset}`,
        };
      },
    });

    // /echo - Echo text
    this.register('echo', {
      description: 'Echo text back',
      usage: '/echo <text>',
      execute: (args) => ({
        text: args.join(' ') || 'You said nothing!',
      }),
    });

    // /time - Current time
    this.register('time', {
      description: 'Show current time',
      usage: '/time',
      execute: () => ({
        text: `${colors.cyan}Current Time${colors.reset}\n${new Date().toLocaleString()}`,
      }),
    });

    // /joke - Random joke
    this.register('joke', {
      description: 'Tell a random joke',
      usage: '/joke',
      execute: () => {
        const jokes = [
          "Why do programmers prefer dark mode? Because light attracts bugs!",
          "Why did the AI go to school? To improve its learning rate!",
          "What's a computer's favorite snack? Microchips!",
          "Why do developers hate nature? Too many bugs!",
          "How many programmers does it take to change a light bulb? None, it's a hardware problem!",
        ];
        return { text: `${colors.yellow}😄 ${jokes[Math.floor(Math.random() * jokes.length)]}${colors.reset}` };
      },
    });

    // /clear - Clear screen ANSI
    this.register('clear', {
      description: 'Clear the terminal',
      usage: '/clear',
      execute: () => ({
        text: '\x1b[2J\x1b[H',
        raw: true,
      }),
    });

    // /roll - Roll dice
    this.register('roll', {
      description: 'Roll a dice (e.g., /roll 6)',
      usage: '/roll [sides]',
      execute: (args) => {
        const sides = parseInt(args[0]) || 6;
        const result = Math.floor(Math.random() * sides) + 1;
        return {
          text: `${colors.green}🎲 Rolled d${sides}: ${colors.bold}${result}${colors.reset}`,
        };
      },
    });

    // /coin - Flip coin
    this.register('coin', {
      description: 'Flip a coin',
      usage: '/coin',
      execute: () => ({
        text: `${colors.yellow}🪙 ${Math.random() > 0.5 ? 'Heads' : 'Tails'}${colors.reset}`,
      }),
    });

    // /quote - Random quote
    this.register('quote', {
      description: 'Show an inspiring quote',
      usage: '/quote',
      execute: () => {
        const quotes = [
          { text: "The only way to do great work is to love what you do.", author: "Steve Jobs" },
          { text: "Innovation distinguishes between a leader and a follower.", author: "Steve Jobs" },
          { text: "Code is like humor. When you have to explain it, it's bad.", author: "Cory House" },
          { text: "First, solve the problem. Then, write the code.", author: "John Johnson" },
          { text: "Simplicity is the soul of efficiency.", author: "Austin Freeman" },
        ];
        const q = quotes[Math.floor(Math.random() * quotes.length)];
        return {
          text: `${colors.cyan}"${q.text}"${colors.reset}\n${colors.gray}— ${q.author}${colors.reset}`,
        };
      },
    });
  }

  register(name, command) {
    this.commands.set(name, command);
  }

  unregister(name) {
    this.commands.delete(name);
  }

  parse(input) {
    // Strip leading whitespace
    const cleanInput = input.trim();

    // Must start with /
    if (!cleanInput.startsWith('/')) {
      return null;
    }

    // Parse command and args
    const parts = cleanInput.slice(1).split(/\s+/);
    const command = parts[0].toLowerCase();
    const args = parts.slice(1);

    return { command, args };
  }

  process(input) {
    const parsed = this.parse(input);

    if (!parsed) {
      return {
        success: false,
        text: `${colors.gray}Not a command (use /help for help)${colors.reset}`,
      };
    }

    const { command, args } = parsed;
    const cmd = this.commands.get(command);

    if (!cmd) {
      return {
        success: false,
        text: `${colors.red}Unknown command: /${command}${colors.reset}\nUse /help to see available commands.`,
      };
    }

    try {
      const result = cmd.execute(args);
      return { success: true, ...result };
    } catch (e) {
      return {
        success: false,
        text: `${colors.red}Error executing /${command}: ${e.message}${colors.reset}`,
      };
    }
  }

  run() {
    const args = process.argv.slice(2);
    const command = args[0];

    if (!command || command === '--help') {
      console.log('Chat Command Processor - Terminal chat-style commands');
      console.log('');
      console.log('Usage:');
      console.log('  cmd.js process "/command args"     Process a single command');
      console.log('  cmd.js --interactive              Run in interactive mode');
      console.log('  cmd.js --list                     List all commands');
      console.log('');
      console.log('Commands:');
      this.commands.forEach((cmd, name) => {
        console.log(`  /${name.padEnd(12)} ${cmd.description}`);
      });
      process.exit(0);
    }

    if (command === '--list') {
      console.log(`${colors.cyan}Available Commands${colors.reset}\n`);
      this.commands.forEach((cmd, name) => {
        console.log(`${colors.green}/${name}${colors.reset}`);
        console.log(`  ${cmd.description}`);
        console.log(`  Usage: ${cmd.usage}`);
        console.log();
      });
      process.exit(0);
    }

    if (command === '--interactive') {
      console.log(`${colors.cyan}🤖 PopeBot Chat Commands${colors.reset}`);
      console.log(`${colors.gray}Type /help for commands, /quit to exit${colors.reset}\n`);

      const readline = require('readline');
      const rl = readline.createInterface({
        input: process.stdin,
        output: process.stdout,
        prompt: `${colors.green}>${colors.reset} `,
      });

      rl.prompt();

      rl.on('line', (line) => {
        if (line.trim() === '/quit' || line.trim() === '/exit') {
          console.log(`${colors.gray}Goodbye! 👋${colors.reset}`);
          rl.close();
          return;
        }

        const result = this.process(line.trim());
        if (result.raw) {
          process.stdout.write(result.text);
        } else {
          console.log(result.text);
        }

        rl.prompt();
      });

      rl.on('close', () => {
        process.exit(0);
      });

      return;
    }

    if (command === 'process') {
      const input = args.slice(1).join(' ');
      if (!input) {
        console.error('Usage: cmd.js process "/command args"');
        process.exit(1);
      }

      const result = this.process(input);
      if (result.raw) {
        process.stdout.write(result.text);
      } else {
        console.log(result.text);
      }

      process.exit(result.success ? 0 : 1);
    }

    console.error(`Unknown subcommand: ${command}`);
    process.exit(1);
  }
}

if (require.main === module) {
  const processor = new ChatCommandProcessor();
  processor.run();
}

module.exports = { ChatCommandProcessor };

#!/usr/bin/env node
/**
 * Security Scanner - Vulnerability and secret detection
 */

const fs = require('fs');
const path = require('path');
const crypto = require('crypto');

// Security patterns
const PATTERNS = {
  secrets: [
    { name: 'API Key', regex: /['"`]?[aA][pP][iI][_-]?[kK][eE][yY]['"`]?\s*(=|:)\s*['"`][a-zA-Z0-9_\-]{16,}['"`]/g },
    { name: 'Secret Token', regex: /['"`]?[sS][eE][cC][rR][eE][tT]['"`]?\s*(=|:)\s*['"`][a-zA-Z0-9_\-]{16,}['"`]/g },
    { name: 'Private Key', regex: /-----BEGIN [A-Z]+ PRIVATE KEY-----/ },
    { name: 'AWS Key', regex: /AKIA[0-9A-Z]{16}/ },
    { name: 'GitHub Token', regex: /gh[pousr]_[A-Za-z0-9_]{36,}/ },
    { name: 'Password', regex: /password\s*(=|:)\s*['"`].{8,}['"`]/i },
    { name: 'Bearer Token', regex: /Bearer\s+[a-zA-Z0-9_\-\.]{20,}/ },
    { name: 'JWT Token', regex: /eyJ[A-Za-z0-9_\-]*\.eyJ[A-Za-z0-9_\-]*\.[A-Za-z0-9_\-]*/ },
  ],
  config: [
    { name: 'Debug Enabled', regex: /debug:\s*true/ },
    { name: 'CORS Wildcard', regex: /['"`]?\*['"`]?\s*in\s*cors|Access-Control-Allow-Origin:\s*\*/ },
    { name: 'Weak SSL', regex: /ssl:\s*false|verify:\s*false/ },
    { name: 'SQL Injection Risk', regex: /SELECT\s+.*FROM\s+.*\+.*\/\/\s*.*\+|['"`]?\$\{.*\}['"`]?.+query/i },
  ],
  permissions: [
    { type: 'file', check: (stat) => stat.mode & 0o044 }, // World readable
  ],
};

class SecurityScanner {
  constructor() {
    this.findings = [];
    this.scanned = 0;
  }

  addFinding(severity, category, file, line, message, details = '') {
    this.findings.push({
      severity,
      category,
      file,
      line: line || 0,
      message,
      details,
      timestamp: new Date().toISOString(),
    });
  }

  scanFile(filePath) {
    const content = fs.readFileSync(filePath, 'utf8');
    const lines = content.split('\n');
    this.scanned++;

    // Scan for secrets
    PATTERNS.secrets.forEach(pattern => {
      lines.forEach((line, idx) => {
        if (pattern.regex.test(line)) {
          const masked = line.replace(/['"`][a-zA-Z0-9_\-\.]{10,}['"`]/g, match => 
            match.charAt(0) + '*'.repeat(Math.min(match.length - 2, 20)) + match.charAt(match.length - 1));
          this.addFinding('HIGH', 'Secret', filePath, idx + 1, 
            `Potential ${pattern.name} found`, masked.trim().substring(0, 100));
        }
      });
    });

    // Scan for config issues
    PATTERNS.config.forEach(pattern => {
      lines.forEach((line, idx) => {
        if (pattern.regex.test(line)) {
          this.addFinding('MEDIUM', 'Config', filePath, idx + 1, 
            pattern.name, line.trim().substring(0, 100));
        }
      });
    });

    // Check file permissions
    try {
      const stat = fs.statSync(filePath);
      if (stat.mode & 0o022) {
        this.addFinding('LOW', 'Permission', filePath, null, 
          'File is world-writable', `Mode: ${(stat.mode & 0o777).toString(8)}`);
      }
    } catch (e) {
      // Ignore
    }
  }

  scanDirectory(dirPath, extensions = ['.js', '.ts', '.json', '.yml', '.yaml', '.md']) {
    const files = this.getFiles(dirPath, extensions);
    files.forEach(f => {
      try {
        this.scanFile(f);
      } catch (e) {
        this.addFinding('INFO', 'Error', f, null, `Could not scan: ${e.message}`);
      }
    });
  }

  getFiles(dir, extensions, files = []) {
    if (!fs.existsSync(dir)) return files;

    const entries = fs.readdirSync(dir, { withFileTypes: true });
    entries.forEach(entry => {
      const fullPath = path.join(dir, entry.name);
      if (entry.isDirectory() && !entry.name.startsWith('.') && 
          entry.name !== 'node_modules' && entry.name !== 'tmp') {
        this.getFiles(fullPath, extensions, files);
      } else if (entry.isFile() && extensions.some(ext => entry.name.endsWith(ext))) {
        files.push(fullPath);
      }
    });
    return files;
  }

  generateReport() {
    const high = this.findings.filter(f => f.severity === 'HIGH').length;
    const medium = this.findings.filter(f => f.severity === 'MEDIUM').length;
    const low = this.findings.filter(f => f.severity === 'LOW').length;

    return {
      timestamp: new Date().toISOString(),
      summary: { filesScanned: this.scanned, high, medium, low, total: this.findings.length },
      findings: this.findings,
    };
  }

  printReport(report) {
    const colors = {
      reset: '\x1b[0m',
      red: '\x1b[31m',
      yellow: '\x1b[33m',
      green: '\x1b[32m',
      cyan: '\x1b[36m',
      gray: '\x1b[90m',
    };

    console.log(`\n${colors.cyan}╔════════════════════════════════════════════════════════╗`);
    console.log(`║           Security Scanner 🛡️ v1.0.0                   ║`);
    console.log(`╚════════════════════════════════════════════════════════╝${colors.reset}\n`);

    console.log(`${colors.cyan}Summary${colors.reset}`);
    console.log(`${'─'.repeat(50)}`);
    console.log(`Files Scanned: ${report.summary.filesScanned}`);
    console.log(`${colors.red}High: ${report.summary.high}${colors.reset}`);
    console.log(`${colors.yellow}Medium: ${report.summary.medium}${colors.reset}`);
    console.log(`${colors.green}Low: ${report.summary.low}${colors.reset}`);
    console.log(`Total Issues: ${report.summary.total}\n`);

    if (report.findings.length === 0) {
      console.log(`${colors.green}✓ No security issues found${colors.reset}`);
      return;
    }

    const grouped = {};
    report.findings.forEach(f => {
      if (!grouped[f.severity]) grouped[f.severity] = [];
      grouped[f.severity].push(f);
    });

    ['HIGH', 'MEDIUM', 'LOW', 'INFO'].forEach(severity => {
      if (grouped[severity]) {
        const color = { HIGH: colors.red, MEDIUM: colors.yellow, LOW: colors.green, INFO: colors.gray }[severity];
        console.log(`\n${color}[${severity}]${colors.reset}`);

        grouped[severity].forEach(f => {
          console.log(`  ${colors.cyan}${f.category}${colors.reset} ${f.file}${f.line ? `:${f.line}` : ''}`);
          console.log(`    ${f.message}`);
          if (f.details) {
            console.log(`    ${colors.gray}${f.details}${colors.reset}`);
          }
        });
      }
    });
  }

  run() {
    const args = process.argv.slice(2);
    const command = args[0];

    if (!command || command === '--help') {
      console.log('Security Scanner - Vulnerability and secret detection');
      console.log('');
      console.log('Commands:');
      console.log('  scan [files...]         Scan specific files or directories');
      console.log('  check --secrets         Check for hardcoded secrets only');
      console.log('  report                  Generate security report');
      console.log('');
      console.log('Options:');
      console.log('  --full                  Full recursive scan of current directory');
      console.log('  --output FILE           Save report to file');
      process.exit(0);
    }

    if (command === 'scan') {
      const files = args.slice(1).filter(a => !a.startsWith('--'));

      if (args.includes('--full') || files.length === 0) {
        console.log('Scanning current directory recursively...');
        this.scanDirectory('.');
      } else {
        files.forEach(f => {
          if (fs.existsSync(f)) {
            console.log(`Scanning ${f}...`);
            this.scanFile(f);
          }
        });
      }

      const report = this.generateReport();
      this.printReport(report);

      if (args.includes('--output')) {
        const outputIdx = args.indexOf('--output');
        const outputFile = args[outputIdx + 1];
        fs.writeFileSync(outputFile, JSON.stringify(report, null, 2));
        console.log(`\nReport saved to: ${outputFile}`);
      }

      process.exit(report.summary.high > 0 ? 1 : 0);
    }

    if (command === 'check' && args.includes('--secrets')) {
      console.log('Checking for secrets in current directory...');
      this.scanDirectory('.');

      const secrets = this.findings.filter(f => f.category === 'Secret');
      if (secrets.length > 0) {
        console.log(`\nFound ${secrets.length} potential secrets`);
        secrets.forEach(s => console.log(`  ${s.file}:${s.line} - ${s.message}`));
        process.exit(1);
      } else {
        console.log('✓ No secrets found');
      }
    }

    console.error(`Unknown command: ${command}`);
    process.exit(1);
  }
}

if (require.main === module) {
  const scanner = new SecurityScanner();
  scanner.run();
}

module.exports = { SecurityScanner };

#!/usr/bin/env node
/**
 * Webhook Forwarder - Route webhooks to multiple destinations
 */

const http = require('http');
const https = require('https');
const fs = require('fs');
const url = require('url');

class WebhookForwarder {
  constructor() {
    this.destinations = [];
    this.retries = 3;
    this.timeout = 30000;
  }

  async forward(webhookUrl, payload, headers = {}) {
    const parsed = new URL(webhookUrl);
    const client = parsed.protocol === 'https:' ? https : http;

    const options = {
      hostname: parsed.hostname,
      port: parsed.port || (parsed.protocol === 'https:' ? 443 : 80),
      path: parsed.pathname + parsed.search,
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Content-Length': Buffer.byteLength(payload),
        'User-Agent': 'PopeBot-Webhook/1.0',
        ...headers,
      },
      timeout: this.timeout,
    };

    return new Promise((resolve, reject) => {
      const req = client.request(options, (res) => {
        let data = '';
        res.on('data', chunk => data += chunk);
        res.on('end', () => {
          resolve({
            statusCode: res.statusCode,
            headers: res.headers,
            body: data,
          });
        });
      });

      req.on('error', reject);
      req.on('timeout', () => {
        req.destroy();
        reject(new Error('Request timeout'));
      });

      req.write(payload);
      req.end();
    });
  }

  async forwardWithRetry(webhookUrl, payload, headers = {}) {
    let lastError;

    for (let i = 0; i < this.retries; i++) {
      try {
        return await this.forward(webhookUrl, payload, headers);
      } catch (error) {
        lastError = error;
        if (i < this.retries - 1) {
          await new Promise(r => setTimeout(r, 1000 * (i + 1))); // Exponential backoff
        }
      }
    }

    throw lastError;
  }

  async test(webhookUrl) {
    console.log(`Testing webhook: ${webhookUrl}`);

    try {
      const response = await this.forward(webhookUrl, JSON.stringify({
        event: 'test',
        timestamp: new Date().toISOString(),
        message: 'Test webhook from PopeBot',
      }));

      console.log(`Status: ${response.statusCode}`);
      return response.statusCode >= 200 && response.statusCode < 300;
    } catch (error) {
      console.error(`Failed: ${error.message}`);
      return false;
    }
  }

  async send(webhookUrl, payload, options = {}) {
    const payloadStr = typeof payload === 'string' ? payload : JSON.stringify(payload);
    const signature = options.signature ? this.sign(payloadStr, options.signature) : null;

    const headers = {};
    if (signature) {
      headers['X-Webhook-Signature'] = signature;
    }

    return await this.forwardWithRetry(webhookUrl, payloadStr, headers);
  }

  sign(payload, secret) {
    const crypto = require('crypto');
    return 'sha256=' + crypto.createHmac('sha256', secret).update(payload).digest('hex');
  }

  run() {
    const args = process.argv.slice(2);
    const command = args[0];

    if (!command || command === '--help') {
      console.log('Webhook Forwarder - Route webhooks to destinations');
      console.log('');
      console.log('Commands:');
      console.log('  send <url> --payload \'{"key":"value"}\'     Send webhook');
      console.log('  test <url>                                   Test webhook endpoint');
      console.log('  server --port 3000                          Start webhook server');
      console.log('');
      console.log('Options:');
      console.log('  --payload JSON                             Payload to send');
      console.log('  --signature SECRET                         Sign with HMAC-SHA256');
      console.log('  --retries N                                Number of retries (default: 3)');
      console.log('  --timeout MS                               Request timeout (default: 30000)');
      process.exit(0);
    }

    if (command === 'send') {
      const webhookUrl = args[1];
      const payloadIdx = args.indexOf('--payload');
      const payload = payloadIdx > -1 ? args[payloadIdx + 1] : '{"event":"test"}';
      const signatureIdx = args.indexOf('--signature');
      const signature = signatureIdx > -1 ? args[signatureIdx + 1] : null;

      this.send(webhookUrl, JSON.parse(payload), { signature }).then(response => {
        console.log(`Status: ${response.statusCode}`);
        console.log(`Response: ${response.body.substring(0, 200)}`);
      }).catch(err => {
        console.error(`Failed: ${err.message}`);
        process.exit(1);
      });
    }

    if (command === 'test') {
      const webhookUrl = args[1];
      this.test(webhookUrl).then(success => {
        process.exit(success ? 0 : 1);
      });
    }

    if (command === 'server') {
      const portIdx = args.indexOf('--port');
      const port = portIdx > -1 ? parseInt(args[portIdx + 1]) : 3000;

      const server = http.createServer((req, res) => {
        if (req.method === 'POST') {
          let body = '';
          req.on('data', chunk => body += chunk);
          req.on('end', () => {
            console.log(`Received: ${body.substring(0, 200)}`);
            res.writeHead(200, { 'Content-Type': 'application/json' });
            res.end(JSON.stringify({ received: true }));
          });
        } else {
          res.writeHead(200);
          res.end('Webhook Receiver Running');
        }
      });

      server.listen(port, () => {
        console.log(`Webhook receiver listening on port ${port}`);
      });
    }
  }
}

if (require.main === module) {
  const forwarder = new WebhookForwarder();
  forwarder.run();
}

module.exports = { WebhookForwarder };

#!/usr/bin/env node
/**
 * Metrics Dashboard - System metrics and statistics
 */

const fs = require('fs');
const os = require('os');
const path = require('path');

const colors = {
  reset: '\x1b[0m',
  cyan: '\x1b[36m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  red: '\x1b[31m',
  gray: '\x1b[90m',
  bold: '\x1b[1m',
};

class MetricsDashboard {
  constructor() {
    this.metricsDir = '/job/tmp/.metrics';
    this.ensureMetricsDir();
  }

  ensureMetricsDir() {
    if (!fs.existsSync(this.metricsDir)) {
      fs.mkdirSync(this.metricsDir, { recursive: true });
    }
  }

  getSystemMetrics() {
    const totalMem = os.totalmem();
    const freeMem = os.freemem();
    const usedMem = totalMem - freeMem;

    return {
      timestamp: new Date().toISOString(),
      cpu: {
        count: os.cpus().length,
        load: os.loadavg(),
      },
      memory: {
        total: this.formatBytes(totalMem),
        used: this.formatBytes(usedMem),
        free: this.formatBytes(freeMem),
        percent: Math.round((usedMem / totalMem) * 100),
      },
      uptime: this.formatUptime(os.uptime()),
      platform: `${os.platform()} ${os.arch()}`,
      hostname: os.hostname(),
    };
  }

  formatBytes(bytes) {
    const units = ['B', 'KB', 'MB', 'GB', 'TB'];
    let size = bytes;
    let unit = 0;
    while (size >= 1024 && unit < units.length - 1) {
      size /= 1024;
      unit++;
    }
    return `${size.toFixed(2)} ${units[unit]}`;
  }

  formatUptime(seconds) {
    const days = Math.floor(seconds / 86400);
    const hours = Math.floor((seconds % 86400) / 3600);
    const mins = Math.floor((seconds % 3600) / 60);

    if (days > 0) return `${days}d ${hours}h`;
    if (hours > 0) return `${hours}h ${mins}m`;
    return `${mins}m`;
  }

  drawBar(value, max, width = 20) {
    const filled = Math.round((value / max) * width);
    const empty = width - filled;
    const bar = '█'.repeat(filled) + '░'.repeat(empty);
    const color = value > 80 ? colors.red : value > 60 ? colors.yellow : colors.green;
    return `${color}${bar}${colors.reset}`;
  }

  printReport(metrics) {
    console.clear();
    console.log(`${colors.cyan}╔════════════════════════════════════════════════════════╗`);
    console.log(`║           PopeBot Metrics Dashboard                    ║`);
    console.log(`╚════════════════════════════════════════════════════════╝${colors.reset}\n`);

    console.log(`${colors.bold}System Information${colors.reset}`);
    console.log(`${'─'.repeat(50)}`);
    console.log(`Platform:  ${metrics.platform}`);
    console.log(`Hostname:  ${metrics.hostname}`);
    console.log(`Uptime:    ${metrics.uptime}`);

    console.log(`\n${colors.bold}CPU${colors.reset}`);
    console.log(`${'─'.repeat(50)}`);
    console.log(`Cores:     ${metrics.cpu.count}`);
    console.log(`Load:      ${metrics.cpu.load.map(l => l.toFixed(2)).join(', ')}`);

    console.log(`\n${colors.bold}Memory${colors.reset}`);
    console.log(`${'─'.repeat(50)}`);
    const memBar = this.drawBar(metrics.memory.percent, 100);
    console.log(`${memBar} ${metrics.memory.percent}%`);
    console.log(`  Total: ${metrics.memory.total}`);
    console.log(`  Used:  ${metrics.memory.used}`);
    console.log(`  Free:  ${metrics.memory.free}`);

    // Process info
    try {
      const memUsage = process.memoryUsage();
      console.log(`\n${colors.bold}Process${colors.reset}`);
      console.log(`${'─'.repeat(50)}`);
      console.log(`RSS:     ${this.formatBytes(memUsage.rss)}`);
      console.log(`Heap:    ${this.formatBytes(memUsage.heapUsed)} / ${this.formatBytes(memUsage.heapTotal)}`);
      console.log(`External: ${this.formatBytes(memUsage.external)}`);
    } catch (e) {
      // Ignore
    }

    console.log(`\n${colors.gray}Generated: ${metrics.timestamp}${colors.reset}`);
  }

  async live() {
    const readline = require('readline');
    readline.emitKeypressEvents(process.stdin);
    process.stdin.setRawMode(true);

    let running = true;

    process.stdin.on('keypress', (str, key) => {
      if (key?.name === 'q' || (key?.ctrl && key?.name === 'c')) {
        running = false;
        process.stdin.setRawMode(false);
        process.stdin.pause();
        console.log('\n' + colors.green + 'Exiting...' + colors.reset);
        process.exit(0);
      }
    });

    console.log(`${colors.gray}Press 'q' to quit, 'c' to clear${colors.reset}\n`);

    while (running) {
      const metrics = this.getSystemMetrics();
      this.printReport(metrics);
      await new Promise(resolve => setTimeout(resolve, 2000));
    }
  }

  export(format = 'json') {
    const metrics = this.getSystemMetrics();

    if (format === 'json') {
      return JSON.stringify(metrics, null, 2);
    }

    if (format === 'csv') {
      return [
        'timestamp,hostname,platform,cpu_count,cpu_load_1m,memory_total,memory_percent',
        `${metrics.timestamp},${metrics.hostname},${metrics.platform},${metrics.cpu.count},${metrics.cpu.load[0]},${metrics.memory.total},${metrics.memory.percent}`,
      ].join('\n');
    }

    return JSON.stringify(metrics);
  }

  run() {
    const args = process.argv.slice(2);
    const command = args[0];

    if (!command || command === '--help') {
      console.log('Metrics Dashboard - System metrics and statistics');
      console.log('');
      console.log('Commands:');
      console.log('  report                           Show metrics report');
      console.log('  live                             Show live updating dashboard (q to quit)');
      console.log('  export --format [json|csv]       Export metrics');
      console.log('  help                             Show this help');
      process.exit(0);
    }

    if (command === 'report') {
      const metrics = this.getSystemMetrics();
      this.printReport(metrics);
    }

    if (command === 'live') {
      this.live();
    }

    if (command === 'export') {
      const formatIdx = args.indexOf('--format');
      const format = formatIdx > -1 ? args[formatIdx + 1] : 'json';
      const output = this.export(format);

      const outputIdx = args.indexOf('--output');
      if (outputIdx > -1) {
        fs.writeFileSync(args[outputIdx + 1], output);
        console.log('Exported successfully');
      } else {
        console.log(output);
      }
    }
  }
}

if (require.main === module) {
  const metrics = new MetricsDashboard();
  metrics.run();
}

module.exports = { MetricsDashboard };

#!/usr/bin/env node
/**
 * Todo Manager - Task management system
 */

const fs = require('fs');
const path = require('path');

const colors = {
  reset: '\x1b[0m',
  red: '\x1b[31m',
  green: '\x1b[32m',
  yellow: '\x1b[33m',
  blue: '\x1b[34m',
  cyan: '\x1b[36m',
  gray: '\x1b[90m',
  bold: '\x1b[1m',
};

const PRIORITY_EMOJI = {
  high: '🔴',
  medium: '🟡',
  low: '🟢',
};

const STATUS_EMOJI = {
  pending: '○',
  doing: '◐',
  done: '✓',
};

class TodoManager {
  constructor() {
    this.todosFile = '/job/tmp/.todos.json';
    this.load();
  }

  load() {
    if (fs.existsSync(this.todosFile)) {
      this.todos = JSON.parse(fs.readFileSync(this.todosFile, 'utf8'));
    } else {
      this.todos = [];
    }
  }

  save() {
    fs.writeFileSync(this.todosFile, JSON.stringify(this.todos, null, 2));
  }

  add(description, options = {}) {
    const todo = {
      id: Date.now(),
      description,
      priority: options.priority || 'medium',
      category: options.category || 'default',
      due: options.due || null,
      status: 'pending',
      created: new Date().toISOString(),
      completed: null,
    };

    this.todos.push(todo);
    this.save();
    return todo;
  }

  list(options = {}) {
    let filtered = this.todos;

    if (options.status) {
      filtered = filtered.filter(t => t.status === options.status);
    }
    if (options.priority) {
      filtered = filtered.filter(t => t.priority === options.priority);
    }
    if (options.category) {
      filtered = filtered.filter(t => t.category === options.category);
    }

    // Sort by priority then due date
    const priorityOrder = { high: 3, medium: 2, low: 1 };
    filtered.sort((a, b) => {
      const pDiff = priorityOrder[b.priority] - priorityOrder[a.priority];
      if (pDiff !== 0) return pDiff;
      if (a.due && b.due) return new Date(a.due) - new Date(b.due);
      return 0;
    });

    return filtered;
  }

  complete(id) {
    const todo = this.todos.find(t => t.id.toString() === id.toString());
    if (todo) {
      todo.status = 'done';
      todo.completed = new Date().toISOString();
      this.save();
      return todo;
    }
    return null;
  }

  delete(id) {
    const index = this.todos.findIndex(t => t.id.toString() === id.toString());
    if (index > -1) {
      const todo = this.todos[index];
      this.todos.splice(index, 1);
      this.save();
      return todo;
    }
    return null;
  }

  printTodos(todos) {
    if (todos.length === 0) {
      console.log(`${colors.gray}No todos found${colors.reset}`);
      return;
    }

    console.log(`${colors.cyan}Todos${colors.reset}\n`);

    const pending = todos.filter(t => t.status === 'pending');
    const doing = todos.filter(t => t.status === 'doing');
    const done = todos.filter(t => t.status === 'done');

    if (pending.length > 0) {
      console.log(`${colors.yellow}Pending:${colors.reset}`);
      pending.forEach(t => this.printTodo(t));
    }

    if (doing.length > 0) {
      console.log(`\n${colors.blue}In Progress:${colors.reset}`);
      doing.forEach(t => this.printTodo(t));
    }

    if (done.length > 0) {
      console.log(`\n${colors.green}Completed:${colors.reset}`);
      done.forEach(t => this.printTodo(t));
    }

    console.log(`\n${colors.gray}Total: ${todos.length} (${pending.length} pending, ${doing.length} doing, ${done.length} done)${colors.reset}`);
  }

  printTodo(todo) {
    const priorityColor = {
      high: colors.red,
      medium: colors.yellow,
      low: colors.green,
    }[todo.priority];

    const statusColor = todo.status === 'done' ? colors.green : colors.gray;
    const dueStr = todo.due ? ` (${todo.due})` : '';

    console.log(`  ${statusColor}${STATUS_EMOJI[todo.status]}${colors.reset} ${priorityColor}[${todo.priority.charAt(0).toUpperCase()}]${colors.reset} ${todo.description}${colors.gray}${dueStr}${colors.reset} ${colors.cyan}#${todo.id}${colors.reset}`);
  }

  run() {
    const args = process.argv.slice(2);
    const command = args[0];

    if (!command || command === '--help') {
      console.log('Todo Manager - Task management system');
      console.log('');
      console.log('Commands:');
      console.log('  add <description>              Add new todo');
      console.log('  list [--filter]                List todos');
      console.log('  complete <id>                 Mark todo as complete');
      console.log('  delete <id>                   Delete todo');
      console.log('  complete-all                   Complete all pending todos');
      console.log('');
      console.log('Options:');
      console.log('  --priority high|medium|low     Set priority');
      console.log('  --category NAME               Set category');
      console.log('  --due YYYY-MM-DD              Set due date');
      console.log('  --status pending|doing|done   Filter by status');
      process.exit(0);
    }

    const getOption = (name) => {
      const idx = args.indexOf(name);
      return idx > -1 ? args[idx + 1] : null;
    };

    switch (command) {
      case 'add': {
        const description = args.slice(1).filter(a => !a.startsWith('--')).join(' ');
        if (!description) {
          console.error('Usage: todo.js add <description> [--options]');
          process.exit(1);
        }

        const options = {
          priority: getOption('--priority'),
          category: getOption('--category'),
          due: getOption('--due'),
        };

        const todo = this.add(description, options);
        console.log(`${colors.green}✓ Added todo #${todo.id}${colors.reset}`);
        break;
      }

      case 'list':
      case 'ls': {
        const options = {};
        if (args.includes('--status')) options.status = getOption('--status');
        if (args.includes('--priority')) options.priority = getOption('--priority');
        if (args.includes('--category')) options.category = getOption('--category');

        const todos = this.list(options);
        this.printTodos(todos);
        break;
      }

      case 'complete':
      case 'done': {
        const id = args[1];
        if (!id) {
          console.error('Usage: todo.js complete <id>');
          process.exit(1);
        }

        const todo = this.complete(id);
        if (todo) {
          console.log(`${colors.green}✓ Completed "${todo.description}"${colors.reset}`);
        } else {
          console.error(`${colors.red}Todo not found${colors.reset}`);
          process.exit(1);
        }
        break;
      }

      case 'delete':
      case 'rm': {
        const id = args[1];
        if (!id) {
          console.error('Usage: todo.js delete <id>');
          process.exit(1);
        }

        const todo = this.delete(id);
        if (todo) {
          console.log(`${colors.yellow}Deleted "${todo.description}"${colors.reset}`);
        } else {
          console.error(`${colors.red}Todo not found${colors.reset}`);
          process.exit(1);
        }
        break;
      }

      case 'complete-all': {
        const pending = this.todos.filter(t => t.status === 'pending');
        pending.forEach(t => {
          t.status = 'done';
          t.completed = new Date().toISOString();
        });
        this.save();
        console.log(`${colors.green}✓ Completed ${pending.length} todo(s)${colors.reset}`);
        break;
      }

      default:
        console.error(`Unknown command: ${command}`);
        process.exit(1);
    }
  }
}

if (require.main === module) {
  const manager = new TodoManager();
  manager.run();
}

module.exports = { TodoManager };
