const fs = require('fs');

// Create a 2x2 red PNG
const pngSig = Buffer.from([0x89, 0x50, 0x4E, 0x47, 0x0D, 0x0A, 0x1A, 0x0A]);

// IHDR chunk
const ihdrData = Buffer.from([
  0x00, 0x00, 0x00, 0x02,  // width: 2
  0x00, 0x00, 0x00, 0x02,  // height: 2
  0x08,                     // bit depth: 8
  0x02,                     // color type: RGB
  0x00,                     // compression: deflate
  0x00,                     // filter: adaptive
  0x00                      // interlace: none
]);

const ihdrType = Buffer.from('IHDR');
const ihdrCrc = Buffer.alloc(4); // Simplified, real CRC needed
const ihdrLen = Buffer.alloc(4);
ihdrLen.writeUInt32BE(ihdrData.length, 0);
const ihdr = Buffer.concat([ihdrLen, ihdrType, ihdrData, ihdrCrc]);

// Minimal IDAT with zlib compressed 2x2 red image
const idatData = Buffer.from([
  0x78, 0x9C,  // zlib header
  0x63, 0x60, 0x60, 0x60, 0x00, 0x00, 0x00, 0x04, 0x00, 0x01  // compressed data
]);
const idatType = Buffer.from('IDAT');
const idatCrc = Buffer.alloc(4);
const idatLen = Buffer.alloc(4);
idatLen.writeUInt32BE(idatData.length, 0);
