#!/usr/bin/env node
/**
 * Glob Finder - Pattern-based file search
 */
const fs = require('fs');
const path = require('path');

function parseArgs(args) {
  const result = {
    patterns: [],
    excludes: [],
    type: null, // 'f' for files, 'd' for directories
    details: false,
    limit: null,
    minSize: null,
    format: 'text'
  };

  for (let i = 0; i < args.length; i++) {
    if (args[i] === '--exclude') {
      result.excludes.push(args[++i]);
    } else if (args[i] === '--type') {
      result.type = args[++i];
    } else if (args[i] === '--details') {
      result.details = true;
    } else if (args[i] === '--limit') {
      result.limit = parseInt(args[++i]);
    } else if (args[i] === '--min-size') {
      const size = args[++i];
      if (size.endsWith('MB')) result.minSize = parseInt(size) * 1024 * 1024;
      else if (size.endsWith('KB')) result.minSize = parseInt(size) * 1024;
      else if (size.endsWith('GB')) result.minSize = parseInt(size) * 1024 * 1024 * 1024;
      else result.minSize = parseInt(size);
    } else if (args[i] === '--json') {
      result.format = 'json';
    } else if (!args[i].startsWith('--')) {
      result.patterns.push(args[i]);
    }
  }
  return result;
}

function globToRegex(pattern) {
  let regex = '';
  let i = 0;

  while (i < pattern.length) {
    const c = pattern[i];

    if (c === '**') {
      // Handle **
      if (pattern[i + 1] === '/') {
        regex += '(?:.*/)?';
        i += 2;
      } else {
        regex += '.*';
        i++;
      }
    } else if (c === '*') {
      regex += '[^/]*';
      i++;
    } else if (c === '?') {
      regex += '[^/]';
      i++;
    } else if (c === '[' && pattern[i + 1] === '!') {
      // Negated character class [!<chars>]
      const endIdx = pattern.indexOf(']', i);
      if (endIdx !== -1) {
        regex += `[^${pattern.slice(i + 2, endIdx)}]`;
        i = endIdx + 1;
      } else {
        regex += '\\[';
        i++;
      }
    } else if (c === '[') {
      // Character class [<chars>]
      const endIdx = pattern.indexOf(']', i);
      if (endIdx !== -1) {
        regex += `[${pattern.slice(i + 1, endIdx)}]`;
        i = endIdx + 1;
      } else {
        regex += '\\[';
        i++;
      }
    } else if (c === '{') {
      // Brace expansion {a,b,c}
      const endIdx = pattern.indexOf('}', i);
      if (endIdx !== -1) {
        const options = pattern.slice(i + 1, endIdx).split(',');
        regex += `(?:${options.map(escapeRegex).join('|')})`;
        i = endIdx + 1;
      } else {
        regex += '\\{';
        i++;
      }
    } else if (/[.+^${}()|\\]/.test(c)) {
      regex += '\\' + c;
      i++;
    } else {
      regex += escapeRegex(c);
      i++;
    }
  }

  return new RegExp('^' + regex + '$');
}

function escapeRegex(str) {
  return str.replace(/[.+^${}()|[\]\\]/g, '\\$\u0026');
}

function shouldExclude(filePath, excludes) {
  const fileName = path.basename(filePath);
  const parts = filePath.split(path.sep);

  for (const exclude of excludes) {
    const excludeRegex = globToRegex(exclude);
    if (excludeRegex.test(fileName)) return true;
    if (excludeRegex.test(filePath)) return true;
    // Check if any part of the path matches
    for (const part of parts) {
      if (excludeRegex.test(part)) return true;
    }
  }
  return false;
}

function matchesPattern(filePath, pattern) {
  const fileName = path.basename(filePath);
  const regex = globToRegex(pattern);

  // Try matching just the filename first
  if (regex.test(fileName)) return true;

  // Try matching the full path
  return regex.test(filePath);
}

function searchDirectory(dir, patterns, excludes, type, results, limit) {
  if (limit && results.length >= limit) return;

  try {
    const entries = fs.readdirSync(dir, { withFileTypes: true });

    for (const entry of entries) {
      const fullPath = path.join(dir, entry.name);
      const relativePath = path.relative(process.cwd(), fullPath);

      // Check excludes
      if (shouldExclude(fullPath, excludes) || shouldExclude(relativePath, excludes)) {
        continue;
      }

      if (entry.isDirectory()) {
        // Check if directory matches
        if (!type || type === 'd') {
          for (const pattern of patterns) {
            if (matchesPattern(fullPath, pattern) || matchesPattern(relativePath, pattern)) {
              results.push(fullPath);
              break;
            }
          }
        }

        // Recurse into subdirectories
        searchDirectory(fullPath, patterns, excludes, type, results, limit);
      } else if (entry.isFile()) {
        // Check if file matches
        if (!type || type === 'f') {
          for (const pattern of patterns) {
            if (matchesPattern(fullPath, pattern) || matchesPattern(relativePath, pattern)) {
              results.push(fullPath);
              break;
            }
          }
        }
      }

      if (limit && results.length >= limit) return;
    }
  } catch (e) {
    // Permission denied or other error - skip this directory
  }
}

function formatBytes(bytes) {
  if (bytes === 0) return '0 B';
  const k = 1024;
  const sizes = ['B', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (args.patterns.length === 0) {
    console.log('Glob Finder - Pattern-based file search');
    console.log('');
    console.log('Usage: glob.js <pattern> [options]');
    console.log('');
    console.log('Options:');
    console.log('  --exclude <pattern>  Exclude pattern (can use multiple)');
    console.log('  --type f|d          Filter by type (file or directory)');
    console.log('  --details           Show size and modification time');
    console.log('  --limit <n>         Limit results to n items');
    console.log('  --min-size <size>  Minimum file size (e.g., 1MB)');
    console.log('  --json              Output as JSON');
    console.log('');
    console.log('Examples:');
    console.log('  glob.js "*.js"');
    console.log('  glob.js "**/*.md" --exclude node_modules');
    process.exit(1);
  }

  const results = [];
  searchDirectory(process.cwd(), args.patterns, args.excludes, args.type, results, args.limit);

  if (args.minSize) {
    results.forEach((file, index) => {
      try {
        const stats = fs.statSync(file);
        if (stats.size < args.minSize) {
          results.splice(index, 1);
        }
      } catch (e) {
        results.splice(index, 1);
      }
    });
  }

  if (args.details) {
    const detailedResults = results.map(file => {
      try {
        const stats = fs.statSync(file);
        return {
          path: file,
          size: stats.size,
          sizeHuman: formatBytes(stats.size),
          modified: stats.mtime.toISOString(),
          isDirectory: stats.isDirectory()
        };
      } catch (e) {
        return { path: file, error: e.message };
      }
    });

    if (args.format === 'json') {
      console.log(JSON.stringify(detailedResults, null, 2));
    } else {
      detailedResults.forEach(item => {
        if (item.error) {
          console.log(`${item.path} [error: ${item.error}]`);
        } else {
          const type = item.isDirectory ? 'DIR' : 'FILE';
          console.log(`${type.padEnd(5)} ${item.sizeHuman.padStart(8)} ${item.modified.substring(0, 19)} ${item.path}`);
        }
      });
    }
  } else {
    if (args.format === 'json') {
      console.log(JSON.stringify(results, null, 2));
    } else {
      results.forEach(file => console.log(file));
    }
  }

  console.log(`\nFound ${results.length} items`);
}

main();

#!/usr/bin/env node
/**
 * Content Search - Search within file contents
 */
const fs = require('fs');
const path = require('path');

function parseArgs(args) {
  const result = {
    pattern: null,
    files: [],
    caseSensitive: true,
    regex: false,
    before: 0,
    after: 0,
    count: false,
    maxSize: 10 * 1024 * 1024, // 10MB max
    excludeDirs: ['node_modules', '.git', 'dist', 'build', '.tmp', 'tmp']
  };

  for (let i = 0; i < args.length; i++) {
    if (i === 0 && !args[i].startsWith('--')) {
      result.pattern = args[i];
    } else if (args[i] === '--files') {
      result.files.push(args[++i]);
    } else if (args[i] === '--case-insensitive') {
      result.caseSensitive = false;
    } else if (args[i] === '--regex') {
      result.regex = true;
    } else if (args[i] === '--before') {
      result.before = parseInt(args[++i]);
    } else if (args[i] === '--after') {
      result.after = parseInt(args[++i]);
    } else if (args[i] === '--count') {
      result.count = true;
    } else if (args[i] === '--max-size') {
      result.maxSize = parseInt(args[++i]) * 1024 * 1024;
    }
  }
  return result;
}

function isBinary(buffer) {
  // Check if file is binary by looking for null bytes
  // and high percentage of non-printable chars
  const sample = buffer.slice(0, 8000);
  let nonPrintable = 0;

  for (let i = 0; i < sample.length; i++) {
    const byte = sample[i];
    // Null bytes or control chars (except tab, newline, carriage return)
    if (byte === 0 || (byte < 32 && byte !== 9 && byte !== 10 && byte !== 13)) {
      nonPrintable++;
    }
  }

  return (nonPrintable / sample.length) > 0.1;
}

function shouldExcludeDir(dirPath, excludeDirs) {
  const parts = dirPath.split(path.sep);
  return excludeDirs.some(exclude => 
    parts.some(part => part === exclude || part.startsWith(exclude + path.sep))
  );
}

function globToRegex(pattern) {
  let regex = '';
  let i = 0;

  while (i < pattern.length) {
    const c = pattern[i];

    if (c === '**') {
      regex += '.*';
      i++;
    } else if (c === '*') {
      regex += '[^/]*';
      i++;
    } else if (c === '?') {
      regex += '[^/]';
      i++;
    } else if (/[.+^${}()|[\]\\]/.test(c)) {
      regex += '\\' + c;
      i++;
    } else {
      regex += c.replace(/[.+^${}()|[\]\\]/g, '\\$\u0026');
      i++;
    }
  }

  return new RegExp(regex);
}

function matchesFilePattern(filePath, patterns) {
  if (patterns.length === 0) return true;

  const fileName = path.basename(filePath);

  for (const pattern of patterns) {
    const regex = globToRegex(pattern);
    if (regex.test(fileName) || regex.test(filePath)) {
      return true;
    }
  }
  return false;
}

function searchFile(filePath, regex, args) {
  try {
    const stats = fs.statSync(filePath);

    if (stats.size > args.maxSize) {
      return { skipped: true, reason: 'File too large' };
    }

    const buffer = fs.readFileSync(filePath);

    if (isBinary(buffer)) {
      return { skipped: true, reason: 'Binary file' };
    }

    const content = buffer.toString('utf8');
    const lines = content.split(/\r?\n/);
    const matches = [];

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i];
      const match = regex.test(line);

      // Reset lastIndex for global regex
      if (args.regex && regex.global) {
        regex.lastIndex = 0;
      }

      if (match) {
        matches.push({
          line: i + 1,
          content: line,
          context: {
            before: lines.slice(Math.max(0, i - args.before), i),
            after: lines.slice(i + 1, Math.min(lines.length, i + 1 + args.after))
          }
        });
      }
    }

    return { matches, totalLines: lines.length };
  } catch (e) {
    return { error: e.message };
  }
}

function searchDirectory(dir, args, regex, results) {
  try {
    const entries = fs.readdirSync(dir, { withFileTypes: true });

    for (const entry of entries) {
      const fullPath = path.join(dir, entry.name);

      if (entry.isDirectory()) {
        if (!shouldExcludeDir(fullPath, args.excludeDirs)) {
          searchDirectory(fullPath, args, regex, results);
        }
      } else if (entry.isFile()) {
        if (matchesFilePattern(fullPath, args.files)) {
          const searchResult = searchFile(fullPath, regex, args);
          if (searchResult.matches && searchResult.matches.length > 0) {
            results.push({
              file: fullPath,
              ...searchResult
            });
          } else if (!searchResult.skipped && !searchResult.error) {
            // Track files with no matches separately
          }
        }
      }
    }
  } catch (e) {
    // Permission denied or other error
  }
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.pattern) {
    console.log('Content Search - Search within file contents');
    console.log('');
    console.log('Usage: content-search.js <pattern> [options]');
    console.log('');
    console.log('Options:');
    console.log('  --files <pattern>      File pattern(s) to search (default: all files)');
    console.log('  --case-insensitive     Case-insensitive search');
    console.log('  --regex                Treat pattern as regular expression');
    console.log('  --before <n>           Show n lines before match');
    console.log('  --after <n>            Show n lines after match');
    console.log('  --count                Count matches only');
    console.log('  --max-size <MB>       Skip files larger than MB (default: 10)');
    console.log('');
    console.log('Examples:');
    console.log('  content-search.js "TODO" --files "*.js"');
    console.log('  content-search.js "function\\s+\\w+" --files "*.ts" --regex');
    console.log('  content-search.js "import" --files "*.js" --before 2 --after 2');
    process.exit(1);
  }

  // Create regex
  let regex;
  const flags = args.caseSensitive ? 'g' : 'gi';

  if (args.regex) {
    try {
      regex = new RegExp(args.pattern, flags);
    } catch (e) {
      console.error(`Invalid regex pattern: ${e.message}`);
      process.exit(1);
    }
  } else {
    const escaped = args.pattern.replace(/[.*+?^${}()|[\]\\]/g, '\\$\u0026');
    regex = new RegExp(escaped, flags);
  }

  const results = [];
  searchDirectory(process.cwd(), args, regex, results);

  let totalMatches = 0;
  let totalFiles = 0;

  if (args.count) {
    for (const result of results) {
      if (result.matches) {
        totalMatches += result.matches.length;
        totalFiles++;
      }
    }
    console.log(`${totalMatches} matches across ${totalFiles} files`);
  } else {
    for (const result of results) {
      if (result.error) {
        console.log(`\n${result.file}:`);
        console.log(`  Error: ${result.error}`);
        continue;
      }

      if (result.skipped) {
        continue;
      }

      console.log(`\n${result.file}:`);

      for (const match of result.matches) {
        // Print context before
        for (const ctx of match.context.before) {
          console.log(`  ${match.line - match.context.before.length - 1 + match.context.before.indexOf(ctx) + 1}: ${ctx}`);
        }

        // Print match line
        console.log(`  \u001b[32m${match.line}: ${match.content}\u001b[0m`);

        // Print context after
        for (let i = 0; i < match.context.after.length; i++) {
          const ctx = match.context.after[i];
          console.log(`  ${match.line + i + 1}: ${ctx}`);
        }

        totalMatches++;
      }

      totalFiles++;
    }

    console.log(`\nFound ${totalMatches} matches in ${totalFiles} files`);
  }
}

main();

#!/usr/bin/env node
/**
 * Web Fetcher - Fetch and extract web content
 */
const https = require('https');
const http = require('http');
const fs = require('fs');
const path = require('path');

function parseArgs(args) {
  const result = {
    url: null,
    extract: false,
    headers: false,
    raw: false,
    output: null,
    timeout: 30,
    userAgent: 'Mozilla/5.0 (compatible; WebFetcher/1.0) Node.js/' + process.version
  };

  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--url': result.url = args[++i]; break;
      case '--extract': result.extract = true; break;
      case '--headers': result.headers = true; break;
      case '--raw': result.raw = true; break;
      case '--output': result.output = args[++i]; break;
      case '--timeout': result.timeout = parseInt(args[++i]); break;
      case '--user-agent': result.userAgent = args[++i]; break;
    }
  }
  return result;
}

function isValidUrl(string) {
  try {
    new URL(string);
    return true;
  } catch (_) {
    return false;
  }
}

function fetchUrl(url, options) {
  return new Promise((resolve, reject) => {
    const urlObj = new URL(url);
    const client = urlObj.protocol === 'https:' ? https : http;

    const requestOptions = {
      hostname: urlObj.hostname,
      port: urlObj.port || (urlObj.protocol === 'https:' ? 443 : 80),
      path: urlObj.pathname + urlObj.search,
      method: 'GET',
      headers: {
        'User-Agent': options.userAgent,
        'Accept': 'text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8',
        'Accept-Encoding': 'gzip, deflate, br',
        'Accept-Language': 'en-US,en;q=0.9',
        'Connection': 'keep-alive'
      },
      timeout: options.timeout * 1000
    };

    const req = client.request(requestOptions, (res) => {
      // Handle redirects
      if (res.statusCode >= 300 && res.statusCode < 400 && res.headers.location) {
        const redirectUrl = res.headers.location.startsWith('http') 
          ? res.headers.location 
          : `${urlObj.protocol}//${urlObj.hostname}${res.headers.location}`;

        if (options.redirectCount > 5) {
          reject(new Error('Too many redirects'));
          return;
        }

        fetchUrl(redirectUrl, { ...options, redirectCount: (options.redirectCount || 0) + 1 })
          .then(resolve)
          .catch(reject);
        return;
      }

      let data = '';
      res.setEncoding('utf8');

      res.on('data', chunk => {
        data += chunk;
      });

      res.on('end', () => {
        resolve({
          statusCode: res.statusCode,
          headers: res.headers,
          data: data
        });
      });
    });

    req.on('error', reject);
    req.on('timeout', () => {
      req.destroy();
      reject(new Error('Request timeout'));
    });

    req.end();
  });
}

function extractReadableContent(html, url) {
  // Remove script and style elements
  let text = html.replace(/<script[^\u003e]*>[\s\S]*?<\/script>/gi, '');
  text = text.replace(/<style[^\u003e]*>[\s\S]*?<\/style>/gi, '');
  text = text.replace(/<noscript[^\u003e]*>[\s\S]*?<\/noscript>/gi, '');

  // Try to find title
  const titleMatch = html.match(/<title[^\u003e]*>([^\u003c]*)<\/title>/i);
  const title = titleMatch ? titleMatch[1].trim() : '';

  // Try to find main content
  // Common content containers
  const contentPatterns = [
    /<main[^\u003e]*>([\s\S]*?)<\/main>/i,
    /<article[^\u003e]*>([\s\S]*?)<\/article>/i,
    /<div[^\u003e]*class=["'][^"']*content[^"']*["'][^\u003e]*>([\s\S]*?)<\/div>/i,
    /<div[^\u003e]*id=["']content["'][^\u003e]*>([\s\S]*?)<\/div>/i,
    /<div[^\u003e]*class=["'][^"']*post[^"']*["'][^\u003e]*>([\s\S]*?)<\/div>/i,
    /<div[^\u003e]*class=["'][^"']*entry[^"']*["'][^\u003e]*>([\s\S]*?)<\/div>/i
  ];

  let content = '';
  for (const pattern of contentPatterns) {
    const match = html.match(pattern);
    if (match) {
      content = match[1];
      break;
    }
  }

  // If no content container found, use body
  if (!content) {
    const bodyMatch = html.match(/<body[^\u003e]*>([\s\S]*?)<\/body>/i);
    content = bodyMatch ? bodyMatch[1] : html;
  }

  // Convert to plain text
  content = content
    .replace(/<[^\u003e]+>/g, '\n')  // Replace tags with newlines
    .replace(/\n\s*\n+/g, '\n\n')           // Collapse multiple newlines
    .replace(/^[\s\u00a0]+|[\s\u00a0]+$/gm, '')  // Trim whitespace
    .replace(/\u00a0/g, ' ');                // Non-breaking space

  // Remove extra whitespace
  const lines = content.split('\n').map(l => l.trim()).filter(l => l.length > 0);

  return {
    title,
    url,
    content: lines.join('\n'),
    wordCount: lines.join(' ').split(/\s+/).filter(w => w.length > 0).length
  };
}

async function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.url) {
    console.log('Web Fetcher - Fetch and extract web content');
    console.log('');
    console.log('Usage: web-fetch.js --url <url> [options]');
    console.log('');
    console.log('Options:');
    console.log('  --url <url>          URL to fetch');
    console.log('  --extract            Extract readable content');
    console.log('  --headers            Show HTTP headers');
    console.log('  --raw                Show raw HTML');
    console.log('  --output <file>     Save to file');
    console.log('  --timeout <seconds>  Timeout (default: 30)');
    console.log('  --user-agent <ua>    Custom user agent');
    console.log('');
    console.log('Examples:');
    console.log('  web-fetch.js --url https://example.com');
    console.log('  web-fetch.js --url https://example.com/blog --extract');
    console.log('  web-fetch.js --url https://api.example.com --output response.json');
    process.exit(1);
  }

  if (!isValidUrl(args.url)) {
    console.error(`Error: Invalid URL: ${args.url}`);
    process.exit(1);
  }

  try {
    console.log(`Fetching: ${args.url}`);
    const response = await fetchUrl(args.url, args);

    if (args.headers) {
      console.log('\nHTTP Headers:');
      console.log(`  Status: ${response.statusCode}`);
      for (const [key, value] of Object.entries(response.headers)) {
        if (typeof value === 'string') {
          console.log(`  ${key}: ${value}`);
        } else if (Array.isArray(value)) {
          value.forEach(v => console.log(`  ${key}: ${v}`));
        }
      }
    }

    let output = response.data;
    let extracted = null;

    if (args.extract) {
      extracted = extractReadableContent(response.data, args.url);
      output = extracted.content;
    }

    if (args.output) {
      fs.writeFileSync(args.output, output);
      console.log(`\nSaved to: ${args.output}`);

      if (extracted) {
        console.log(`Title: ${extracted.title}`);
        console.log(`Words: ${extracted.wordCount}`);
      }
    } else {
      console.log('');
      if (extracted) {
        console.log(`Title: ${extracted.title}`);
        console.log(`URL: ${extracted.url}`);
        console.log(`Words: ${extracted.wordCount}\n`);
      }
      console.log(output.substring(0, 10000));
      if (output.length > 10000) {
        console.log(`\n... (${output.length - 10000} more characters)`);
      }
    }

    console.log(`\nStatus: ${response.statusCode} OK`);
    console.log(`Size: ${response.data.length} bytes`);

  } catch (error) {
    console.error(`\nError: ${error.message}`);
    process.exit(1);
  }
}

main();

#!/usr/bin/env node
/**
 * SOP Runner - Standard Operating Procedure execution engine
 */
const fs = require('fs');
const path = require('path');
const { spawn } = require('child_process');
const crypto = require('crypto');

function parseArgs(args) {
  const result = {
    file: null,
    list: false,
    validate: false,
    dryRun: false,
    audit: false,
    auditId: null,
    variables: {},
    sopsDir: './sops'
  };

  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--file': result.file = args[++i]; break;
      case '--list': result.list = true; break;
      case '--validate': result.validate = true; break;
      case '--dry-run': result.dryRun = true; break;
      case '--audit': result.audit = true; break;
      case '--id': result.auditId = args[++i]; break;
      case '--var': {
        const [key, value] = args[++i].split('=');
        result.variables[key] = value;
        break;
      }
      case '--sops-dir': result.sopsDir = args[++i]; break;
    }
  }
  return result;
}

function loadYaml(content) {
  const result = {};
  const lines = content.split('\n');
  let currentKey = null;
  let currentArray = null;
  let indent = 0;

  for (const line of lines) {
    const trimmed = line.trim();
    if (!trimmed || trimmed.startsWith('#')) continue;

    const match = trimmed.match(/^(\w+):\s*(.*)$/);
    if (match) {
      const [, key, value] = match;
      if (value) {
        result[key] = value.replace(/^["']|["']$/g, '');
      } else {
        currentKey = key;
        result[currentKey] = {};
      }
    }
  }

  return result;
}

function loadSop(filePath) {
  const content = fs.readFileSync(filePath, 'utf8');
  const ext = path.extname(filePath);

  if (ext === '.json') {
    return JSON.parse(content);
  } else if (ext === '.yaml' || ext === '.yml') {
    // Simple YAML parser
    return parseYaml(content);
  } else if (ext === '.toml') {
    // Simple TOML parser
    return parseToml(content);
  }

  throw new Error(`Unsupported file format: ${ext}`);
}

function parseYaml(content) {
  const result = { steps: [] };
  let currentStep = null;
  let inSteps = false;

  const lines = content.split('\n');

  for (let i = 0; i < lines.length; i++) {
    const line = lines[i];
    const trimmed = line.trim();

    if (!trimmed || trimmed.startsWith('#')) continue;

    // Root level keys
    if (!line.startsWith(' ') && !line.startsWith('\t')) {
      if (trimmed.includes(':')) {
        const [key, value] = trimmed.split(':', 2);
        result[key.trim()] = value ? value.trim().replace(/^["']|["']$/g, '') : null;
        inSteps = key.trim() === 'steps';
        if (inSteps) {
          result.steps = [];
        }
      }
      continue;
    }

    // Steps section
    if (inSteps) {
      if (trimmed.startsWith('- name:')) {
        if (currentStep) result.steps.push(currentStep);
        currentStep = { name: trimmed.split(':', 2)[1].trim() };
      } else if (currentStep && trimmed.includes(':')) {
        const [key, value] = trimmed.split(':', 2);
        const keyTrimmed = key.trim();
        const valueTrimmed = value ? value.trim().replace(/^["']|["']$/g, '') : '';

        if (keyTrimmed === 'depends_on' || keyTrimmed === 'approvers') {
          currentStep[keyTrimmed] = valueTrimmed.split(',').map(s => s.trim());
        } else if (keyTrimmed === 'timeout' || keyTrimmed === 'retries') {
          currentStep[keyTrimmed] = parseInt(valueTrimmed) || valueTrimmed;
        } else {
          currentStep[keyTrimmed] = valueTrimmed;
        }
      }
    }
  }

  if (currentStep) result.steps.push(currentStep);

  return result;
}

function parseToml(content) {
  // Similar to YAML but TOML format
  return parseYaml(content); // Simplified - many constructs are similar
}

function expandVariables(str, vars) {
  return str.replace(/\{\{(\w+)\}\}/g, (match, key) => {
    return vars[key] !== undefined ? vars[key] : match;
  });
}

function executeCommand(command, timeout = 300) {
  return new Promise((resolve) => {
    const parts = command.split(' ');
    const cmd = parts[0];
    const args = parts.slice(1);

    const child = spawn(cmd, args, { 
      shell: true,
      stdio: ['pipe', 'pipe', 'pipe']
    });

    let stdout = '';
    let stderr = '';
    let killed = false;

    const timer = setTimeout(() => {
      killed = true;
      child.kill('SIGTERM');
    }, timeout * 1000);

    child.stdout.on('data', (data) => {
      stdout += data.toString();
    });

    child.stderr.on('data', (data) => {
      stderr += data.toString();
    });

    child.on('close', (code) => {
      clearTimeout(timer);
      resolve({
        success: code === 0 && !killed,
        exitCode: code,
        killed,
        stdout: stdout.trim(),
        stderr: stderr.trim()
      });
    });
  });
}

async function runStep(step, variables, dryRun) {
  const expandedCommand = step.command ? expandVariables(step.command, variables) : null;

  console.log(`\nâ–¶ Step: ${step.name}`);
  if (step.description) console.log(`  Description: ${step.description}`);

  if (dryRun) {
    console.log(`  [DRY RUN] Would execute: ${expandedCommand || 'N/A'}`);
    return { success: true, dryRun: true };
  }

  if (step.type === 'approval') {
    console.log(`  âš  APPROVAL REQUIRED`);
    console.log(`  This step requires manual approval.`);
    console.log(`  Approvers: ${(step.approvers || ['anyone']).join(', ')}`);
    console.log(`  Waiting for approval (simulated for testing)...`);
    // In production, this would wait for actual approval via API/webhook
    await new Promise(r => setTimeout(r, 1000));
    console.log(`  âœ“ Approved (simulated)`);
    return { success: true, approved: true };
  }

  if (!expandedCommand) {
    console.log(`  âš  No command specified`);
    return { success: true };
  }

  console.log(`  Executing: ${expandedCommand}`);

  const maxRetries = step.retries || 0;
  let attempt = 0;
  let result;

  do {
    if (attempt > 0) {
      console.log(`  Retry attempt ${attempt}/${maxRetries}...`);
    }

    result = await executeCommand(expandedCommand, step.timeout || 300);
    attempt++;

    if (!result.success && attempt <= maxRetries) {
      await new Promise(r => setTimeout(r, 1000));
    }
  } while (!result.success && attempt <= maxRetries);

  if (result.success) {
    console.log(`  âœ“ Success`);
    if (result.stdout) console.log(`    Output: ${result.stdout.substring(0, 200)}`);
  } else {
    console.log(`  âœ— Failed (exit code: ${result.exitCode})`);
    if (result.stderr) console.log(`    Error: ${result.stderr.substring(0, 200)}`);
  }

  return result;
}

async function runSop(sop, variables, dryRun) {
  const executionId = crypto.randomUUID();
  const startTime = Date.now();
  const audit = {
    id: executionId,
    sop: sop.name,
    startTime: new Date().toISOString(),
    variables,
    steps: [],
    status: 'running'
  };

  console.log(`\nâ•”â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•—`);
  console.log(`â•‘ SOP: ${sop.name.padEnd(33)} â•‘`);
  console.log(`â•‘ Execution ID: ${executionId.substring(0, 20).padEnd(20)} â•‘`);
  console.log(`â•šâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•`);

  // Build dependency graph
  const completed = new Set();
  const failed = new Set();

  try {
    // Simple sequential execution (respects depends_on)
    const stepsToRun = [...sop.steps];
    const running = new Set();

    while (stepsToRun.length > 0 || running.size > 0) {
      // Find steps that can run
      const ready = stepsToRun.filter(step => {
        if (!step.depends_on || step.depends_on.length === 0) return true;
        return step.depends_on.every(dep => completed.has(dep));
      });

      if (ready.length === 0 && running.size === 0) {
        throw new Error('Deadlock: No steps can run');
      }

      // Run ready steps
      for (const step of ready) {
        stepsToRun.splice(stepsToRun.indexOf(step), 1);
        running.add(step.name);

        const stepResult = await runStep(step, variables, dryRun);

        audit.steps.push({
          name: step.name,
          status: stepResult.success ? 'success' : 'failed',
          output: stepResult.stdout,
          error: stepResult.stderr
        });

        running.delete(step.name);

        if (stepResult.success) {
          completed.add(step.name);
        } else {
          failed.add(step.name);

          // Check if rollback is needed
          if (step.on_failure === 'rollback' && sop.rollback) {
            console.log('\nâš  Executing rollback...');
            for (const rollbackStep of sop.rollback) {
              console.log(`  Rollback: ${rollbackStep.name}`);
            }
          }

          audit.status = 'failed';
          audit.endTime = new Date().toISOString();
          audit.duration = Date.now() - startTime;

          saveAudit(audit);
          return audit;
        }
      }

      // Wait a bit before checking again
      if (stepsToRun.length > 0 && running.size > 0) {
        await new Promise(r => setTimeout(r, 100));
      }
    }

    audit.status = 'success';

  } catch (e) {
    audit.status = 'error';
    audit.error = e.message;
  }

  audit.endTime = new Date().toISOString();
  audit.duration = Date.now() - startTime;

  saveAudit(audit);

  console.log(`\nâ•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•`);
  console.log(`Status: ${audit.status.toUpperCase()}`);
  console.log(`Duration: ${audit.duration}ms`);
  console.log(`Execution ID: ${executionId}`);
  console.log(`â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•`);

  return audit;
}

function saveAudit(audit) {
  const auditDir = path.join(process.cwd(), '.sop', 'audit');
  if (!fs.existsSync(auditDir)) {
    fs.mkdirSync(auditDir, { recursive: true });
  }

  const filePath = path.join(auditDir, `${audit.id}.json`);
  fs.writeFileSync(filePath, JSON.stringify(audit, null, 2));
}

function listSops(sopsDir) {
