#!/usr/bin/env node
/**
 * Model Router - Route between LLM providers
 */
const fs = require('fs');
const path = require('path');

const CONFIG_PATH = path.join(process.cwd(), '.model-router.json');

const KNOWN_PROVIDERS = [
  { id: 'anthropic', name: 'Anthropic', models: ['claude-opus-4', 'claude-sonnet-4', 'claude-haiku'], defaultUrl: 'https://api.anthropic.com/v1', requiresKey: 'ANTHROPIC_API_KEY' },
  { id: 'openai', name: 'OpenAI', models: ['gpt-4o', 'gpt-4o-mini', 'gpt-4-turbo'], defaultUrl: 'https://api.openai.com/v1', requiresKey: 'OPENAI_API_KEY' },
  { id: 'groq', name: 'Groq', models: ['llama-3.1-70b', 'mixtral-8x7b', 'gemma-2-9b'], defaultUrl: 'https://api.groq.com/openai/v1', requiresKey: 'GROQ_API_KEY' },
  { id: 'google', name: 'Google (Gemini)', models: ['gemini-2.0-pro', 'gemini-1.5-pro', 'gemini-1.5-flash'], defaultUrl: 'https://generativelanguage.googleapis.com/v1beta', requiresKey: 'GOOGLE_API_KEY' },
  { id: 'together', name: 'Together AI', models: ['meta-llama/Llama-3.1-70B', 'mistralai/Mixtral-8x22B'], defaultUrl: 'https://api.together.xyz/v1', requiresKey: 'TOGETHER_API_KEY' },
  { id: 'mistral', name: 'Mistral', models: ['mistral-large', 'mistral-medium', 'mistral-small'], defaultUrl: 'https://api.mistral.ai/v1', requiresKey: 'MISTRAL_API_KEY' },
  { id: 'deepseek', name: 'DeepSeek', models: ['deepseek-chat', 'deepseek-coder'], defaultUrl: 'https://api.deepseek.com/v1', requiresKey: 'DEEPSEEK_API_KEY' },
  { id: 'openrouter', name: 'OpenRouter', models: ['openrouter/auto'], defaultUrl: 'https://openrouter.ai/api/v1', requiresKey: 'OPENROUTER_API_KEY' },
  { id: 'ollama', name: 'Ollama (local)', models: ['llama3.2', 'mistral', 'qwen2.5'], defaultUrl: 'http://localhost:11434/v1', requiresKey: null },
  { id: 'custom', name: 'Custom Endpoint', models: ['custom'], defaultUrl: '', requiresKey: 'CUSTOM_API_KEY' }
];

const TASK_MODELS = {
  coding: { provider: 'anthropic', model: 'claude-opus-4' },
  research: { provider: 'anthropic', model: 'claude-sonnet-4' },
  summarize: { provider: 'anthropic', model: 'claude-haiku' },
  creative: { provider: 'openai', model: 'gpt-4o' },
  fast: { provider: 'groq', model: 'llama-3.1-70b' },
  vision: { provider: 'openai', model: 'gpt-4o' },
  long_context: { provider: 'anthropic', model: 'claude-opus-4' }
};

function loadConfig() {
  if (fs.existsSync(CONFIG_PATH)) {
    return JSON.parse(fs.readFileSync(CONFIG_PATH, 'utf8'));
  }
  return {
    version: '1.0',
    defaultProvider: 'anthropic',
    defaultModel: 'claude-sonnet-4',
    fallbacks: [],
    routing: TASK_MODELS,
    providers: {}
  };
}

function saveConfig(config) {
  fs.writeFileSync(CONFIG_PATH, JSON.stringify(config, null, 2));
}

function parseArgs(args) {
  const result = {
    command: null,
    task: null,
    provider: null,
    model: null,
    primary: null,
    fallback: null,
    prompt: null,
    file: null,
    url: null,
    json: false
  };

  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--command': result.command = args[++i]; break;
      case '--task': result.task = args[++i]; break;
      case '--provider': result.provider = args[++i]; break;
      case '--model': result.model = args[++i]; break;
      case '--primary': result.primary = args[++i]; break;
      case '--fallback': result.fallback = args[++i]; break;
      case '--prompt': result.prompt = args[++i]; break;
      case '--file': result.file = args[++i]; break;
      case '--url': result.url = args[++i]; break;
      case '--json': result.json = true; break;
    }
  }
  return result;
}

function listProviders() {
  const config = loadConfig();

  return KNOWN_PROVIDERS.map(p => {
    const hasKey = p.requiresKey ? process.env[p.requiresKey] !== undefined : true;
    const configured = config.providers[p.id] !== undefined;

    return {
      ...p,
      available: hasKey,
      configured: configured,
      envVar: p.requiresKey
    };
  });
}

function configureRouting(task, provider, model) {
  const config = loadConfig();

  config.routing[task] = { provider, model };
  saveConfig(config);

  return { task, provider, model };
}

function setFallback(primary, fallbacks) {
  const config = loadConfig();

  config.fallbacks = fallbacks.split(',').map(s => s.trim());
  saveConfig(config);

  return { primary, fallbacks: config.fallbacks };
}

function routeRequest(task, prompt, config) {
  const routing = config.routing[task] || { provider: config.defaultProvider, model: config.defaultModel };

  // Check if provider is available
  const provider = KNOWN_PROVIDERS.find(p => p.id === routing.provider);
  const available = provider && (!provider.requiresKey || process.env[provider.requiresKey]);

  if (!available && config.fallbacks.length > 0) {
    // Try fallbacks
    for (const fallback of config.fallbacks) {
      const fbProvider = KNOWN_PROVIDERS.find(p => p.id === fallback);
      if (fbProvider && (!fbProvider.requiresKey || process.env[fbProvider.requiresKey])) {
        return { provider: fallback, model: config.routing[task]?.model || fbProvider.models[0], prompt, fallback: true };
      }
    }
  }

  return { provider: routing.provider, model: routing.model, prompt, fallback: false };
}

function checkStatus() {
  const providers = listProviders();

  return providers.map(p => {
    const status = p.available ? 'healthy' : 'not_configured';
    return {
      provider: p.id,
      name: p.name,
      status: status,
      models: p.models.slice(0, 3),
      envVar: p.requiresKey
    };
  });
}

function formatBytes(bytes) {
  if (bytes === 0) return '0 B';
  const k = 1024;
  const sizes = ['B', 'KB', 'MB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));
  return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
}

async function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.command) {
    console.log('Model Router - Route between LLM providers');
    console.log('');
    console.log('Usage: model-router.js --command <cmd> [options]');
    console.log('');
    console.log('Commands:');
    console.log('  list                List available providers');
    console.log('  config              Configure routing');
    console.log('  fallback            Set fallback providers');
    console.log('  route               Route a request');
    console.log('  status              Check provider status');
    console.log('');
    console.log('Options:');
    console.log('  --task <type>       Task type (coding, research, summarize)');
    console.log('  --provider <name>   Provider name');
    console.log('  --model <name>      Model name');
    console.log('  --primary <prov>    Primary provider for fallback');
    console.log('  --fallback <list>   Comma-separated fallback providers');
    console.log('  --prompt <text>     Prompt to route');
    console.log('  --json              Output as JSON');
    process.exit(1);
  }

  switch (args.command) {
    case 'list': {
      const providers = listProviders();

      if (args.json) {
        console.log(JSON.stringify(providers, null, 2));
      } else {
        console.log('Available Providers');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        console.log('');

        for (const p of providers) {
          const status = p.available ? 'âœ“' : 'âœ—';
          const config = p.configured ? '[configured]' : '';
          console.log(`${status} ${p.name.padEnd(15)} ${config}`);
          console.log(`  ID: ${p.id}`);
          console.log(`  Models: ${p.models.join(', ')}`);
          if (p.requiresKey) {
            console.log(`  Env Var: ${p.requiresKey}`);
          }
          console.log('');
        }

        const available = providers.filter(p => p.available).length;
        console.log(`Available: ${available}/${providers.length} providers`);
      }
      break;
    }

    case 'config': {
      if (!args.task || !args.provider) {
        console.error('Error: --task and --provider required');
        process.exit(1);
      }

      const result = configureRouting(args.task, args.provider, args.model);

      if (args.json) {
        console.log(JSON.stringify({ success: true, config: result }, null, 2));
      } else {
        console.log('âœ“ Routing configured');
        console.log(`  Task: ${result.task}`);
        console.log(`  Provider: ${result.provider}`);
        console.log(`  Model: ${result.model || 'default'}`);
      }
      break;
    }

    case 'fallback': {
      if (!args.fallback) {
        console.error('Error: --fallback required');
        process.exit(1);
      }

      const result = setFallback(args.primary, args.fallback);

      if (args.json) {
        console.log(JSON.stringify({ success: true, ...result }, null, 2));
      } else {
        console.log('âœ“ Fallback providers configured');
        console.log(`  Primary: ${result.primary || 'default'}`);
        console.log(`  Fallbacks: ${result.fallbacks.join(', ')}`);
      }
      break;
    }

    case 'route': {
      const config = loadConfig();
      const task = args.task || 'default';
      const prompt = args.prompt || (args.file ? `Process file: ${args.file}` : 'Direct request');

      const result = routeRequest(task, prompt, config);

      if (args.json) {
        console.log(JSON.stringify(result, null, 2));
      } else {
        console.log('Route Configuration');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        console.log(`Task: ${task}`);
        console.log(`Provider: ${result.provider}${result.fallback ? ' (fallback)' : ''}`);
        console.log(`Model: ${result.model}`);
        console.log(`Prompt: ${result.prompt.substring(0, 100)}${result.prompt.length > 100 ? '...' : ''}`);
      }
      break;
    }

    case 'status': {
      const status = checkStatus();

      if (args.json) {
        console.log(JSON.stringify(status, null, 2));
      } else {
        console.log('Provider Status');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        console.log('');

        for (const s of status) {
          const icon = s.status === 'healthy' ? 'â—' : 'â—‹';
          console.log(`${icon} ${s.name.padEnd(20)} ${s.status.toUpperCase()}`);
        }

        const healthy = status.filter(s => s.status === 'healthy').length;
        console.log(`\nHealthy: ${healthy}/${status.length}`);
      }
      break;
    }

    default:
      console.error(`Unknown command: ${args.command}`);
      process.exit(1);
  }
}

main();

#!/usr/bin/env node
/**
 * Cron Advanced - Enhanced cron management
 */
const fs = require('fs');
const path = require('path');
const { exec } = require('child_process');

const CRON_DIR = path.join(process.cwd(), '.cron');
const TASKS_FILE = path.join(CRON_DIR, 'tasks.json');
const HISTORY_DIR = path.join(CRON_DIR, 'history');

function ensureDirs() {
  if (!fs.existsSync(CRON_DIR)) fs.mkdirSync(CRON_DIR, { recursive: true });
  if (!fs.existsSync(HISTORY_DIR)) fs.mkdirSync(HISTORY_DIR, { recursive: true });
}

function loadTasks() {
  ensureDirs();
  if (fs.existsSync(TASKS_FILE)) {
    return JSON.parse(fs.readFileSync(TASKS_FILE, 'utf8'));
  }
  return { version: '1.0', tasks: [] };
}

function saveTasks(tasks) {
  ensureDirs();
  fs.writeFileSync(TASKS_FILE, JSON.stringify(tasks, null, 2));
}

function generateTaskId() {
  return `task-${Date.now().toString(36)}`;
}

function addTask(name, type, schedule, command) {
  const tasks = loadTasks();

  const task = {
    id: generateTaskId(),
    name,
    type,
    schedule,
    command,
    status: 'active',
    created: new Date().toISOString(),
    lastRun: null,
    nextRun: calculateNextRun(type, schedule)
  };

  tasks.tasks.push(task);
  saveTasks(tasks);

  return task;
}

function calculateNextRun(type, schedule) {
  const now = new Date();

  switch (type) {
    case 'at':
      return schedule;
    case 'every':
      return new Date(now.getTime() + parseInt(schedule) * 1000).toISOString();
    case 'cron':
      // Simplified - next run would be calculated from cron expression
      return new Date(now.getTime() + 24 * 60 * 60 * 1000).toISOString();
    default:
      return null;
  }
}

function listTasks() {
  return loadTasks().tasks;
}

function updateTask(taskId, updates) {
  const tasks = loadTasks();
  const task = tasks.tasks.find(t => t.id === taskId);

  if (task) {
    Object.assign(task, updates);
    saveTasks(tasks);
  }

  return task;
}

function removeTask(taskId) {
  const tasks = loadTasks();
  const index = tasks.tasks.findIndex(t => t.id === taskId);

  if (index >= 0) {
    tasks.tasks.splice(index, 1);
    saveTasks(tasks);
    return true;
  }
  return false;
}

function addHistory(taskId, result) {
  const historyFile = path.join(HISTORY_DIR, `${taskId}.jsonl`);
  fs.appendFileSync(historyFile, JSON.stringify({ ...result, timestamp: new Date().toISOString() }) + '\n');
}

function getHistory(taskId) {
  const historyFile = path.join(HISTORY_DIR, `${taskId}.jsonl`);
  if (!fs.existsSync(historyFile)) return [];

  return fs.readFileSync(historyFile, 'utf8')
    .split('\n')
    .filter(l => l.trim())
    .map(l => JSON.parse(l));
}

function parseArgs(args) {
  const result = {
    command: null,
    name: null,
    schedule: null,
    cmd: null,
    id: null,
    time: null,
    interval: null,
    json: false
  };

  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--command': result.command = args[++i]; break;
      case '--name': result.name = args[++i]; break;
      case '--schedule': result.schedule = args[++i]; break;
      case '--cmd': result.cmd = args[++i]; break;
      case '--id': result.id = args[++i]; break;
      case '--time': result.time = args[++i]; break;
      case '--interval': result.interval = args[++i]; break;
      case '--json': result.json = true; break;
    }
  }
  return result;
}

async function runTask(task) {
  return new Promise((resolve) => {
    exec(task.command, (error, stdout, stderr) => {
      resolve({
        success: !error,
        exitCode: error ? error.code : 0,
        stdout: stdout.trim(),
        stderr: stderr.trim()
      });
    });
  });
}

async function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.command) {
    console.log('Cron Advanced - Enhanced cron management');
    console.log('');
    console.log('Commands: list, add, add-at, add-every, pause, resume, remove, history, run-now');
    process.exit(1);
  }

  switch (args.command) {
    case 'list': {
      const tasks = listTasks();

      if (args.json) {
        console.log(JSON.stringify(tasks, null, 2));
      } else {
        console.log('Scheduled Tasks');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•');

        if (tasks.length === 0) {
          console.log('No tasks scheduled');
        } else {
          for (const t of tasks) {
            const statusIcon = t.status === 'active' ? 'â—' : t.status === 'paused' ? 'â¸' : 'â—‹';
            console.log(`${statusIcon} ${t.name} (${t.id})`);
            console.log(`  Type: ${t.type}`);
            console.log(`  Schedule: ${t.schedule}`);
            console.log(`  Status: ${t.status}`);
            console.log(`  Command: ${t.command.substring(0, 50)}`);
            console.log('');
          }
        }

        console.log(`Total: ${tasks.length} task(s)`);
      }
      break;
    }

    case 'add': {
      if (!args.name || !args.schedule || !args.cmd) {
        console.error('Error: --name, --schedule, --cmd required');
        process.exit(1);
      }

      const task = addTask(args.name, 'cron', args.schedule, args.cmd);

      if (args.json) {
        console.log(JSON.stringify({ success: true, task }, null, 2));
      } else {
        console.log('âœ“ Task added');
        console.log(`  ID: ${task.id}`);
        console.log(`  Name: ${task.name}`);
        console.log(`  Schedule: ${task.schedule}`);
      }
      break;
    }

    case 'add-at': {
      if (!args.name || !args.time || !args.cmd) {
        console.error('Error: --name, --time, --cmd required');
        process.exit(1);
      }

      const task = addTask(args.name, 'at', args.time, args.cmd);

      if (args.json) {
        console.log(JSON.stringify({ success: true, task }, null, 2));
      } else {
        console.log('âœ“ One-time task added');
        console.log(`  ID: ${task.id}`);
        console.log(`  Name: ${task.name}`);
        console.log(`  Time: ${task.schedule}`);
      }
      break;
    }

    case 'add-every': {
      if (!args.name || !args.interval || !args.cmd) {
        console.error('Error: --name, --interval, --cmd required');
        process.exit(1);
      }

      const task = addTask(args.name, 'every', args.interval, args.cmd);

      if (args.json) {
        console.log(JSON.stringify({ success: true, task }, null, 2));
      } else {
        console.log('âœ“ Recurring task added');
        console.log(`  ID: ${task.id}`);
        console.log(`  Name: ${task.name}`);
        console.log(`  Interval: ${args.interval} seconds`);
      }
      break;
    }

    case 'pause': {
      if (!args.id) {
        console.error('Error: --id required');
        process.exit(1);
      }

      const task = updateTask(args.id, { status: 'paused' });

      if (task) {
        console.log(`âœ“ Task ${args.id} paused`);
      } else {
        console.error(`Task ${args.id} not found`);
        process.exit(1);
      }
      break;
    }

    case 'resume': {
      if (!args.id) {
        console.error('Error: --id required');
        process.exit(1);
      }

      const task = updateTask(args.id, { status: 'active' });

      if (task) {
        console.log(`âœ“ Task ${args.id} resumed`);
      } else {
        console.error(`Task ${args.id} not found`);
        process.exit(1);
      }
      break;
    }

    case 'remove': {
      if (!args.id) {
        console.error('Error: --id required');
        process.exit(1);
      }

      if (removeTask(args.id)) {
        console.log(`âœ“ Task ${args.id} removed`);
      } else {
        console.error(`Task ${args.id} not found`);
        process.exit(1);
      }
      break;
    }

    case 'history': {
      if (!args.id) {
        console.error('Error: --id required');
        process.exit(1);
      }

      const history = getHistory(args.id);

      if (args.json) {
        console.log(JSON.stringify(history, null, 2));
      } else {
        console.log(`History for ${args.id}`);
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');

        if (history.length === 0) {
          console.log('No history available');
        } else {
          for (const h of history) {
            console.log(`\n[${h.timestamp}]`);
            console.log(`  Status: ${h.success ? 'Success' : 'Failed'}`);
            if (h.stdout) console.log(`  Output: ${h.stdout.substring(0, 100)}`);
          }
        }

        console.log(`\nTotal runs: ${history.length}`);
      }
      break;
    }

    case 'run-now': {
      if (!args.id) {
        console.error('Error: --id required');
        process.exit(1);
      }

      const tasks = listTasks();
      const task = tasks.find(t => t.id === args.id);

      if (!task) {
        console.error(`Task ${args.id} not found`);
        process.exit(1);
      }

      console.log(`Running task: ${task.name}`);
      const result = await runTask(task);
      addHistory(args.id, result);

      if (result.success) {
        console.log('âœ“ Task completed successfully');
        if (result.stdout) console.log(result.stdout);
      } else {
        console.error('âœ— Task failed');
        if (result.stderr) console.error(result.stderr);
      }
      break;
    }

    default:
      console.error(`Unknown command: ${args.command}`);
      process.exit(1);
  }
}

main();

#!/usr/bin/env node
/**
 * USB Scanner - Scan USB devices
 */
const fs = require('fs');
const path = require('path');

const USB_DEVICES_PATH = '/dev/bus/usb';
const SYS_USB_PATH = '/sys/bus/usb/devices';

const USB_CLASSES = {
  0x00: 'Use interface class',
  0x01: 'Audio',
  0x02: 'Communications',
  0x03: 'Human Interface Device',
  0x05: 'Physical Interface',
  0x06: 'Image',
  0x07: 'Printer',
  0x08: 'Mass Storage',
  0x09: 'Hub',
  0x0A: 'CDC Data',
  0x0B: 'Smart Card',
  0x0D: 'Content Security',
  0x0E: 'Video',
  0x0F: 'Personal Healthcare',
  0x10: 'Audio/Video',
  0xDC: 'Diagnostic Device',
  0xE0: 'Wireless Controller',
  0xEF: 'Miscellaneous',
  0xFE: 'Application Specific',
  0xFF: 'Vendor Specific'
};

const VENDOR_IDS = {
  '0781': 'SanDisk',
  '0951': 'Kingston',
  '0BDA': 'Realtek',
  '05AC': 'Apple',
  '046D': 'Logitech',
  '04F2': 'Chicony',
  '0424': 'Standard Microsystems',
  '8087': 'Intel',
  '1D6B': 'Linux Foundation',
  '0CF3': 'Qualcomm Atheros',
  '1004': 'LG Electronics',
  '04E8': 'Samsung'
};

function parseArgs(args) {
  const result = {
    command: null,
    device: null,
    vendor: null,
    product: null,
    class: null,
    json: false
  };

  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--command': result.command = args[++i]; break;
      case '--device': result.device = args[++i]; break;
      case '--vendor': result.vendor = args[++i]; break;
      case '--product': result.product = args[++i]; break;
      case '--class': result.class = args[++i]; break;
      case '--json': result.json = true; break;
    }
  }
  return result;
}

function readUsbDescriptorData(path) {
  try {
    const data = fs.readFileSync(path);
    if (data.length < 18) return null; // Too short

    // USB descriptor format
    const bLength = data[0];
    const bDescriptorType = data[1];
    const bcdUSB = data.readUInt16LE(2);
    const bDeviceClass = data[4];
    const bDeviceSubClass = data[5];
    const bDeviceProtocol = data[6];
    const bMaxPacketSize = data[7];
    const idVendor = data.readUInt16LE(8);
    const idProduct = data.readUInt16LE(10);
    const bcdDevice = data.readUInt16LE(12);
    const iManufacturer = data[14];
    const iProduct = data[15];
    const iSerialNumber = data[16];
    const bNumConfigurations = data[17];

    return {
      idVendor,
      idProduct,
      vendorName: VENDOR_IDS[idVendor.toString(16).toUpperCase().padStart(4, '0')] || 'Unknown',
      deviceClass: bDeviceClass,
      deviceClassName: USB_CLASSES[bDeviceClass] || 'Unknown',
      maxPacketSize: bMaxPacketSize,
      numConfigurations: bNumConfigurations
    };
  } catch (e) {
    return null;
  }
}

function scanUsbBus(busPath) {
  const devices = [];

  try {
    const entries = fs.readdirSync(busPath);

    for (const entry of entries) {
      const devicePath = path.join(busPath, entry);

      if (fs.statSync(devicePath).isFile()) {
        const descriptor = readUsbDescriptorData(devicePath);
        if (descriptor) {
          devices.push({
            bus: path.basename(busPath),
            device: entry,
            path: devicePath,
            ...descriptor
          });
        }
      }
    }
  } catch (e) {
    // Permission denied or other error
  }

  return devices;
}

function scanUsbDevices() {
  const devices = [];

  if (!fs.existsSync(USB_DEVICES_PATH)) {
    // Try alternative method via /sys
    return scanViaSys();
  }

  try {
    const buses = fs.readdirSync(USB_DEVICES_PATH);

    for (const bus of buses) {
      const busPath = path.join(USB_DEVICES_PATH, bus);
      if (fs.statSync(busPath).isDirectory()) {
        const busDevices = scanUsbBus(busPath);
        devices.push(...busDevices);
      }
    }
  } catch (e) {
    // Try alternative
    return scanViaSys();
  }

  return devices;
}

function scanViaSys() {
  const devices = [];

  if (!fs.existsSync(SYS_USB_PATH)) {
    return devices;
  }

  try {
    const entries = fs.readdirSync(SYS_USB_PATH);

    for (const entry of entries) {
      if (entry.includes(':')) continue; // Skip interface entries

      const devicePath = path.join(SYS_USB_PATH, entry);
      try {
        const idVendor = fs.readFileSync(path.join(devicePath, 'idVendor'), 'utf8').trim();
        const idProduct = fs.readFileSync(path.join(devicePath, 'idProduct'), 'utf8').trim();
        const bDeviceClass = parseInt(fs.readFileSync(path.join(devicePath, 'bDeviceClass'), 'utf8').trim());

        devices.push({
          bus: entry.substring(0, 3),
          device: entry,
          path: devicePath,
          idVendor: parseInt(idVendor, 16),
          idProduct: parseInt(idProduct, 16),
          vendorName: VENDOR_IDS[idVendor.toUpperCase()] || 'Unknown',
          deviceClass: bDeviceClass,
          deviceClassName: USB_CLASSES[bDeviceClass] || 'Unknown',
          productName: 'USB Device'
        });
      } catch (e) {
        // Skip incomplete entries
      }
    }
  } catch (e) {
    // Cannot scan
  }

  return devices;
}

function filterDevices(devices, filters) {
  return devices.filter(d => {
    if (filters.vendor && !d.vendorName.toLowerCase().includes(filters.vendor.toLowerCase()) && 
        d.idVendor.toString(16).toUpperCase() !== filters.vendor.toUpperCase()) {
      return false;
    }
    if (filters.class && d.deviceClassName.toLowerCase().replace(/\s+/g, '_') !== filters.class.toLowerCase() &&
        d.deviceClassName.toLowerCase() !== filters.class.toLowerCase()) {
      return false;
    }
    return true;
  });
}

function getDeviceInfo(devicePath) {
  const devices = scanUsbDevices();
  return devices.find(d => d.path === devicePath || d.device === path.basename(devicePath));
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.command) {
    console.log('USB Scanner');
    console.log('Usage: usb-scan.js --command <cmd> [options]');
    console.log('');
    console.log('Commands:');
    console.log('  list            List USB devices');
    console.log('  info            Get device info');
    console.log('');
    console.log('Options:');
    console.log('  --vendor <code>   Filter by vendor');
    console.log('  --class <type>    Filter by device class');
    console.log('  --json            Output as JSON');
    process.exit(1);
  }

  switch (args.command) {
    case 'list': {
      const devices = scanUsbDevices();
      const filtered = filterDevices(devices, { vendor: args.vendor, class: args.class });

      if (args.json) {
        console.log(JSON.stringify(filtered, null, 2));
      } else {
        console.log('USB Devices');
        console.log('â•â•â•â•â•â•â•â•â•â•â•');
        console.log('');

        if (filtered.length === 0) {
          console.log('No USB devices found');
          console.log('Note: This command may require elevated permissions');
        } else {
          for (const d of filtered) {
            console.log(`Bus ${d.bus.padStart(3)} Device ${d.device.padEnd(3)}`);
            console.log(`  Vendor: ${d.vendorName} (${d.idVendor.toString(16).toUpperCase().padStart(4, '0')})`);
            console.log(`  Product: ${d.idProduct.toString(16).toUpperCase().padStart(4, '0')}`);
            console.log(`  Class: ${d.deviceClassName}`);
            console.log('');
          }
        }

        console.log(`Found: ${filtered.length} device(s)`);
      }
      break;
    }

    case 'info': {
      if (!args.device) {
        console.error('Error: --device required');
        process.exit(1);
      }

      const device = getDeviceInfo(args.device);

      if (!device) {
        console.error('Device not found');
        process.exit(1);
      }

      if (args.json) {
        console.log(JSON.stringify(device, null, 2));
      } else {
        console.log('Device Information');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        console.log(`Path: ${device.path}`);
        console.log(`Bus: ${device.bus}`);
        console.log(`Device: ${device.device}`);
        console.log(`Vendor ID: 0x${device.idVendor.toString(16).toUpperCase().padStart(4, '0')}`);
        console.log(`Vendor Name: ${device.vendorName}`);
        console.log(`Product ID: 0x${device.idProduct.toString(16).toUpperCase().padStart(4, '0')}`);
        console.log(`Class: ${device.deviceClassName} (${device.deviceClass})`);
      }
      break;
    }

    default:
      console.error(`Unknown command: ${args.command}`);
      process.exit(1);
  }
}

main();

#!/usr/bin/env node
/**
 * Hardware Prober - System hardware information
 */
const fs = require('fs');
const path = require('path');
const os = require('os');

function parseArgs(args) {
  const result = {
    component: null,
    json: false
  };

  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '--component': result.component = args[++i]; break;
      case '--json': result.json = true; break;
    }
  }
  return result;
}

function readFileSafe(path) {
  try {
    return fs.readFileSync(path, 'utf8').trim();
  } catch (e) {
    return null;
  }
}

function getCpuInfo() {
  const cpus = os.cpus();
  const cpu0 = cpus[0] || {};

  const model = cpu0.model || 'Unknown';
  const speed = cpu0.speed || 0;
  const cores = os.cpus().length;
  const logicalCores = cores;

  const physicalCores = readFileSafe('/sys/devices/system/cpu/cpu0/topology/core_siblings_list');

  const arch = os.arch();
  const platform = os.platform();

  return {
    model,
    architecture: arch,
    platform,
    cores,
    logicalCores,
    physicalCores: physicalCores ? physicalCores.split(',').length : Math.floor(cores / 2),
    clockSpeed: speed,
    clockSpeedGHz: (speed / 1000).toFixed(2) + ' GHz'
  };
}

function getMemoryInfo() {
  const total = os.totalmem();
  const free = os.freemem();
  const used = total - free;

  // Try to get more details from /proc
  const meminfo = readFileSafe('/proc/meminfo');
  let memAvailable = null;

  if (meminfo) {
    const match = meminfo.match(/MemAvailable:\s+(\d+)/);
    if (match) memAvailable = parseInt(match[1]) * 1024;
  }

  return {
    total,
    free,
    used,
    available: memAvailable || free,
    totalGB: (total / 1024 / 1024 / 1024).toFixed(2) + ' GB',
    usedGB: (used / 1024 / 1024 / 1024).toFixed(2) + ' GB',
    freeGB: (free / 1024 / 1024 / 1024).toFixed(2) + ' GB',
    percentUsed: Math.round((used / total) * 100) + '%'
  };
}

function getStorageInfo() {
  const info = {
    disks: []
  };

  try {
    const mounts = readFileSafe('/proc/mounts');
    if (mounts) {
      const lines = mounts.split('\n');
      const seen = new Set();

      for (const line of lines) {
        const parts = line.split(' ');
        if (parts.length > 2 && parts[0].startsWith('/dev/')) {
          const device = parts[0];
          const mount = parts[1];
          const fs = parts[2];

          if (!seen.has(device) && (fs === 'ext4' || fs === 'ext3' || fs === 'xfs' || fs === 'btrfs' || fs === 'tmpfs')) {
            seen.add(device);
            try {
              const stats = fs.statSync(mount);
              // Try to get size info
              info.disks.push({
                device,
                mountpoint: mount,
                filesystem: fs,
                type: fs
              });
            } catch (e) {
              info.disks.push({
                device,
                mountpoint: mount,
                filesystem: fs
              });
            }
          }
        }
      }
    }
  } catch (e) {
    // Fallback
  }

  // Add root filesystem
  try {
    const cwd = process.cwd();
    const stats = fs.statSync(cwd);
    info.root = {
      available: 'Unknown',
      cwd
    };
  } catch (e) {}

  return info;
}

function getNetworkInfo() {
  const interfaces = os.networkInterfaces();
  const result = [];

  for (const [name, addrs] of Object.entries(interfaces)) {
    const iface = {
      name,
      addresses: addrs.map(a => ({
        address: a.address,
        family: a.family,
        internal: a.internal,
        mac: a.mac || 'N/A'
      }))
    };
    result.push(iface);
  }

  return result;
}

function getSystemInfo() {
  return {
    hostname: os.hostname(),
    platform: os.platform(),
    release: os.release(),
    arch: os.arch(),
    uptime: os.uptime(),
    uptimeFormatted: formatUptime(os.uptime()),
    loadAverage: os.loadavg(),
    totalMemory: os.totalmem(),
    freeMemory: os.freemem()
  };
}

function formatUptime(seconds) {
  const days = Math.floor(seconds / 86400);
  const hours = Math.floor((seconds % 86400) / 3600);
  const mins = Math.floor((seconds % 3600) / 60);
  return `${days}d ${hours}h ${mins}m`;
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.component) {
    console.log('Hardware Prober');
    console.log('Usage: hardware-probe.js --component <cpu|memory|storage|network|all> [--json]');
    process.exit(1);
  }

  let result = {};

  switch (args.component) {
    case 'cpu':
      result = getCpuInfo();
      break;
    case 'memory':
      result = getMemoryInfo();
      break;
    case 'storage':
      result = getStorageInfo();
      break;
    case 'network':
      result = getNetworkInfo();
      break;
    case 'all':
      result = {
        cpu: getCpuInfo(),
        memory: getMemoryInfo(),
        storage: getStorageInfo(),
        network: getNetworkInfo(),
        system: getSystemInfo()
      };
      break;
    default:
      console.error(`Unknown component: ${args.component}`);
      process.exit(1);
  }

  if (args.json) {
    console.log(JSON.stringify(result, null, 2));
  } else {
    switch (args.component) {
      case 'cpu':
        console.log('CPU Information');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        console.log(`Model: ${result.model}`);
        console.log(`Architecture: ${result.architecture}`);
        console.log(`Cores: ${result.cores} logical, ${result.physicalCores} physical`);
        console.log(`Clock Speed: ${result.clockSpeedGHz}`);
        break;

      case 'memory':
        console.log('Memory Information');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        console.log(`Total: ${result.totalGB}`);
        console.log(`Used: ${result.usedGB} (${result.percentUsed})`);
        console.log(`Free: ${result.freeGB}`);
        break;

      case 'storage':
        console.log('Storage Information');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        if (result.disks.length > 0) {
          for (const disk of result.disks) {
            console.log(`${disk.device} on ${disk.mountpoint} (${disk.filesystem})`);
          }
        } else {
          console.log('No disk information available');
        }
        break;

      case 'network':
        console.log('Network Interfaces');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        for (const iface of result) {
          console.log(`${iface.name}:`);
          for (const addr of iface.addresses) {
            const type = addr.internal ? '[internal]' : '';
            console.log(`  ${addr.family}: ${addr.address} ${type}`);
          }
        }
        break;

      case 'all':
        console.log('System Overview');
        console.log('â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•');
        console.log('');
        console.log('CPU:');
        console.log(`  ${result.cpu.model}`);
        console.log(`  ${result.cpu.cores} cores @ ${result.cpu.clockSpeedGHz}`);
        console.log('');
