#!/usr/bin/env node
/**
 * File Encryptor - AES-256-GCM file encryption/decryption
 */

const crypto = require('crypto');
const fs = require('fs');
const path = require('path');

const ALGORITHM = 'aes-256-gcm';
const SALT_LENGTH = 16;
const IV_LENGTH = 12;
const ITERATIONS = 100000;
const KEY_LENGTH = 32;
const DIGEST = 'sha256';

function deriveKey(password, salt) {
  return crypto.pbkdf2Sync(password, salt, ITERATIONS, KEY_LENGTH, DIGEST);
}

function encrypt(inputPath, outputPath, password) {
  const data = fs.readFileSync(inputPath);
  const salt = crypto.randomBytes(SALT_LENGTH);
  const key = deriveKey(password, salt);
  const iv = crypto.randomBytes(IV_LENGTH);

  const cipher = crypto.createCipheriv(ALGORITHM, key, iv);
  const encrypted = Buffer.concat([cipher.update(data), cipher.final()]);
  const authTag = cipher.getAuthTag();

  // Write: salt + iv + authTag + encrypted
  const output = Buffer.concat([salt, iv, authTag, encrypted]);
  fs.writeFileSync(outputPath, output);

  return {
    input: inputPath,
    output: outputPath,
    size_before: data.length,
    size_after: output.length
  };
}

function decrypt(inputPath, outputPath, password) {
  const data = fs.readFileSync(inputPath);

  // Extract components
  if (data.length < SALT_LENGTH + IV_LENGTH + 16) {
    throw new Error('File too small or corrupted');
  }

  let offset = 0;
  const salt = data.slice(offset, offset + SALT_LENGTH);
  offset += SALT_LENGTH;
  const iv = data.slice(offset, offset + IV_LENGTH);
  offset += IV_LENGTH;
  const authTag = data.slice(offset, offset + 16);
  offset += 16;
  const encrypted = data.slice(offset);

  const key = deriveKey(password, salt);

  const decipher = crypto.createDecipheriv(ALGORITHM, key, iv);
  decipher.setAuthTag(authTag);

  try {
    const decrypted = Buffer.concat([decipher.update(encrypted), decipher.final()]);
    fs.writeFileSync(outputPath, decrypted);

    return {
      input: inputPath,
      output: outputPath,
      size_before: data.length,
      size_after: decrypted.length
    };
  } catch (e) {
    throw new Error('Decryption failed: incorrect password or corrupted file');
  }
}

function parseArgs(args) {
  const result = {
    decrypt: false,
    input: null,
    output: null,
    password: null,
    deleteOriginal: false
  };

  for (let i = 0; i < args.length; i++) {
    switch (args[i]) {
      case '-d':
      case '--decrypt':
        result.decrypt = true;
        break;
      case '-i':
      case '--input':
        result.input = args[++i];
        break;
      case '-o':
      case '--output':
        result.output = args[++i];
        break;
      case '-p':
      case '--password':
        result.password = args[++i];
        break;
      case '--delete-original':
        result.deleteOriginal = true;
        break;
    }
  }
  return result;
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.input) {
    console.log('File Encryptor - AES-256-GCM encryption/decryption');
    console.log('');
    console.log('Usage: encrypt.js [options]');
    console.log('');
    console.log('Options:');
    console.log('  -i, --input <file>      Input file to encrypt/decrypt');
    console.log('  -o, --output <file>     Output file (default: input + .enc or without .enc)');
    console.log('  -p, --password <pass>    Password for encryption/decryption');
    console.log('  -d, --decrypt           Decrypt mode');
    console.log('  --delete-original       Delete original file after successful operation');
    console.log('');
    console.log('Examples:');
    console.log('  encrypt.js -i secret.txt -p "mypassword"');
    console.log('  encrypt.js -d -i secret.txt.enc -p "mypassword"');
    process.exit(1);
  }

  if (!args.password) {
    console.log(JSON.stringify({
      success: false,
      error: 'Password required (--password)'
    }, null, 2));
    process.exit(1);
  }

  if (!fs.existsSync(args.input)) {
    console.log(JSON.stringify({
      success: false,
      error: `Input file not found: ${args.input}`
    }, null, 2));
    process.exit(1);
  }

  try {
    let result;
    const outputPath = args.output || (args.decrypt 
      ? args.input.replace(/\.enc$/, '') || args.input + '.decrypted'
      : args.input + '.enc');

    if (args.decrypt) {
      result = decrypt(args.input, outputPath, args.password);
      result.operation = 'decrypt';
    } else {
      result = encrypt(args.input, outputPath, args.password);
      result.operation = 'encrypt';
    }

    result.success = true;
    console.log(JSON.stringify(result, null, 2));

    if (args.deleteOriginal) {
      fs.unlinkSync(args.input);
    }

    process.exit(0);

  } catch (e) {
    console.log(JSON.stringify({
      success: false,
      error: e.message
    }, null, 2));
    process.exit(1);
  }
}

main();

#!/usr/bin/env node
/**
 * Line Counter - Count lines of code by language
 */

const fs = require('fs');
const path = require('path');

const LANGUAGE_PATTERNS = {
  javascript: { extensions: ['.js', '.jsx', '.mjs'], single: '//', multi: ['/*', '*/'] },
  typescript: { extensions: ['.ts', '.tsx'], single: '//', multi: ['/*', '*/'] },
  python: { extensions: ['.py'], single: '#', multi: ['"""', '"""'] },
  java: { extensions: ['.java'], single: '//', multi: ['/*', '*/'] },
  c: { extensions: ['.c', '.h', '.cpp', '.hpp', '.cc'], single: '//', multi: ['/*', '*/'] },
  go: { extensions: ['.go'], single: '//', multi: ['/*', '*/'] },
  rust: { extensions: ['.rs'], single: '//', multi: ['/*', '*/'] },
  ruby: { extensions: ['.rb'], single: '#', multi: ['=begin', '=end'] },
  php: { extensions: ['.php'], single: ['//', '#'], multi: ['/*', '*/'] },
  shell: { extensions: ['.sh', '.bash', '.zsh'], single: '#', multi: null },
  perl: { extensions: ['.pl', '.pm'], single: '#', multi: null },
  r: { extensions: ['.r'], single: '#', multi: null },
  sql: { extensions: ['.sql'], single: '--', multi: ['/*', '*/'] },
  html: { extensions: ['.html', '.htm'], single: null, multi: ['<!--', '-->'] },
  css: { extensions: ['.css', '.scss', '.sass'], single: null, multi: ['/*', '*/'] },
  yaml: { extensions: ['.yml', '.yaml'], single: '#', multi: null },
  json: { extensions: ['.json'], single: null, multi: null },
  markdown: { extensions: ['.md', '.markdown'], single: null, multi: null },
  xml: { extensions: ['.xml', '.svg'], single: null, multi: ['<!--', '-->'] }
};

function getLanguageForFile(filepath) {
  const ext = path.extname(filepath).toLowerCase();
  for (const [lang, pattern] of Object.entries(LANGUAGE_PATTERNS)) {
    if (pattern.extensions.includes(ext)) return lang;
  }
  return 'other';
}

function detectLine(line, lang) {
  const trimmed = line.trim();
  if (trimmed === '') return 'blank';

  const pattern = LANGUAGE_PATTERNS[lang] || {};
  const singleComments = Array.isArray(pattern.single) ? pattern.single : [pattern.single];

  for (const marker of singleComments) {
    if (marker && trimmed.startsWith(marker)) return 'comment';
  }

  // Multi-line comments are handled at file level
  return 'code';
}

function countFile(filepath, language) {
  const content = fs.readFileSync(filepath, 'utf8');
  const lines = content.split('\n');
  const stats = {
    total: lines.length,
    code: 0,
    blank: 0,
    comment: 0
  };

  const lang = language || getLanguageForFile(filepath);
  const pattern = LANGUAGE_PATTERNS[lang] || {};
  const multiStart = pattern.multi ? pattern.multi[0] : null;
  const multiEnd = pattern.multi ? pattern.multi[1] : null;

  let inMultiComment = false;

  for (const line of lines) {
    const trimmed = line.trim();

    if (trimmed === '') {
      stats.blank++;
      continue;
    }

    if (multiStart && multiEnd) {
      if (inMultiComment) {
        stats.comment++;
        if (trimmed.includes(multiEnd)) {
          inMultiComment = false;
        }
        continue;
      }

      if (trimmed.includes(multiStart)) {
        if (trimmed.includes(multiEnd)) {
          stats.comment++;
        } else {
          stats.comment++;
          inMultiComment = true;
        }
        continue;
      }
    }

    const type = detectLine(line, lang);
    if (type === 'comment') {
      stats.comment++;
    } else {
      stats.code++;
    }
  }

  return stats;
}

function countDirectory(dir, excludes = []) {
  const results = {
    files: [],
    by_language: {},
    totals: { total: 0, code: 0, blank: 0, comment: 0 }
  };

  function shouldExclude(filepath) {
    const basename = path.basename(filepath);
    for (const exclude of excludes) {
      if (filepath.includes(exclude)) return true;
      if (basename.match(new RegExp(exclude))) return true;
    }
    return false;
  }

  function walk(currentPath) {
    try {
      const entries = fs.readdirSync(currentPath, { withFileTypes: true });

      for (const entry of entries) {
        const fullPath = path.join(currentPath, entry.name);

        if (shouldExclude(fullPath)) continue;

        if (entry.isDirectory()) {
          walk(fullPath);
        } else if (entry.isFile()) {
          try {
            const lang = getLanguageForFile(fullPath);
            const stats = countFile(fullPath, lang);
            const fileInfo = {
              path: fullPath,
              language: lang,
              ...stats
            };

            results.files.push(fileInfo);

            if (!results.by_language[lang]) {
              results.by_language[lang] = { files: 0, total: 0, code: 0, blank: 0, comment: 0 };
            }

            const langStats = results.by_language[lang];
            langStats.files++;
            langStats.total += stats.total;
            langStats.code += stats.code;
            langStats.blank += stats.blank;
            langStats.comment += stats.comment;

            results.totals.total += stats.total;
            results.totals.code += stats.code;
            results.totals.blank += stats.blank;
            results.totals.comment += stats.comment;

          } catch (e) {
            // Skip unreadable files
          }
        }
      }
    } catch (e) {
      // Permission denied or other error
    }
  }

  walk(dir);
  return results;
}

function formatTable(stats) {
  const lines = [];
  lines.push('='.repeat(60));
  lines.push('Line Count Summary');
  lines.push('='.repeat(60));
  lines.push('');
  lines.push('Total Statistics:');
  lines.push(`  Files:    ${stats.files.length}`);
  lines.push(`  Lines:    ${stats.totals.total.toLocaleString()}`);
  lines.push(`  Code:     ${stats.totals.code.toLocaleString()}`);
  lines.push(`  Blank:    ${stats.totals.blank.toLocaleString()}`);
  lines.push(`  Comments: ${stats.totals.comment.toLocaleString()}`);
  lines.push('');
  lines.push('By Language:');
  lines.push('-'.repeat(50));
  lines.push(`${'Language'.padEnd(12)} ${'Files'.padStart(6)} ${'Total'.padStart(8)} ${'Code'.padStart(8)} ${'Blank'.padStart(6)} ${'Comments'.padStart(6)}`);
  lines.push('-'.repeat(50));

  const sorted = Object.entries(stats.by_language).sort((a, b) => b[1].total - a[1].total);
  for (const [lang, s] of sorted) {
    lines.push(`${lang.padEnd(12)} ${String(s.files).padStart(6)} ${String(s.total).padStart(8)} ${String(s.code).padStart(8)} ${String(s.blank).padStart(6)} ${String(s.comment).padStart(6)}`);
  }
  lines.push('-'.repeat(50));

  return lines.join('\n');
}

function parseArgs(args) {
  const result = {
    command: null,
    target: null,
    excludes: ['node_modules', '.git', 'dist', 'build', '.next', 'coverage'],
    format: 'table',
    summary: false
  };

  let i = 0;
  while (i < args.length) {
    const arg = args[i];

    if (!result.command) {
      result.command = arg;
    } else if (!result.target) {
      result.target = arg;
    } else if (arg === '--exclude' && args[i + 1]) {
      result.excludes.push(args[++i]);
    } else if (arg === '--format' && args[i + 1]) {
      result.format = args[++i];
    } else if (arg === '--summary') {
      result.summary = true;
    }
    i++;
  }

  return result;
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.command || !args.target) {
    console.log('Line Counter - Count lines of code by language');
    console.log('');
    console.log('Usage: lines.js <command> <target> [options]');
    console.log('');
    console.log('Commands:');
    console.log('  count <path>    Count lines in file or directory');
    console.log('');
    console.log('Options:');
    console.log('  --exclude <pattern>  Exclude pattern (can be used multiple times)');
    console.log('  --format <format>   Output format: json, table (default: table)');
    console.log('  --summary           Show summary only');
    console.log('');
    console.log('Examples:');
    console.log('  lines.js count src');
    console.log('  lines.js count . --exclude node_modules --exclude test');
    console.log('  lines.js count src --format json');
    process.exit(1);
  }

  const target = args.target;

  if (!fs.existsSync(target)) {
    console.log(JSON.stringify({ error: `Path not found: ${target}` }, null, 2));
    process.exit(1);
  }

  let result;

  try {
    const stats = fs.statSync(target);

    if (stats.isFile()) {
      const lang = getLanguageForFile(target);
      const counts = countFile(target, lang);
      result = {
        files: [{ path: target, language: lang, ...counts }],
        by_language: { [lang]: { files: 1, ...counts } },
        totals: counts
      };
    } else {
      result = countDirectory(target, args.excludes);
    }

    if (args.summary) {
      const summary = {
        files: result.files.length,
        ...result.totals
      };
      console.log(JSON.stringify(summary, null, args.format === 'json' ? 2 : 0));
    } else if (args.format === 'json') {
      console.log(JSON.stringify(result, null, 2));
    } else {
      console.log(formatTable(result));
    }

  } catch (e) {
    console.log(JSON.stringify({ error: e.message }, null, 2));
    process.exit(1);
  }
}

main();

#!/usr/bin/env node
/**
 * Time Converter - Timezone and date conversion utilities
 */

const COMMON_ZONES = [
  'UTC', 'GMT',
  'America/New_York', 'America/Chicago', 'America/Denver', 'America/Los_Angeles',
  'Europe/London', 'Europe/Paris', 'Europe/Berlin', 'Europe/Rome', 'Europe/Madrid',
  'Asia/Tokyo', 'Asia/Shanghai', 'Asia/Hong_Kong', 'Asia/Singapore',
  'Australia/Sydney', 'Pacific/Auckland', 'Asia/Kolkata'
];

function parseDate(input) {
  if (!input) return new Date();

  // If it's a number, treat as Unix timestamp (seconds or milliseconds)
  if (!isNaN(input) && !isNaN(parseFloat(input))) {
    const num = parseFloat(input);
    // If less than 10 billion, it's probably seconds
    return num < 10000000000 ? new Date(num * 1000) : new Date(num);
  }

  const d = new Date(input);
  return isNaN(d.getTime()) ? null : d;
}

function formatDate(date, format = 'ISO') {
  if (format === 'ISO') {
    return date.toISOString();
  }

  const pad = (n) => String(n).padStart(2, '0');
  const tokens = {
    'YYYY': date.getFullYear(),
    'MM': pad(date.getUTCMonth() + 1),
    'DD': pad(date.getUTCDate()),
    'HH': pad(date.getUTCHours()),
    'mm': pad(date.getUTCMinutes()),
    'ss': pad(date.getUTCSeconds()),
    'MMM': date.toLocaleString('en', { month: 'short' }),
    'MMMM': date.toLocaleString('en', { month: 'long' }),
    'ddd': date.toLocaleString('en', { weekday: 'short' }),
    'dddd': date.toLocaleString('en', { weekday: 'long' }),
    'YY': String(date.getFullYear()).slice(-2)
  };

  let result = format;
  for (const [token, value] of Object.entries(tokens)) {
    result = result.replace(new RegExp(token, 'g'), value);
  }
  return result;
}

function toTimezone(date, timezone) {
  // Return timezone offset info
  try {
    const options = { timeZone: timezone, timeZoneName: 'short' };
    const str = date.toLocaleString('en-US', { ...options, hour12: false });
    return str;
  } catch (e) {
    return null;
  }
}

function getComponents(date) {
  return {
    year: date.getUTCFullYear(),
    month: date.getUTCMonth() + 1,
    day: date.getUTCDate(),
    hour: date.getUTCHours(),
    minute: date.getUTCMinutes(),
    second: date.getUTCSeconds(),
    millisecond: date.getUTCMilliseconds(),
    dayOfWeek: date.getUTCDay(),
    dayOfWeekName: ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'][date.getUTCDay()]
  };
}

function calculateDuration(start, end) {
  const diff = end.getTime() - start.getTime();
  if (diff < 0) return null;

  const ms = diff;
  const seconds = Math.floor(ms / 1000);
  const minutes = Math.floor(seconds / 60);
  const hours = Math.floor(minutes / 60);
  const days = Math.floor(hours / 24);
  const weeks = Math.floor(days / 7);
  const months = Math.floor(days / 30.44); // Average
  const years = Math.floor(days / 365.25);

  return {
    milliseconds: ms,
    seconds: seconds,
    minutes: minutes,
    hours: hours,
    days: days,
    weeks: weeks,
    months: Math.floor(months),
    years: years,
    formatted: `${years}y ${months % 12}mo ${days % 30}d ${hours % 24}h ${minutes % 60}m ${seconds % 60}s`
  };
}

function parseArgs(args) {
  const result = {
    command: null,
    input: null,
    from: 'UTC',
    to: null,
    timezone: null,
    format: null
  };

  let i = 0;
  while (i < args.length) {
    const arg = args[i];

    if (!result.command) {
      result.command = arg;
    } else if (!result.input && !arg.startsWith('--') && !arg.startsWith('-')) {
      result.input = arg;
    } else if ((arg === '-z' || arg === '--timezone') && args[i + 1]) {
      result.timezone = args[++i];
    } else if ((arg === '-f' || arg === '--from') && args[i + 1]) {
      result.from = args[++i];
    } else if ((arg === '-t' || arg === '--to') && args[i + 1]) {
      result.to = args[++i];
    } else if ((arg === '-fmt' || arg === '--format') && args[i + 1]) {
      result.format = args[++i];
    }
    i++;
  }

  return result;
}

function main() {
  const args = parseArgs(process.argv.slice(2));

  if (!args.command) {
    console.log('Time Converter - Timezone and date conversion utilities');
    console.log('');
    console.log('Usage: time.js <command> [options]');
    console.log('');
    console.log('Commands:');
    console.log('  now              Current time');
    console.log('  convert <date>  Convert between timezones');
    console.log('  format <date>   Format a date');
    console.log('  duration <from> Calculate duration');
    console.log('  unix <ts>        Convert Unix timestamp to date');
    console.log('  to-unix <date>   Convert date to Unix timestamp');
    console.log('  list             List common timezones');
    console.log('');
    console.log('Options:');
    console.log('  -z, --timezone <tz>    Specify timezone');
    console.log('  -f, --from <tz>       Source timezone');
    console.log('  -t, --to <tz>         Target timezone');
    console.log('  -fmt, --format <str>  Date format string');
    console.log('');
    console.log('Examples:');
    console.log('  time.js now');
    console.log('  time.js now -z America/New_York');
    console.log('  time.js convert "2024-03-15" -t Asia/Tokyo');
    console.log('  time.js unix 1709836800');
    process.exit(1);
  }

  try {
    let result;

    switch (args.command) {
      case 'now': {
        const now = new Date();
        result = {
          utc: now.toISOString(),
          epoch_ms: now.getTime(),
          epoch_s: Math.floor(now.getTime() / 1000),
          timezone: args.timezone || 'UTC',
          formatted: args.timezone ? toTimezone(now, args.timezone) : now.toISOString(),
          components: getComponents(now)
        };
        break;
      }

      case 'convert': {
        if (!args.input) {
          console.log(JSON.stringify({ error: 'Date input required' }, null, 2));
          process.exit(1);
        }
        const date = parseDate(args.input);
        if (!date) {
          console.log(JSON.stringify({ error: 'Could not parse date' }, null, 2));
          process.exit(1);
        }
        result = {
          input: args.input,
          from: args.from,
          to: args.to || 'UTC',
          source: date.toISOString(),
          converted: args.to ? toTimezone(date, args.to) : date.toISOString(),
          components: getComponents(date)
        };
        break;
      }

      case 'format': {
        if (!args.input) {
          console.log(JSON.stringify({ error: 'Date input required' }, null, 2));
          process.exit(1);
        }
        const date = parseDate(args.input);
        if (!date) {
          console.log(JSON.stringify({ error: 'Could not parse date' }, null, 2));
          process.exit(1);
        }
        result = {
          input: args.input,
          format: args.format || 'ISO',
          formatted: args.format ? formatDate(date, args.format) : date.toISOString(),
          iso: date.toISOString()
        };
        break;
      }

      case 'duration': {
        if (!args.input) {
          console.log(JSON.stringify({ error: 'Start date required' }, null, 2));
          process.exit(1);
        }
        const start = parseDate(args.input);
        const end = parseDate(args.to) || new Date();
        if (!start) {
          console.log(JSON.stringify({ error: 'Could not parse start date' }, null, 2));
          process.exit(1);
        }
        result = {
          from: start.toISOString(),
          to: end.toISOString(),
          duration: calculateDuration(start, end)
        };
        break;
      }

      case 'unix': {
        const ts = args.input ? parseFloat(args.input) : Date.now();
        const date = new Date(ts < 10000000000 ? ts * 1000 : ts);
        result = {
          input: args.input,
          timestamp_s: Math.floor(date.getTime() / 1000),
          timestamp_ms: date.getTime(),
          iso: date.toISOString(),
          utc: date.toUTCString(),
          local: date.toLocaleString(),
          components: getComponents(date)
        };
        break;
      }

      case 'to-unix': {
        if (!args.input) {
          console.log(JSON.stringify({ error: 'Date input required' }, null, 2));
          process.exit(1);
        }
        const date = parseDate(args.input);
        if (!date) {
          console.log(JSON.stringify({ error: 'Could not parse date' }, null, 2));
          process.exit(1);
        }
        result = {
          input: args.input,
          timestamp_s: Math.floor(date.getTime() / 1000),
          timestamp_ms: date.getTime(),
          iso: date.toISOString()
        };
        break;
      }

      case 'list': {
        result = { timezones: COMMON_ZONES };
        break;
      }

      default:
        console.log(JSON.stringify({ error: `Unknown command: ${args.command}` }, null, 2));
        process.exit(1);
    }

    console.log(JSON.stringify(result, null, 2));

  } catch (e) {
    console.log(JSON.stringify({ error: e.message }, null, 2));
    process.exit(1);
  }
}

main();

#!/usr/bin/env node
/**
 * QR Generator - Generate QR codes as ASCII art
 */

// Simple QR-like pattern generator (not actual QR, but creates visual patterns)
function generatePattern(data, size = 21) {
  // Use simple hash to create deterministic pattern
  let hash = 0;
  for (let i = 0; i < data.length; i++) {
    const char = data.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash;
  }

  // Create pattern
  const pattern = [];
  const seed = Math.abs(hash);

  // Standard QR modules: Finder patterns, timing patterns, modules
  const modules = new Array(size * size).fill(0);

  // Set finder patterns (corners)
  const setFinder = (row, col) => {
    for (let r = 0; r < 7; r++) {
      for (let c = 0; c < 7; c++) {
        const isBorder = r === 0 || r === 6 || c === 0 || c === 6;
        const isInner = r >= 2 && r <= 4 && c >= 2 && c <= 4;
        const idx = (row + r) * size + (col + c);
        if (isBorder || isInner) {
          modules[idx] = 1;
        }
      }
    }
  };

  // Top-left finder
  setFinder(0, 0);
  // Top-right finder
  setFinder(0, size - 7);
  // Bottom-left finder
  setFinder(size - 7, 0);

  // Timing patterns
  for (let i = 8; i < size - 8; i++) {
    modules[6 * size + i] = i % 2 === 0 ? 1 : 0;
    modules[i * size + 6] = i % 2 === 0 ? 1 : 0;
  }

  // Encode data using data bits
  // Simple encoding: spread data bits across modules
  let dataPtr = 0;
  for (let i = 0; i < size * size; i++) {
    // Skip finder patterns and timing patterns
    const row = Math.floor(i / size);
    const col = i % size;

    // Skip finder areas
    if ((row < 8 && col < 8) ||
        (row < 8 && col >= size - 8) ||
        (row >= size - 8 && col < 8)) {
      continue;
    }

    // Skip timing patterns
    if (row === 6 || col === 6) continue;

    // Set data bit
    const dataVal = (data.charCodeAt(dataPtr % data.length) ^ seed) & 1;
    modules[i] = dataVal;
    dataPtr++;
  }

  return modules;
}

function renderQR(modules, size = 21) {
  const lines = [];
  const fullBlock = 'â–ˆ';
  const emptyBlock = ' ';
  const border = fullBlock.repeat(size + 2);

  lines.push(border);

  for (let row = 0; row < size; row++) {
    let line = fullBlock;
    for (let col = 0; col < size; col++) {
      line += modules[row * size + col] ? fullBlock : fullBlock;
      // Use different block characters for pattern
      line = line.slice(0, -1) + (modules[row * size + col] ? fullBlock : emptyBlock);
    }
    line += fullBlock;
    lines.push(line);
  }

  lines.push(border);
  return lines.join('\n');
}

function renderCompactQR(modules, size = 21) {
  const chars = {
    '00': ' ', // white white
    '01': 'â–€', // white black  
    '10': 'â–„', // black white
    '11': 'â–ˆ'  // black black
  };

  const lines = [];
  lines.push('â–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆ');

  // Process 2 rows at a time
  for (let row = 0; row < size; row += 2) {
    const r1 = row;
    const r2 = row + 1 < size ? row + 1 : row;

    let line = 'â–ˆâ–ˆ';
    for (let col = 0; col < size; col++) {
      const v1 = modules[r1 * size + col] || 0;
      const v2 = modules[r2 * size + col] || 0;
      line += chars[`${v1}${v2}`];
    }
    line += 'â–ˆâ–ˆ';
    lines.push(line);
  }

  lines.push('â–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆâ–ˆ');
  return lines.join('\n');
}

function formatWifiQR(ssid, password, type = 'WPA') {
  return `WIFI:T:${type};S:${ssid};P:${password};;`;
}

function formatContactQR(name, phone, email = '', org = '') {
  let vcard = 'BEGIN:VCARD\nVERSION:3.0\n';
  vcard += `FN:${name}\n`;
  if (phone) vcard += `TEL:${phone}\n`;
  if (email) vcard += `EMAIL:${email}\n`;
  if (org) vcard += `ORG:${org}\n`;
  vcard += 'END:VCARD';
  return vcard;
}

function formatEmailQR(email, subject = '', body = '') {
  let url = `mailto:${email}`;
  const params = [];
  if (subject) params.push(`subject=${encodeURIComponent(subject)}`);
  if (body) params.push(`body=${encodeURIComponent(body)}`);
  if (params.length > 0) {
    url += '?' + params.join('&');
  }
  return url;
}

function formatPhoneQR(phone) {
  return `tel:${phone}`;
}

function formatURLQR(url) {
  if (!url.startsWith('http://') && !url.startsWith('https://')) {
    url = 'https://' + url;
  }
  return url;
}

function parseArgs(args) {
  const result = {
    type: 'text',
    data: null,
    output: null,
    ssid: null,
    password: null,
    name: null,
    phone: null,
    email: null,
    subject: null,
    org: null,
    body: null
  };

  let i = 0;
  while (i < args.length) {
    const arg = args[i];

    if ((arg === '-t' || arg === '--type') && args[i + 1]) {
      result.type = args[++i];
    } else if ((arg === '-o' || arg === '--output') && args[i + 1]) {
      result.output = args[++i];
    } else if ((arg === '-s' || arg === '--ssid') && args[i + 1]) {
      result.ssid = args[++i];
    } else if ((arg === '-p' || arg === '--password') && args[i + 1]) {
      result.password = args[++i];
    } else if ((arg === '-n' || arg === '--name') && args[i + 1]) {
      result.name = args[++i];
    } else if ((arg === '-ph' || arg === '--phone') && args[i + 1]) {
      result.phone = args[++i];
    } else if ((arg === '-e' || arg === '--email') && args[i + 1]) {
      result.email = args[++i];
    } else if ((arg === '--subject') && args[i + 1]) {
